/* Nav: next page button image replacement */
img[src="themes/bugguide/images/next.gif"] { background:transparent url(data:image/gif;base64,R0lGODlhDwAPAMQAAAMDAgsLCAwMCQ0NCg4OC42GaubbruHWqtbMomdiTtvRptjOpNPJoNDGnqqigZqTdVlVRIqDaExIOd7TqKCYeVdSQgoJBwUEAwYFBAcGBQAAAP///wAAAAAAAAAAAAAAACH5BAEAABsALAAAAAAPAA8AAAVN4KZpW2mepWZdI4pqAZOwpJs6BjK3rgYZQB3thZkAg7ta6nE8CpWaSrPZGNQ0hanhEBFcAYsm1wuVHMc8k4ay7abVGYVb+SKQbac3PgQAOw==) no-repeat !important; padding:0px 0px 15px 15px !important; height:0px !important; width:0px !important; border: 0px !important; } 



/* ............ Page numbers ............ */





/* Page #'s box */
div.pager {
border: 1px solid #000000 !important;
border-radius: 7px !important;
background-color: #111111 !important;
/*width: 10px !important;*/
margin-left:auto !important;
margin-right:auto !important;
padding-top: 2px !important;
padding-bottom: 2px !important;
box-shadow: 5px 5px 5px 0px #111111 !important;
-webkit-box-shadow: 5px 5px 5px 0px #111111 !important;
-moz-box-shadow: 5px 5px 5px 0px #111111 !important;
white-space: nowrap !important;
}

/* Neat code that auto sizes page # table */
div.pager { display: table; width: 1px; }
div.pager > div { display: table-cell; }


/* Page #'s links and active page # */

html body.body div.container table tbody tr td.col2 table tbody tr td.col2-body div.pager div a:link {font-weight:bold !important; text-decoration:none !important; color:#949494 !important;
}

html body.body div.container table tbody tr td.col2 table tbody tr td.col2-body div.pager div a:visited {color:#949494 !important;
font-weight:bold !important; text-decoration:none !important;
}

html body.body div.container table tbody tr td.col2 table tbody tr td.col2-body div.pager div a:hover {color:#FFFFFF !important;
font-weight:bold !important; text-decoration:none !important;
}

html body.body div.container table tbody tr td.col2 table tbody tr td.col2-body div.pager div a:active {color:#000000 !important;
font-weight:bold !important; text-decoration:none !important;
}

td.node-container div.pager div {
color: #ffffff !important;
font-weight:normal !important;
}



}
