css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("www.foopets.com") {




#page, #content, #pet_info
{background:#FFFFFF url('http://i871.photobucket.com/albums/ab272/0420Bekka/8-1.png') repeat-y!important}



body{background:#FFFFFF url('http://i55.tinypic.com/zuinb5.png') repeat 0 0!important}

a
{ color: #000000 !important; }


#nav li.selected a, #nav li a:hover
{ background: transparent !important;
color: #FFFFFF !important; }




.foobutton
{ background: #000000 !important;
-moz-box-shadow: rgba(0,0,0, .8) 0px 4px 18px 2px !important; }

.foobutton:hover
{ border-color: #FF3E96 !important;
-moz-box-shadow: inset rgba(98,128,136,0.5) 0px 2px 8px 2px, rgba(98,128,136,0.5) 0px 2px 12px 2px !important;  }

.foobutton span
{ font-size: 15px !important;
color: #FF3E96 !important;
text-shadow: 1px 2px 2px black, 1px 2px 2px black !important; }

#page
{ border-bottom: none !important; }



/* navbar logo */
#logo > A
{ width: 110px !important;
height: 90px !important;
margin-left: 60px !important;
margin-top: 2px !important;
 }




#nav
{ background: url(http://i56.tinypic.com/2klr0g.png) center no-repeat !important; } }
