css:
/*
 Firefox 4: center images + dynamic background
 http://userstyles.org/styles/49276
 BEGIN
*/
@namespace url(http://www.w3.org/1999/xhtml);

html
{ 
}

html > body > img:only-child 
{
; border-width: 5px
; border-style: solid
; display: block
; margin: 0px auto
; background-image: url(data:image/gif;base64,R0lGODlhCgAKAIAAAAAAAP///yH5BAEAAAAALAAAAAAKAAoAAAIRhB2ZhxoM3GMSykqd1VltzxQAOw==)
; background-attachment: fixed
; background-color: #fff
; border-color: #fff
}

html > body:hover > img:only-child 
{
; background-color: #999
; border-color: #999
; image-rendering: -moz-crisp-edges !important
}

html > body:active > img:only-child 
{
; background-color: #000
; border-color: #000
}

html > body > img:only-child:hover
{
; background-image: none
; background-color: #333
; border-color: #333
}

html > body > img:only-child:active
{
; background-color: #000
; border-color: #000
}


/*
 END
 Firefox 4: center images + dynamic background
 http://userstyles.org/styles/49276
*/
