css:
@namespace url(http://www.w3.org/1999/xhtml);      @-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/') 


 {#dashboard_index{background: url('http://i51.tinypic.com/2qcnz4k.jpg') center right #891a4f repeat fixed !important;}
body {background: url('http://i51.tinypic.com/2qcnz4k.jpg') center right #891a4f repeat fixed !important; opacity: 7; }  *{text-shadow:1px !important; letter-spacing: -1px;}
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:#891a4f !important; opacity:.9;}
ol#posts li.notification{background-color: #891a4f !important; border-bottom: 1px solid #95b8d7 !important; color: #000000 !important; letter-spacing: -1px;}
#right_column a#dashboard_switch_blog_menu_current:hover {background:#891a4f;}
#nav .nav_item.active{background-color:#e6dadb;}
#left_column #posts .post .permalink{background-color:#95b8d7; letter-spacing: -1px;}
#nav .nav_item.active,
#nav .nav_item .nav_menu,
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background-color:#95b8d7 !important;
background-image:none !important;
border:0px !important}
#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current
{background-image:none !important}
.notification_type_icon{display:none !important}
#left_column #posts .notification {
background-color:#891a4f  !important;
color:#55a7cf !important;
border-bottom-color: #891a4f  !important}
#new_post_notice_container{
color:#55a7cf !important}
#new_post_notice_container a{
color:#fff !important}
/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://i55.tinypic.com/1ifjmr.jpg') center center no-repeat !important;
}
.new_post_label_icon {
	background-image: none !important;
}

ol#posts .post.new_post .new_post_label {
display:none !important;
}


}
