css:
@-moz-document domain("facebook.com")  {
#fbPhotoTheater {margin-top: 40px !important; }

/* Fixed top bar  */
#pageHead {
opacity: 0.9 !important;
background-color: #3b5999 !important;
border-radius: 0px 8px 8px / 0px 0px 8px 8px !important;
position: fixed !important;
width: 981px !important;
top: 0px !important;
left: 0 !important;
right: 0 !important;
margin: 0 auto !important;
}
#content {margin-top: 45px !important;}
#pageHead {z-index: 999999 !important; }
/* end Fixed top bar */
#contentCol  { background-color:#ECECFF !important; }
#blueBar { display: none !important; }

#pageHead:hover {
    opacity: 1 !important;
}

#pageLogo, .jewel {
    margin-top: 1px !important;
    border-radius: 2px !important;
    margin-left: 2px !important;
}

#headNav {
    background-color: transparent !important;
    border: none !important;
    padding: 2px 0px !important;
}

.uiProfilePhoto {
    border-radius: .5em !important;
}

.uiStreamStory {
    background: -webkit-linear-gradient(top, #FFF, #F8F8F8) !important;
    background: -moz-linear-gradient(top, #FFF, #F8F8F8) !important;
    border: 1px solid #CCC !important;
    border-radius: 1em !important;
    margin-bottom: 0.5em !important;
    padding: 5px !important;
}

.uiStreamStory:hover {
    border-color: #999 !important;
    background: -webkit-linear-gradient(top, #FFF, #EEE) !important;
    background: -moz-linear-gradient(top, #FFF, #EEE) !important;
}

.uiListLight {
    background: -webkit-linear-gradient(top, #FFF, #F8F8F8) !important;
    background: -moz-linear-gradient(top, #FFF, #F8F8F8) !important;
    border: 1px solid #CCC !important;
    border-radius: 1em !important;
    margin-bottom: 0.5em !important;
    padding: 5px !important;
}

.uiListLight:hover {
    border-color: #999 !important;
    background: -webkit-linear-gradient(top, #FFF, #EEE) !important;
    background: -moz-linear-gradient(top, #FFF, #EEE) !important;
}

.uiBoxWhite {
    background: -webkit-linear-gradient(top, #FFF, #F8F8F8) !important;
    background: -moz-linear-gradient(top, #FFF, #F8F8F8) !important;
    border: 1px solid #CCC !important;
    border-radius: 1em !important;
    margin-bottom: 0.2em !important;
    padding: 5px !important;
}

.uiBoxWhite:hover {
    border-color: #999 !important;
    background: -webkit-linear-gradient(top, #FFF, #EEE) !important;
    background: -moz-linear-gradient(top, #FFF, #EEE) !important;
}

.uiUfiComment {
    background: -webkit-linear-gradient(top, #CAE4FF, #CAE4FF) !important;
    background: -moz-linear-gradient(top, #CAE4FF, #CAE4FF) !important;
    border-radius: 1em !important;
}

.uiSelectorButton {
    border: none !important;
}

.uiStream .hideButton, .uiStream .hideSelector {
    margin-right: -4px !important;
}

.uiUfi .ufiItem {
    background-color: transparent !important;
    border: none !important;
}

.uiPhotoThumb {
    border-radius: .5em !important;
}

.img {
    border-radius: .33em !important;
}

.uiHeader {
    border: none !important;
    border-radius: 0 .5em .5em / 0 0 .5em .5em !important;
}

.mbs >div >div >.uiCloseButton {
    margin-top: 0px !important;
}

.uiMediaThumb {
    border-radius: .5em !important;
}

.uiTooltip {
    border-radius: .5em !important;
}

.pas {
    border-radius: .5em !important;
}

.shaded {
    border-top-left-radius: .5em !important;
    border-bottom-left-radius: .5em !important;
}

.dialog_title {
    border-top-left-radius: .5em !important;
    border-top-right-radius: .5em !important;
}

.GBThreadRow {
    background: -webkit-linear-gradient(top, #FFF, #F8F8F8) !important;
    background: -moz-linear-gradient(top, #FFF, #F8F8F8) !important;
    padding: 1px !important;
    margin-bottom: 0.2em !important;
    border: 1px solid #CCC !important;
    border-radius: 1em !important;
}

.GBThreadRow:hover {
    background: -webkit-linear-gradient(top, #FFF, #EEE) !important;
    background: -moz-linear-gradient(top, #FFF, #EEE) !important;
}
.GBThreadMessageRow {
    background: -webkit-linear-gradient(top, #FFF, #F8F8F8) !important;
    background: -moz-linear-gradient(top, #FFF, #F8F8F8) !important;
    padding: 4px !important;
    margin-bottom: 0.2em !important;
    border: 1px solid #CCC !important;
    border-radius: 1em !important;
}

.GBThreadMessageRow:hover {
    background: -webkit-linear-gradient(top, #FFF, #EEE) !important;
    background: -moz-linear-gradient(top, #FFF, #EEE) !important;
}

body {
    background: -moz-linear-gradient(top, #FFF, #F8F8F8) !important;
    background-attachment: fixed !important;
}

.gigaboxx_thread_hidden_messages {
    border-radius: .5em !important;
}

.UIWell {
    background: none !important;
    border: none !important;
    margin: 0 !important;
}

.uiHeaderPage {
    padding-bottom: 5px !important;
}

.ego_unit {
    border: none !important;
    padding-top: 0 !important;
}

.uiSideHeader {
    background: -webkit-linear-gradient(left, #EEE, #FFF) !important;
    background: -moz-linear-gradient(left, #EEE, #FFF) !important;
}

#navAccount > ul, .jewelFlyout, .uiOverlay, #navSearch .uiTypeaheadView,  .pop_container_advanced, .HovercardOverlay div.stage {
  -webkit-box-shadow: 3px 3px 20px rgba(0,0,0,0.4);
  -moz-box-shadow: 3px 3px 20px rgba(0,0,0,0.4);
}
#fbDockChat, .fbNubFlyout, .uiMenu {
  -webkit-box-shadow: 0px 0px 12px rgba(0,0,0,0.3);
  -moz-box-shadow: 0px 0px 12px rgba(0,0,0,0.3);
}
form .uiTypeaheadView {
  z-index: -10 !important;
  border-top: 0;
}

.uiMenu,
#dropmenu_container, .hovercard .stage {
  -moz-box-shadow: 3px 3px 20px rgba(0,0,0,0.4); 
  -webkit-box-shadow: 3px 3px 20px rgba(0,0,0,0.4);
}

#navAccountPic .img,
img[class*="uiProfilePhoto"].profilePic.img,
.fbxWelcomeBox a.fbxWelcomeBoxBlock > img.img,
.largeFbProfileBlankIcon {
  -moz-box-shadow: 0px 0px 3px #D8DFEA;
  -webkit-box-shadow: 0px 0px 3px #D8DFEA;
}

.jewel:not([class*="Toggler"]):hover:active { 
  background-color: #627AAD !important;
  border: 1px solid #1D4088 !important;
  margin: -1px 0 -1px -1px !important; 
  border-bottom: none !important;
}
.jewelNew {
  border-radius: 6px 6px 0 0;
  -moz-border-radius: 6px 6px 0 0;
  -webkit-border-radius: 6px 6px 0 0;
  border: solid 1px white !important;
}

.fbDock, 
[class*="NubContainer"], 
.fbNubFlyout, .fbNubFlyoutTitlebar,
ul.tabs li a, a.tab, [class="openToggler"],
#headNavOut, .jewel, .jewelToggler, 
#pageLogo > a {
  -webkit-border-top-left-radius: 4px;
  -webkit-border-top-right-radius: 4px;
  -moz-border-radius-topleft: 4px;
  -moz-border-radius-topright: 4px;
}
.profile .top_bar ul.tabs li,
#profile_tabs > li.selected,
#pageNav > li > a[accesskey], li.openToggler, 
.connect_widget_facebook_logo_menubar, 
.fan_box .full_widget {
  -webkit-border-top-left-radius: 5px;
  -webkit-border-top-right-radius: 5px;
  -moz-border-radius-topleft: 5px;
  -moz-border-radius-topright: 5px;
}

select, textarea,
#navSearch div[class],
.uiTypeahead .wrap,
.uiTypeahead .wrap input,
.inputtext, .textInput, .inputpassword {
  border-radius: 3px !important;
  -moz-border-radius: 3px !important;
  -webkit-border-radius: 3px !important;
}
 
.uiComboInput,
.uiSearchInput,
[class$="Tokenizer"],
.blurb, .uiTooltipText, .titletip,
.uiHeader, .HC_Center,
#welcome_dashboard .active_step,
#welcome_dashboard .step_indicator,
input.editorkit_button, 
input.inputbutton, input.inputsubmit,
.button_fb, .uiButton, .create_button, 
a[style*="border-color: #e7e7e7 #666666 #666666 #e7e7e7;"],
.UIWell,
[class^="UIComposer"], .fluidInputCell,
.uiComposerMessageBox .composerTypeahead,
[class^="UIActionMenu"], .UISelectList,
.UIMessageBox,
.pas.attachmentFrame, .fbTabGridItem,
.uiPhotoThumb, .UIImageBlock_SMALL_Image,
.uiMediaThumb, .owner_thumb>a,
.uiMediaThumbWrap, .UIPhotoGrid_Image,
#editphotoalbum, #editphotoalbum .photo,
#hubs_wikipedia div.thumbinner,
a.edit_link, a.box_editor, a.skip,
.ubersearch .result, .protip, 
.status, .error, .uiBoxOverlay,
.uiBoxGray, .uiBoxWhite, .uiBoxLightblue, .uiBoxRed, .uiBoxYellow,
.account_note, .network_pending, 
.notifications, .noticebox, .form_container, #ads_privacy_examples,
.photo_table, .photo_table > table, #photoborder,
img.img[style*="padding:4px; border:1px solid #DDDDDD;"],
#tagging_instructions, #photo_tag_selector,
.datebox, .calendar_picker, .calendar_picker_container,
.side_column, div.feed_comments,
.connect_widget_comment_box,

.commentable_item .ufi_section,
.uiUfi .ufiItem, .uiBlingBox,
.uiTypeaheadView ul.basic,
.uiTypeahead, .uiTypeahead .wrap,
.hovercard .stage,
.fbTextEditor, 
.editaccount .editor_panel {
  border-radius: 3px;
  -moz-border-radius: 3px;
  -webkit-border-radius: 3px;
}

.navHeaderPencil:hover,
.uiCloseButton:hover {
  border-radius: 2px;
  -moz-border-radius: 2px;
  -webkit-border-radius: 2px;
}


.uiMenu,
.uiTypeaheadView,
.uiComposerMessageBox,
.language_form, .mobile_account_inlay,
div.box.moveable,
.editaccount .editor_panel,
#contentCurve {
  border-radius: 0 0 3px 3px;
  -moz-border-radius: 0 0 3px 3px;
  -webkit-border-radius: 0 0 3px 3px;
}


.UIComposer_Attachment_TDTextArea,
#buddy_list_panel_settings_flyout,
#buddy_list_panel_lists_flyout,
.uiSideNav .sortableItem,
.uiSideNav a.item,
.uiSideNav a.subitem,
#fbFriendsSidebar li.buddyRow a:hover {
  border-radius: 5px 0 0 5px;
  -moz-border-radius: 5px 0 0 5px;
  -webkit-border-radius: 5px 0 0 5px;
}
.GBSearchBox_Input,
#navSearch .uiTypeahead, 
#navSearch .uiTypeahead .wrap,
#navSearch .uiTypeahead > * { 
  border-radius: 3px 0 0 3px;
  -moz-border-radius: 3px 0 0 3px;
  -webkit-border-radius: 3px 0 0 3px;
}


h2.dialog_title,
.toggle_tabs li.first a.selected,
.fbDockChatTab > a.fbNubButton,
a.fbDock {
  border-top-left-radius: 5px;
  -moz-border-radius-topleft: 5px;
  -webkit-border-top-left-radius: 5px;
}

h2.dialog_title,
#attribution_span,
.toggle_tabs li.last a,
#buddy_list_panel_settings_flyout,
#buddy_list_panel_lists_flyout,
.HovercardOverlay > .HovercardHeavy.hovercard {
  border-top-right-radius: 5px;
  -moz-border-radius-topright: 5px;
  -webkit-border-top-right-radius: 5px;
}

#navAccount > ul, 
.jewelBox, .jewelFooter, 
div.jewelFooter > a.seeMore,
.edit_profilepicture,
#dropmenu_container,
.header-link.first,
.HovercardHeavy.hovercard .stage,
.fbNubFlyout, .fbNubFlyoutFooter,
.fbNubFlyoutFooter.inputContainer > textarea,
.dialog_content {
  border-bottom-left-radius: 5px;
  -moz-border-radius-bottomleft: 5px;
  -webkit-border-bottom-left-radius: 5px;
}

#boxes_left, .dialog_content {
  -moz-border-radius-bottomright: 5px;
  border-bottom-right-radius: 5px;
}

a.next, #photonav_next:hover,
.video_permalink_pager a.pager-next:hover,
.UIPager_Button.UIPager_ButtonForward,
.UIThumbPagerControl_Button_Right,
.featureBox:last-of-type {
  border-radius: 0 2px 3px 0;
  -moz-border-radius: 0 2px 3px 0;
  -webkit-border-radius: 0 2px 3px 0; 
}

a.prev, #photonav_prev:hover,
.video_permalink_pager a.pager-prev:hover,
.UIThumbPagerControl_Button_Left,
.UIPager_Button.UIPager_ButtonBack { 
  border-radius: 2px 0 0 3px;
  -moz-border-radius: 2px 0 0 3px;
  -webkit-border-radius: 2px 0 0 3px; 
}
.uiTypeaheadView ul.basic,
.hovercard .stage {
  border: 1px solid #3B5998;
  border-bottom-width: 2px !important;
}
.uiSearchInput button[type="submit"] {background-color: transparent !important;}

.uiCloseButton,
.uiCloseButton:hover {
  -moz-transition: ease-out .5s opacity;
  -webkit-transition: ease-out .5s opacity;
}
.create_button { margin-bottom: -2px !important;}
}
