css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

/* Go to this page if you want to CHANGE THE COLOR: http://www.w3schools.com/css/css_colors.asp   */

/* * {
font-family: "segoe ui" !important;
} */

/* Defined min and max size of the tabs, DEFAULT IS 100PX AND 180 PX, CHANGE IF DESIRED BELOW: */
.tabbrowser-tab:not([pinned])[fadein] {
        min-width: 120px !important;
        max-width: 120px !important;
}
.tabbrowser-tab[selected]:not([pinned])[fadein] {
        min-width: 120px !important;
        max-width: 120px !important;
}


/*Hide the Search Engine image*/
.searchbar-engine-image {
	display: none !important;
}
/*Hide the search button (magnifying glass)*/
.search-go-button {
	display: none !important;
}
/*Hide bookmark icons in the Personal Toolbar*/
toolbarbutton.bookmark-item > .toolbarbutton-icon {
	display: none !important;
}
/*Hide urlbar history dropmarker*/
.urlbar-history-dropmarker {
	display: none !important;
}

/*Reduce titlebar margin - choose margin according your windows theme (minimize, maximize and close buttons)*/
#main-window[sizemode="normal"][tabsontop="true"]:not([inFullscreen]) #toolbar-menubar[autohide="true"] {
	margin-top: -2px !important;
}
#main-window[sizemode="maximized"][tabsontop="false"]:not([inFullscreen]) #toolbar-menubar[autohide="true"] {
	margin-top: -1px !important;

}
.titlebar-placeholder[type="caption-buttons"] {
	margin-left: 8px !important;
}
/*Disable tabstoolbar drag - enable newtab double clicking*/
#navigator-toolbox > #TabsToolbar, #main-window[tabsintitlebar] #TabsToolbar {
	-moz-binding: url("chrome://global/content/bindings/toolbar.xml#toolbar") !important;


}
/*Glass, slim and naked UI*/
#navigator-toolbox::after {
	height: 0 !important;
}
#nav-bar {
	background-image: -moz-linear-gradient(rgba(255,255,255,.55), rgba(255,255,255,.3)) !important;
	background-color: transparent !important;
	border: none !important;
	border-radius: 2px 2px 0 0 !important;
	margin: 0 !important;
	padding: 0 !important;
	height: 24px !important;
	visibility: visible !important;
}
#urlbar {
	background: -moz-linear-gradient(left, rgba(255,255,255,.0), rgba(255,255,255,.0), rgba(255,255,255,.0)) !important;
        color: #E0E0E0 !important;
        font-weight:bold!important;
	border: none !important;
	border-radius: 2px 0 0 0 !important;
	margin: 0 !important;
	box-shadow: none !important;
}
#urlbar:hover {
	background: -moz-linear-gradient(left, rgba(255,255,255,.0), rgba(255,255,255,.0), rgba(255,255,255,.0)) !important;
        color: #E0E0E0 !important;
}
.urlbar-frontcap {
	margin: 0 !important;
}

#identity-box {
	background: none !important;
	border-radius: 0 !important;
}

#identity-box.verifiedDomain {
	background-color: hsl(219,45%,60%) !important;
	background-image: -moz-linear-gradient(rgba(255,255,255,.25), rgba(0,0,0,.15)) !important;
}

#identity-box.verifiedIdentity {
	background-color: hsl(92,45%,52%) !important;
	background-image: -moz-linear-gradient(rgba(255,255,255,.25), rgba(0,0,0,.15)) !important;
}

#searchbar > *, #search-box {
	background: -moz-linear-gradient(left, rgba(255,255,255,.0), rgba(255,255,255,.0), rgba(255,255,255,.0)) !important;
        color: #E0E0E0 !important;
        font-weight:bold!important;
	border: none !important;
	border-radius: 2px 0 0 0 !important;
	margin: 0 !important;
	box-shadow: none !important;
	height: 22px !important;
}
#searchbar:hover > *, .findbar-textbox:hover {
	background: -moz-linear-gradient(left, rgba(255,255,255,.0), rgba(255,255,255,.0), rgba(255,255,255,.0)) !important;
        color: #E0E0E0 !important;
}

#PersonalToolbar{
	 background-color: transparent!important;
	 color: #ADF5F9 !important;
         border: none !important;
         border-top: none !important;
}

#personal-bookmarks{
	 background-color: transparent!important; 
} 

tabs.tabbrowser-tabs#tabbrowser#tabs{
	 background-color: transparent!important;
}

.tabbrowser-tab, .tabs-newtab-button {
	 background: transparent !important;
	 padding-left: 5px !important;
         padding-right: 1px !important;
         border: #000000 !important;
         -moz-border-radius: 0px 0px 0px 0px !important;
	 height: 25px !important;
         margin-top: 2px !important;
         margin-left: 2px !important;
}
	
.tabbrowser-tab:not([pinned="true"]){
         background-image: -moz-linear-gradient(rgba(0,0,0,0), rgba(255,255,255,.0) 0%),
         -moz-linear-gradient(rgba(255,255,255,0.0), rgba(255,255,255,.0) 0%)  !important;

/* DEFAULT COLOR ON INACTIVE TABS IS WHITE (#ffffff), change to an another css-color below if desired: */
         
         color: #000000 !important;

/* DEFAULT FONT IS CALIBRI, change to an another font below if desired (ie Arial Narrow, Segoe UI, ....): */         
         
         font-family: Calibri !important;
                  
	 font-size: 1.05em !important;
         font-weight:bold!important;
         text-shadow: #000000 1px 1px 0px !important;
         border-left: 0px solid rgba(96,96,96,0.5) !important;
         border-right: 0px solid rgba(96,96,96,0.5) !important;
         border-bottom: 0px solid rgba(255,255,255,0.65) !important;
         padding-left: 0px !important;
         padding-top: 0px !important;
}

.tabbrowser-tab[selected]:not([pinned])[fadein] {
         background-image: -moz-linear-gradient(rgba(0,0,0,0.0), rgba(0,0,0,.0) 0%),
         -moz-linear-gradient(rgba(0,0,0,0.0), rgba(255,255,255,.0) 10%)  !important;

/* DEFAULT COLOR ON SELECTED TAB IS LIGHT BLUE (#ADF5F9), change to an another css-color below if desired: */	 

         color: #ADF5F9!important;

         border-left: 1px !important;
         border-right: 1px !important;
         border-bottom: 1px !important;
        -moz-box-shadow: 0px 0px 2px 2px #ff9900, 0px 0px 0px #ff9900, 0px 0px 1px 1px #ff9900!important;
         font-size: 1.05em !important;
         padding-left: 0px !important;
         padding-top: 0px !important;
}
	
.tabbrowser-tab:hover:not([pinned]){
          background-image: -moz-linear-gradient(rgba(0,0,0,0.05), rgba(255,255,255,.0) 0%),
         -moz-linear-gradient(rgba(255,255,255,0.25), rgba(255,255,255,.0) 0%)  !important;

/* DEFAULT COLOR ON HOVER TAB IS WHITE (#ffffff), change to an another css-color below if desired: */
	 
        color: #ffffff !important;

        border-bottom: none !important;
        -moz-box-shadow: 0px 0px 0px 1px #ff9900,0px 0px 0px 1px #ffffff, 0px 0px 0px 0px #ffffff!important;
       	font-size: 1.05em !important;
        padding-left: 0px !important;
        padding-top: 0px !important;
}
.tabs-newtab-button {
	background: none !important;
	border: none !important;
}

#TabsToolbar, #browser-bottombox, #status-bar, #status-bar > *, #nav-bar, #PersonalToolbar, #addon-bar {
	background: transparent !important;
        color: #ffffff !important;
        -moz-appearance: none !important;
	border: #000000 !important;
}
	
.chromeclass-directories *, #bookmarksPanel * {
         color: #000000 !important;
         text-shadow: #ffffff 0px 0px 0px !important;    
}

.toolbarbutton-1 > .toolbarbutton-menubutton-button, .toolbarbutton-1 > .toolbarbutton-menubutton-dropmarker, .toolbarbutton-1 {
	border: none !important;
	border-radius: 2px !important;
	margin-right: 0 !important;
	margin-left: 0 !important;
}
.toolbarbutton-1 > .toolbarbutton-menubutton-button:not(:hover), .toolbarbutton-1 > .toolbarbutton-menubutton-dropmarker:not(:hover), .toolbarbutton-1:not(:hover), .toolbarbutton-1[disabled="true"] {
	background: none !important;
	box-shadow: none !important;
}
#browser-border-end, #browser-border-start {
	display: none !important;
}
#browser-bottombox {
	background-color: rgba(255,255,255,.0) !important;
	border: none !important;
}
#addon-bar {
	-moz-appearance: none !important;
	background: none !important;
	border: none !important;
}
#addonbar-closebutton {
    display: none !important;
}
