css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.netvibes.com/") {
body {
background: #5c5c5c none! important;
}
div#topPart{
background: #4c4c4c none! important;
}

div#footer{
display:none ! important;
}
.moduleContent{
background: #9c9c9c ! important;
}
.linkOver{
	color: white ! important;
	border-bottom: none ! important;
	text-decoration: none !important;
}
a:hover{
	color: white ! important;
	border-bottom: none ! important;
	text-decoration: none !important;
}
#topPart a:hover {
	color: gray ! important;
}
#topPart a:link, #topPart a:visited {
	color: white !important;
	text-decoration: none ! important;
}
.moduleFrame{
	border : 1px solid black ! important;
}
.apiContent table.border td, .apiContent table.border th {
border:1px solid black ! important;
}
.module{
border: none ! important;
background: #9c9c9c ! important;
margin: 5px ! important;
}
.moduleHeader{
background-color: #4c4c4c ! important;
}
.moduleHeader .title{
	color: #BD804A ! important;
}
.moduleHeader .title a{
	color: #BD804A ! important;
	text-decoration: none ! important;
	border-bottom: none ! important;
}
.moduleHeader .title a:hover{
	color: white ! important;
}
#divTabsOuter{
	border-bottom: 1px solid black ! important;
	border-top: 1px solid gray ! important;
}
#divTabs {
	border-bottom: none ! important;
}
#divTabs ul li {
	border-left: none ! important;
	border-top: none ! important;
	border-right: none ! important;
	border-bottom: none ! important;
	background: none ! important;
	background-color: gray ! important;
}
#divTabs ul li.selected {
	color: black;
	background-color: lightgray ! important;
	border-bottom: none;
}
.contentInsideFrame {
	background:#9c9c9c ! important;
}
.contentInsideFrame a:link, .contentInsideFrame a:visited{
	color: black ! important;
	text-decoration: none ! important;
	border-bottom: none ! important;
}
.contentInsideFrame a:hover{
	color: white ! important;
}
.headline {
	background:#4c4c4c! important;
	border-color: #9c9c9c ! important;
	color:  #BD804A ! important;
}
.headline:hover {
	background:#3c3c3c! important;
}
#FeedreaderReadAll, #FeedreaderUnReadAll {
color: black ! important;
background-color: lightgray ! important;
border: none ! important;
margin: 0px ! important;
}
#FeedreaderReadAll:hover, #FeedreaderUnReadAll:hover {
background-color: white ! important;
}
.tdHeadlines, .tdSeparator, .tdContent, #feedReaderContentFrame, #feedReaderHeadlinesFrame{
border: 1px solid black! important;
}
.tdContent{
background-color: #6c6c6c! important;
}
#feedReaderHeadlinesFrame{
background-color: #6c6c6c! important;
}
.header a:link, .header a:visited{
color: white! important;
text-decoration: underline! important;
}
.header a:hover{
text-decoration: none! important;
}
ul.nv-feedList li:hover a {
background: inherit! important;
}
ul.nv-feedList li a {
border-bottom: 0! important;
display: inline! important;
}
div.moduleHeader{
background: #4c4c4c! important;
}
div[style="margin: 4px 0pt; font-size: 9px; color: rgb(204, 204, 204); text-align: center;"] {
display: none! important;
}
div.frame {
background: #2c2c2c! important;
border-color: #9c9c9c ! important;
color:  #BD804A ! important;
}
div.headline a {
color: lightgray! important;
}
div.headline a:hover {
background: #2c2c2c! important;
border: 0! important;
}

}
