@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rollonfriday.com") {

#header {display: none;}
#dnn_LeftPane {display: none;}
#dnn_RightPane {display: none;}
#Abstracts_3col .containerMain {width: 960px !important;}
#content .containerMain, #content .containerMainRight, #content .containerRight {margin: 0 !important;}
#Abstracts_3col .containerMain .containerOrangeDark, #Abstracts_Admin .containerMain .containerOrangeDark, #Abstracts_4colIntro .containerIntro .containerOrangeDark {width: 960px !important;}
#Abstracts_3col .containerMain .containerOrangeDark .continerTitle, #Abstracts_Admin .containerMain .containerOrangeDark .continerTitle, #Abstracts_4colIntro .containerIntro .containerOrangeDark .continerTitle {background: none !important;}
.containerMain .containerOrangeDark .continerTitle, .containerMain .containerOrangeMid .continerTitle, .containerMain .containerOrangeLight .continerTitle, .containerMainRight .containerOrangeDark .continerTitle, .containerMainRight .containerOrangeMid .continerTitle, .containerMainRight .containerOrangeLight .continerTitle, .containerIntro .containerOrangeLight .continerTitle, .containerIntro .containerOrangeMid .continerTitle, .containerIntro .containerOrangeDark .continerTitle {background: none !important;}
.AbstractsGrid {width: 958px !important;}
a:link, a:visited {color: #000000 !important; text-decoration: none;}
.sortArrow {display: none;}
.RadMenu_ROFSub {background: none !important;}
#mainMenuSub .subMenuStart, #mainMenuSub .subMenuEnd {background: none !important;}
#breadcrum {display: none;}
.RadMenu_ROF .activeHeaderMenu:link, .RadMenu_ROF .activeHeaderMenu:visited, .RadMenu_ROF .activeHeaderMenu:active, .RadMenu_ROF .link:hover, .RadMenu_ROF .focused, .RadMenu_ROF .expanded {background: #CCC !important;}
.RSSButton {display: none;}
.Report_button {display: none !important}
#SHORTCUT ICON[src*="http://www.google.co.uk/images/google_favicon_128.png"]{background:none !important;}
tab[image*="example.org"]>image{background-image:url("http://www.google.co.uk/images/google_favicon_128.png")!important}
.ViewDiscussionPost_TextBoxSubject {width: 930px !important;}
.ViewDiscussionPost_TextBoxMessage {width: 930px !important;}
}
