css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://ace-spades.com/forums/") {

body  
{
background-image: url("http://img811.imageshack.us/img811/2827/18555723.png") !important;
background-repeat: repeat !important;
color: #eee !important;
font: 12px Calibri !important;
margin: 0 auto !important;
padding: 0 !important;
}

/*User Controls*/
.black {
line-height: 2px !important;
}


/*Navigation Text*/
.left.big_text {
line-height: 8px !important;

}

h3.bbcrumb {
background-color: #000000 !important;
}
h3.bbcrumb a {
color: #FFF !important;
}

/*Main Wrapper*/
#wrapper {
width:85% !important;
background:#101010 !important;
border: 1px #333 solid !important;
}

#wrapper > TABLE:nth-child(46)
{
display: none !important;
}

/*Links*/
a:hover {
border-bottom: 0px #333 solid !important;
text-decoration:underline !important;
}

a {
text-decoration:none !important;
}

/*Untagged Page*/
#latest {
margin-left: auto !important;
margin-right: auto !important;
font: 14px Calibri !important;
width: 90% !important;
text-align: left !important;
margin-top: 20px !important;
border: 1px #333 solid !important;
border-collapse:collapse !important;

}

#latest td {
border-top: 1px #333 solid !important;
}

td.num {
border-left: 1px #333 solid !important;
border-right: 1px #333 solid !important;
}



/*Forum Classes*/
.new-topic {
float: right !important;
}


td.left {
font-size:15px !important;
border-bottom: 1px #333 solid !important;
}

p {
color:#eee !important;
background-color:#1c1c1c !important;
}

/*Threads*/
.white {
background:#101010 !important;
font-size:18px !important;
}

.smalltext {
color:#eee !important;
}

/***Topics***/
.page-numbers {
font-size:18px !important;
padding-right:5px !important;

}

.content {
border-bottom:1px solid #333 !important;
}

td.text p, html body#front-page div#wrapper table.content tbody tr td.text {
color: #777 !important;
}

#post_content {
width: 100% !important;
min-height: 90px !important;
}

textarea, input, select {
-moz-appearance: none !important;
background-color:#1c1c1c !important;
border: 1px solid #333 !important;
color: #eee !important;
margin-bottom:10px !important;
}

td.left.text {
text-align: center !important;
}

.black a:hover,.black {
border-bottom:0 !important;
}

#header,table.red {
display:none !important;
}

.title,.title a {
color:#FFF !important;
}

/*
td.watcher {
width: 15px !important;
}
*/
pre {
background:#101010 !important;
border: 1px #333 dashed !important;
/*width: 80% !important;
max-height: 200px;
overflow: scroll !important;*/
}

p.tags a {
font-size: 12px !important;
}

p.tags a:hover {
text-decoration: none !important;
color: #eee !important;
}

.yeshover,.topic-tags {
background:#101010 !important;
}

td.watcher,td.text,td.info,td.info_light {
background-color:#1c1c1c !important;
border:0px !important;
color:#FFF !important;
}

.info_light {
font: 12px Calibri !important;
}
}
