css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(discuss.com.hk){
/*
* Discuss.com.hk[clean version] 
*
* Author      : Yancie
* Created     : 16/06/11
* Version     : 1.0
* Description : Remove all the ads in Discuss.com.hk
*
*/


/***First page ads remove***/
.ad,#featuread,.banner_b1{display:none !important}

/***First page right hand side bar visible choice***/
#sidebar{visibility:/*[[visibility]]*/; !important}

/***Post page upper ads remove***/
div[align^="center"],#ad_text,#thread_ads_top,div#thread_ads_top + table[width="100%"] > tbody > tr > td > a[target="_blank"],#aYiSxswu,#rightads,#mainbody > tbody > tr > td > br + br + p[align^="center"]{display:none !important}

/***Post page inpost ads remove***/
#startiframe,#startiframe2,#startiframe3,#startiframe4,#displayiframe,#displayiframe1,#displayiframe2,#displayiframe3,#displayiframe4,#hideiframe,#hideiframe1,#hideiframe2,#hideiframe3,#hideiframe4,#ad_interthread,.t_msgfont > b,.t_msgfont > a,
#oVaZmk0,#oVaZmk1,#oVaZmk2,#oVaZmk3,#oVaZmk4,#oVaZmk5,#oVaZmk6,#oVaZmk7,#oVaZmk8,#oVaZmk9,#oVaZmk10,#oVaZmk11,#oVaZmk12,#oVaZmk13,#oVaZmk14,#oVaZmk15,pspan,#bottom_banner
{display:none !important}

/***Post page bottom ads remove***/
#PFhaxCacia,#thread_ads_bottom,div#thread_ads_bottom + table[width="100%"] > tbody > tr > td > a[target="_blank"]{display:none !important}


/***Other ads remove***/
#overture_top,#overture_bottom,#qLdbcfhri,a[href^="http://ad.networld.hk/"]  {display:none !important}

/***Post intext ads remove***/
.t_msgfont > span > span > span {
background-color:transparent !important;
cursor: text !important;
}

/***Position arrangement after ads remove***/
#subhead{
width:400px !important;
position:relative;
top:-60px;
left:80px;
}

#main{
margin-top:-40px;
}



}
