css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ylilauta.fi") {
body, html{
text-transform: none !important;
background: #2c2C2C !important;
color: #eee !important;
font-family: arial !important;
}


a:link {
color: #cccccc !important;
}

a:visited {
color: #cccccc !important;
}

a:hover {
color: #ffffff !important;
}

a:active {
color: #ff0000 !important;
}
.adminbar {
text-transform: none !important;
text-align:right;
background:inherit;
clear:both;
float:right;
}
.admin {
color: #ED00F0 !important;
font-weight: normal;
}
hr {
text-transform: none !important;
background: #333 !important;
width: 50% !important;
height: 2px !important;
border: 1px !important;
margin-left: 0;
}

img {
border: 2px solid #000000 !important;
background-color: #666 !important;
padding: 0px !important;
margin-left:0px !important;
-moz-border-radius:8px !important;
-webkit-border-radius:8px !important;
-khtml-border-radius:8px !important;
border-radius: 8px !important;
}

.ad-title {
display: none !important;
}

#ad {
display: none !important;
}

#footer {
display: none !important;
}

#navtop {
text-transform: none !important;
background: #000000 !important;
width: 99.5% !important;
text-align: center !important;
font-weight: bold !important;
color: #999 !important;
padding: .4em !important;
top: 0;
z-index: 1;
}

#navtopr {
display: none !important;
}

#navtop a:hover {
text-transform: none !important;
background: #555 !important;
}

.logo,.logo font b span{
text-transform: none !important;
padding-top: 15px;
color: #fff !important;
}

form {
text-transform: none !important;
margin: 1em !important;
}

.inputtext, textarea {
text-transform: none !important;
width: 295px !important;
}

.rules{
text-transform: none !important;
text-transform: none !important;
font-size: 10pt;
}

td.postblock {
background: rgba(56,56,56,0.5) !important;
text-transform: none !important;
/* background: #444 !important;*/
border: 0px solid #666 !important;
color: #aaa !important;
-moz-border-radius:3px !important;
-webkit-border-radius:3px !important;
-khtml-border-radius:3px !important;
border-radius: 3px !important;
}
td.reply {
text-transform: none !important;
background-color:#777 !important;
padding: .2em !important;
border: 0px solid #333 !important;
color: #eee !important;
-moz-border-radius:8px !important;
-webkit-border-radius:8px !important;
-khtml-border-radius:8px !important;
border-radius: 8px !important;
}

td.replyhl {
text-transform: none !important;
background-color:#4B4B4B !important;
width: 100% !important;
padding: .2em !important;
border: 0px outset #333 !important;
color: #eee !important;
}
.commentpostername, .omittedposts, .abbr {
text-transform: none !important;
color: #d9d9d9 !important;
}

.postertrip {
text-transform: none !important;
color: #999 !important;
}

blockquote{
text-transform: none !important;
-moz-border-radius:8px !important;
-webkit-border-radius:8px !important;
-khtml-border-radius:8px !important;
border-radius: 8px !important;
opacity:100 !important;
background-color:#777 !important;
color:rgb(0,0,0)!important;
width:500px !important;
border:solid 0px rgb(0,0,0) !important;
margin-left:1px !important;
margin-right:1px !important;
margin-top:1px !important;
margin-bottom:1px !important;
padding-left:30px !important;
padding-right:20px !important;
padding-top:10px !important;
padding-bottom:10px !important;
}
.filetitle, .replytitle {

text-transform: none !important;
color: #444 !important;
font-size: large;
}

.unkfunc {
text-transform: none !important;
color: #000 !important;
font-weight: bold;}

.doubledash {
visibility: hidden !important;
}

th {
text-transform: none !important;
background: #222 !important;
padding: .2em !important;
font-size: large;
width: 200px !important;
}

#hd {
text-transform: none !important;
display: none;
}

.boxbar {
text-transform: none !important;
background: #444 !important;
color: #eee !important;
}

.box-outer {
text-transform: none !important;
border: none !important;
background: #333 !important;
color: #eee !important;
}

.box-inner {
text-transform: none !important;
background: #333 !important;
color: #eee !important;
}

#filter-button, #option-button {
text-transform: none !important;
display: none;
}

#ft{
text-transform: none !important;
display: none;
}

.pages {
text-transform: none !important;
background: #444 !important;
border: none !important;
}

.pages td, .pages td b {
text-transform: none !important;
color: #eee !important;
}

#recent-images li{
text-transform: none !important;
background: #333 !important;
}

pre {
text-transform: none !important;
color: inherit !important;
background: #333333 !important;
font-size: 12pt ! important;
border-left-color: gray !important;
border-right-color: gray !important;
border-top-color: gray !important;
border-bottom-color: gray !important;
}

.reflinkpreview {
text-transform: none !important;
z-index: 90000000000;
background: #777 !important;
border: 2px solid #333 !important;
-moz-border-radius:8px !important;
-webkit-border-radius:8px !important;
-khtml-border-radius:8px !important;
border-radius: 8px !important;
}

.highlight {
text-transform: none !important;
background: #999 !important;
border: 2px dashed #eee !important;
-moz-border-radius:8px !important;
-webkit-border-radius:8px !important;
-khtml-border-radius:8px !important;
border-radius: 8px !important;
}
.postername {
text-transform: none !important;
color: #10a500!important;
font-weight: bold;
}
.postername a {
text-transform: none !important;
color: #1200ff!important;
font-weight: bold;
}
.postertrip {
text-transform: none !important;
color: #10a500 !important;
font-weight: normal;
}
.highlight label, .highlight .filesize{
text-transform: none !important;
color: #eee;
font-weight: bold;
}


div.navbar {
text-transform: none !important;
opacity: 0.1;
position:fixed;
top: 0px;
left: 0px;
background: rgba(102,102,102,0.5) !important;
-moz-border-radius:8px !important;
-webkit-border-radius:8px !important;
-khtml-border-radius:8px !important;
border-radius: 8px !important;
}
div.navbar:hover {
text-transform: none !important;
opacity: 1.0;
background: rgba(102,102,102,0.8) !important;
}

div.navbar a {
text-transform: none !important;
opacity: 0.5;
display: inline-block;
font-size: 16px;
text-align: center;
background: none repeat scroll 0 0 transparent;
margin: 0 4px 0 4px;
}
div.navbar a:hover{
text-transform: none !important;
opacity: 1.0;
}

#watchedthreads {
text-transform: none !important;
opacity: 0.1;
width: 400px !important;
z-index: 90000;
position: fixed !important;
top: 340px !important;
left: auto !important;
right: 8px !important;
background: rgba(102,102,102,0.5) !important;
border: 0px solid #333 !important;
-moz-border-radius:8px !important;
-webkit-border-radius:8px !important;
-khtml-border-radius:8px !important;
border-radius: 8px !important;
}
#watchedthreads span#watchedthreadlist {
text-transform: none !important;
margin: 4px;
display: inline-block;
}
#watchedthreads:hover {
text-transform: none !important;
opacity: 1.0;
}
#watchedthreads img {
text-transform: none !important;
opacity: 0.1;
}
#watchedthreads img:hover {
text-transform: none !important;
opacity: 1.0;
}

#watchedthreads .postername {
text-transform: none !important;
display: none;
}

#watchedthreadsdraghandle {
text-transform: none !important;
display: none;
}

.filetitle{
text-transform: none !important;
color: #eee !important;
}

div.postarea{
text-transform: none !important;
opacity: 0.5;
font-size: xx-small;
text-align: center; margin: auto
background: rgba(102,102,102,0.5) !important;
top: 50px;
margin: 0 auto;
height: 510px;
width: 510px;
-moz-border-radius:8px !important;
-webkit-border-radius:8px !important;
-khtml-border-radius:8px !important;
border-radius: 8px !important;
}




div.postarea input[type=text] {
text-transform: none !important;
width: 170px;
}

div.postarea:hover {
text-transform: none !important;
opacity: 1.0;
}

div.adminbar {
text-transform: none !important;
font-size: xx-small;
position: fixed;
right: 0px;
top: 0px;
}

div.abbrev {
text-transform: none !important;
color: #eee !important;
}

textarea {
text-transform: none !important;
width: 100% !important;
height: 128px;
}

table.userdelete {
text-transform: none !important;
opacity: 0.1;
-moz-border-radius:8px !important;
-webkit-border-radius:8px !important;
-khtml-border-radius:8px !important;
border-radius: 8px !important;
background: rgba(102,102,102,0.5) !important;
font-size: xx-small;
z-index: 9000000000;
position: fixed;
bottom: 24px;
right: 8px;
}
table.userdelete:hover {
text-transform: none !important;
opacity: 1.0;
}

table.userdelete tr + tr {
text-transform: none !important;

/*display: none;*/
}

.replymode {
text-transform: none !important;
margin-left: auto;
margin-right: auto;
-moz-border-radius:8px !important;
-webkit-border-radius:8px !important;
-khtml-border-radius:8px !important;
border-radius: 8px !important;
display: block;
width: 200px !important;
background: rgba(255,196,0,0.5) !important;
}

#posttypeindicator {
text-transform: none !important;
font-size: 8px !important;
}

.extrabtns img, img.unhidethread {
text-transform: none !important;
z-index: 3;
opacity: 0.1;
vertical-align: middle;
}
.extrabtns img:hover, img.unhidethread:hover {
text-transform: none !important;
text-transform: none !important;
opacity: 1.0;
}

body:last-child table {
text-transform: none !important;
border: 0px solid pink !important;
}

body:last-child table td {
text-transform: none !important;
border: 0px solid pink !important;
}



/*Front page!! */
.newssub , h2, h1 {
text-transform: none !important;
color: #eee !important;
-moz-border-radius:8px !important;
-webkit-border-radius:8px !important;
-khtml-border-radius:8px !important;
border-radius: 8px !important;
background: rgba(102,102,102,0.5) !important;
}

.plus {
text-transform: none !important;
-moz-border-radius:8px !important;
background: rgba(102,102,102,0.5) !important;
border-color: #666 !important;
}

h3 {
text-transform: none !important;
background: transparent !important;
color: #eee !important;
}

#topmenu ul li {
text-transform: none !important;
-moz-border-radius:8px 8px 0 0  !important;
-webkit-border-radius:8px 8px 0 0  !important;
-khtml-border-radius:8px 8px 0 0  !important;
border-radius:8px 8px 0 0  !important;
background: rgba(102,102,102,0.5) !important;
border-color: #666 !important;
border-bottom-width: 0px;

}

#Laudat ul li:hover {
text-transform: none !important;
background: transparent !important;
}


}
