css:
/*====================================================*/
/*  Hide Ad
/*====================================================*/

DIV.bks {
  display: none !important;
}


/*====================================================*/
/*  Ideas to down
/*====================================================*/

.furjbqy {
  height: 95% !important;
}

.furjbqy TD {
  vertical-align: bottom !important;
}


/*====================================================*/
/*  Conver to CSS3 (without visual change)
/*====================================================*/

/* main border radius */
.rounddiv, .rounddiv .title, #tab_waste_div, #tab_income_div, #tab_move_div, #tab_change_div, .b_body {
  border-radius: 10px !important;
}

.d_b1, .b2, .b3, .b4, .b5, .b6, .b7, .b1_b {
  display: none !important;
}

.b_body {
  border-width: 1px !important;
  padding-bottom: 10px !important;
  padding-top: 9px !important;
  margin-bottom: 1px !important;
  margin-top: 1px !important;
}

#td_waste .b_body, #td_income .b_body, #td_move .b_body, #td_change .b_body {
  border-bottom-left-radius: 0px !important;
  border-bottom-right-radius: 0px !important;
  margin-bottom: 0px !important;
  padding-bottom: 1px !important;
}

#div_waste_a .b_body, #div_income_a .b_body, #div_move_a .b_body, #div_change_a .b_body {
  border-bottom-width: 0px !important;
}

.rounddiv .title {
  border-bottom-left-radius: 0px !important;
  border-bottom-right-radius: 0px !important;
  border-top-width: 1px !important;
  margin-top: 1px !important;
  padding-top: 9px !important;
}

#tab_waste_div, #tab_income_div, #tab_move_div, #tab_change_div {
  border-top-left-radius: 0px !important;
  border-top-right-radius: 0px !important;
  border-bottom-width: 1px !important;
  margin-bottom: 22px !important;
  padding-bottom: 9px !important;
}

.rounddiv .b_body {
  border-top-left-radius: 0px !important;
  border-top-right-radius: 0px !important;
  border-top-width: 0px !important;
  margin-bottom: 22px !important;
  margin-top: 0px !important;
  padding-top: 0px !important;
}

#remind_div #tab_waste_div {
  border-radius: 0px !important;
  border-bottom-width: 0px !important;
  margin-bottom: 0px !important;
  padding-bottom: 0px !important;
}


/*====================================================*/
/*  Round borders everywhere
/*====================================================*/

.help, #tab_set_waste_div, #tab_set_income_div, #tab_set_place_div, #tab_set_duty_div, #tab_set_currency_div, #tab_set_tag_div, #tab_set_theme_div, #detail_report, #grapth, #remind_div #tab_waste_div, div.restMenu {
  border-radius: 5px !important;
}

#active, .otherMenu {
  border-radius: 3px !important;
}

#active {
  border-bottom-right-radius: 0px !important;
  border-top-right-radius: 0px !important;
}


/*====================================================*/
/*  Upgrade pop-up menu
/*====================================================*/

DIV.restMenu {
  border-top-left-radius: 10px !important;
  margin-top: 4px !important;
  margin-left: 3px !important;
  box-shadow: 3px 3px 6px rgba(0,0,0,0.5) !important;
  border: none !important;
/*  background-color: rgba(240,247,255,0.9) !important;*/
  background:
    -webkit-gradient(linear, left top, right top,
      from(rgba(240,247,255,0.9)),
      to(rgba(240,247,255,0))
    ),
    -webkit-gradient(linear, left bottom, left top,
      from(rgba(240,247,255,0.9)),
      to(rgba(240,247,255,0))
    ) !important;
  background:
    -moz-linear-gradient(
      left center,
      rgba(240,247,255,0.9),
      rgba(240,247,255,0)
    ),
    -moz-linear-gradient(
      center bottom,
      rgba(240,247,255,0.9),
      rgba(240,247,255,0)
    ) !important;
}

DIV.restMenu .h {
  margin-left: -2px !important;
  margin-top: -3px !important;
  text-shadow: 0px -1px 0px white !important;
}

/* Hide close button */
DIV.restMenu:not(#remind_menu) > DIV:first-child {
  visibility: hidden;
}


/*====================================================*/
/*  Rounded panes colors
/*====================================================*/

.rounddiv {
  box-shadow: 0 0 6px rgba(0,0,0,0.3) !important;
  background-color: #B1BCCE !important;
}


/*----------------------------------------------------*/
/*  Rounded panes titles
/*----------------------------------------------------*/

.rounddiv .title {
  background-image:
    -webkit-gradient(linear, left top, left bottom,
      from(#B1BCCE),
      color-stop(0.5, #8C9EB6),
      color-stop(0.5, #899AB4),
      to(#6C85A3)
    ) !important;
  background-image: -moz-linear-gradient(
    center top,
    #B1BCCE,
    #8C9EB6 50%,
    #899AB4 50%,
    #6C85A3
  ) !important;
  border: none !important;
  padding: 6px 1px 0px 1px !important;
}

.minmax {
  margin-top: -7px !important;
}

.rounddiv .title .content, .rounddiv .title .wait {
  color: white !important;
  text-shadow: 0px -1px 0px #586365 !important;
  padding-top: 1px !important;
}

.rounddiv .title .content .dash {
  border-color: white !important;
}

/* arrow down */
.rounddiv .title .dash + IMG, div.restMenu .h IMG {
  width: 0px !important;
  height: 0px !important;
  padding-left: 12px !important;
  padding-top: 12px !important;
  margin-top: 2px !important;
  opacity: 0.6 !important;
  background-image: url(data:image/png;base64,) !important;
}

/* close button */
.rounddiv .title .minmax > A:last-child IMG {
  width: 0px !important;
  height: 0px !important;
  padding-left: 7px !important;
  padding-top: 7px !important;
  background-image: url(data:image/png;base64,) !important;
}


/*----------------------------------------------------*/
/*  Rounded panes bodies
/*----------------------------------------------------*/

.rounddiv .b_body {
  border-color: #B1BCCE !important;
  background-color: #FAFAFA !important;
}

.rounddiv .b_body > TABLE {
  width: 100% !important;
}


/*====================================================*/
/*  Last block
/*====================================================*/

#m_last_block {
  width: 391px !important;
}

#last_block {
  padding-bottom: 3px !important;
  text-shadow: 1px 1px 0px white !important;
}

/* alternating colors */
#last_block > TABLE TR:nth-child(4n-1), #last_block > TABLE TR:nth-child(4n) {
  background-color: rgba(0,0,0,0.04) !important;
}

#last_block > TABLE TD {
  border-top: none !important;
}

/* total row */
#last_block > TABLE TR:last-child {
  background-color: transparent !important;
}

#last_block > TABLE TR:last-child TD {
  border-top: 1px solid white !important;
}

#last_block > TABLE TR:nth-last-child(2) TD {
  border-bottom: 1px solid #CCC !important;
}

#last_block > TABLE TR:last-child TD:first-child {
  padding-left: 10px !important;
}

/* delete buttons */
#last_block > TABLE TD:last-child IMG {
  visibility: hidden !important;
}

#last_block > TABLE TR:nth-child(2n+1):hover TD:last-child IMG {
  visibility: visible !important;
}


/*====================================================*/
/*  Main background color
/*====================================================*/

#tab_waste_div, #tab_income_div, #tab_move_div, #tab_change_div, #edit_record, .yel, #tab_set_waste_div, #tab_set_income_div, #tab_set_place_div, #tab_set_duty_div, #tab_set_currency_div, #tab_set_tag_div, #tab_set_theme_div, #detail_report, #grapth {
  background-color: #F0F7FF !important;/*#F0F0FF;*/
}

#tab_set_waste_div, #tab_set_income_div, #tab_set_place_div, #tab_set_duty_div, #tab_set_currency_div, #tab_set_tag_div, #tab_set_theme_div, #detail_report, #grapth {
  border-color: white !important;
  box-shadow: 0px 0px 0px 1px rgb(160,160,160), 0px 0px 6px 1px rgba(0,0,0,0.3) !important;
}


/*====================================================*/
/*  Main pane
/*====================================================*/

#tab_waste_div, #tab_income_div, #tab_move_div, #tab_change_div, #edit_record {
  border-color: white !important;
  box-shadow: 0px 0px 0px 1px rgb(160,160,160), 0px 0px 6px 1px rgba(0,0,0,0.3) !important;
  width: 387px !important;
  border-width: 1px !important;
  margin-top: -1px !important;
  padding-top: 7px !important;
}

.div_t_a, #w_title, #i_title, #m_title, #ch_title, #waste_fdate, #income_fdate, #move_fdate, #change_fdate {
  text-shadow: 1px 1px 0px white !important;
}

.h_categ.tags {
  text-shadow: 0px 1px 0px white !important;
}

.tags:hover {
  text-shadow: none !important;
  background-color: #6C85A3 !important;
}

#w_short_categ .b_body, #i_short_categ .b_body {
  box-shadow: inset 1px 1px 0px 0px rgba(255,255,255,0.7), 1px 1px 0px 0px rgba(255,255,255,0.7) !important;
}

/*----------------------------------------------------*/
/*  Tabs active
/*----------------------------------------------------*/

#div_waste_a .b_body, #div_income_a .b_body, #div_move_a .b_body, #div_change_a .b_body {
  position: relative !important;
  box-shadow: inset 0px 1px 0px 0px white, inset 1px 0px 0px 0px white, inset -1px 0px 0px 0px white !important;
  border-color: rgb(160,160,160)/*#6C85A3*/ !important;
}


/*----------------------------------------------------*/
/*  Tabs passive
/*----------------------------------------------------*/

#div_waste_p .b_body, #div_income_p .b_body, #div_move_p .b_body, #div_change_p .b_body {
  padding-top: 7px !important;
  border-color: #B1BCCE !important;
}

#div_waste_p:hover .b_body, #div_income_p:hover .b_body, #div_move_p:hover .b_body, #div_change_p:hover .b_body {
  background-color: #F6F6F6 !important;
  padding-top: 9px !important;
}

#div_waste_p .div_t_p, #div_income_p .div_t_p, #div_move_p .div_t_p, #div_change_p .div_t_p {
  margin-top: -20px !important;
}

#div_waste_p:hover .div_t_p, #div_income_p:hover .div_t_p, #div_move_p:hover .div_t_p, #div_change_p:hover .div_t_p {
  margin-top: -21px !important;
  color: #8A8A8A !important;
}


/*====================================================*/
/*  Other
/*====================================================*/

#tplMenuDiv + TABLE TR[onmouseover]:hover,
#tplMenuDiv + TABLE TR[onmouseover]:hover + TR {
  background-color: #DCDCFF !important;
}

.otherMenu, .popup {
  box-shadow: 3px 3px 6px rgba(0,0,0,0.5) !important;
}

/* Calendar icon */
#w_date_click IMG, #i_date_click IMG, #m_date_click IMG, #ch_date_click IMG {
  width: 0px !important;
  height: 0px !important;
  padding-left: 22px !important;
  padding-top: 22px !important;
  background-image: url(data:image/png;base64,) !important;
}

.calendar, .calendar table {
  background-color: rgba(243,247,255,0.5)/*#E5E5F5*/ !important;
}

.help {
  background-color: #FAFAFF !important;
  border-color: red !important;
}
