css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(photobucket.com){
/*
* Photobucket[Ad Cleaner++] + Redesign(optional)
*
* Author      : Yancie
* Created     : 20/06/11
* Version     : 1.0
* Description : Remove all the adds and Redesign the outlook of photobucket.com
*
*/

/*****************Ad Remove********************/
.bannerContainer,#containerMrecAd,#uploadMrecAd,.skyCell,.panelAdv,.whatsnewRefresh,#containerSqAd {display:none !important}

/*[[redesign]]*/

}

style_setting_options.value:
/*****************Redesign********************/

/* Disable the Right sidebar in album page */
#panelAlbumRight {display:none !important}   

/* Make the background image  */
.themeRoot,#widthPage{background-image:url('http://pic.photobucket.com/homepage/textures/paper_grunge_KA.jpeg') !important}

/* Center everything, the album, and give shadow and radius to the border */
.containerAll{
width:990px !important;
background-color:white !important;
margin:20px auto 20px auto !important;
box-shadow:0 0 16px #000 !important;
-webkit-box-shadow: 0 0 16px #000 !important;
-moz-box-shadow: 0 0 16px #000 !important;
-moz-border-radius: 23px 23px 23px 23px !important;
-webkit-border-radius: 23px 23px 23px 23px !important;
}

/* Make the album look more center by giving a little bit empty space at the left hand side  */
#panelAlbumLeft{
padding-left: 10px !important;
}

/* Delete the useless empty space at the bottom  */
.containerAll > .album:first-child {
margin-bottom: -110px !important;}

/* Fix the bug of photobucket.com, which sometimes auto minimize the album to only 3 row  */
#thumbContainer,#panelAlbumLeft{width:970px !important;}

}

@-moz-document url-prefix(http://photobucket.com/){

/*****This part make sure the homepage still looks the same after redesign*****/

.homepage .containerAll {
        box-shadow: 0 0 15px rgba(0,0,0,0.3)!important;
        -webkit-box-shadow: 0 0 15px rgba(0,0,0,0.3)!important;
        -moz-box-shadow: 0 0 15px rgba(0,0,0,0.3)!important;
        -moz-border-radius: 12px!important;
        -webkit-border-radius: 12px!important;
        background-color: #f7f7f7!important;
        padding: 0 10px 10px 10px!important;
        width:970px !important;
}
