css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("xing.com") {

/* erstmal alles was endgültig raus sollte */
.startpage-teaser, .startpage-teaser-full-pic-only, .networking-teaser .dipa-teaser, 
#communication-box, .vertical-intro, #footer, .teaser, .quickinvite
{ display: none !important; }

/* Graue Hintergrund weg */
.container { background: none !important;}

/* Header verschieben */
#header { left: -40% !important; width: 140% !important; padding: 2px !important; }
.nav-main { position: fixed !important; }
.usearchform { right: -200px !important; }

/* Hauptbildschirm inhalt breidter machen */
#content { width: 99% !important; }

/* Hauptbildschirm inhalt breidter machen - ACHTUNG je nach bildschirm sind hier kleinere zahlen zu verwenden */
/* Bildschirm auflösungsverhältnis 4:3 -> 68/78 ; 16:9 -> 72/82 und 16:10 -> 73/83 */
#maincontent { width: 73% !important; padding: 12px !important; }
.wgt-display-list dd { width: 83% !important; }

/* Person darstellung Businesscard */
.business-card { width: 98% !important; margin: 0px !important; }
#business-card-additional { display: inline !important; }
#business-card-additional li { display: inline !important; }
.inplace-editor { width: 100% !important; }
.expand-business-card { display: none !important; }
.action-list-additional ul { display: inline !important; }
.action-list-toggle { display: none !important; }

/* Lightbox - person merken */
#modal-window-addbookmark.lightbox { width: 80% !important; left: 10% !important; }
textarea { width: 100% !important; }

/* Lightbox - Kontakt hinzufügen */
#contact-lightbox { width: 98% !important; left: 10% !important; }
#lightbox { width: 70% !important; left: 10% !important; }
.col-left { width: 80% !important; }
.col-right { margin-left: 83% !important; }
input.text { width: 100% !important; }
.field-row { width: 100% !important; }

/* Leerräume entfernen -> (Activty) Streams verkürzen */
.activity-content { margin-top: 1px !important; margin-bottom: 1px !important; }
.activity-text { margin-bottom: 1px !important; }
.activity-preview { padding-bottom: 1px !important; }
.section { margin-bottom: 1px !important; }
.subnav { margin-bottom: 5px !important; }
.mt20 { margin-top: 1px !important; }
.pagination { padding: 2px !important; margin: 2px !important; }

/* Events etwas besser darstellen */
.details-pairs dt { width: 30% !important; }
.details-pairs dd { width: 60% !important; }

}
