css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain('tumblr.com') {

#logo {
        height: 0 !important;
	width: 0 !important;
	padding-left: 232px !important;
	padding-top: 38px !important;
	background: url(http://i56.tinypic.com/x0w45c.jpg) no-repeat !important;
}

  /* CSS rules here apply to: 

     + The page "http://www.tumblr.com/".

     + Any page whose URL begins with "http://www.tumblr.com/"

     + Any page whose URL's host is "http://www.tumblr.com/" or ends with 

       "http://www.tumblr.com/" 

   */ 
   
#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts,#dashboard_post_queue, #dashboard_followers, #dashboard_members, #main_explore, #dashboard_tumblelog_settings, #dashboard_directory, #dashboard_following, #dashboard_likes, #dashboard_lookup_contacts {
background-color: #FFFFFF;
background-image:url('http://i51.tinypic.com/t6z6u0.jpg') !important;background-repeat:repeat !important;
}

#dashboard_index #content, #dashboard_submissions #content, #dashboard_drafts #content,#dashboard_post_queue #content, #dashboard_members #content, #dashboard_tumblelog_settings #content, #dashboard_followers #content, #dashboard_likes #content {
background: rgba(0,0,0,0.2) !important;
}
	
#dashboard_edit_post {
	background-color: #ffffff !important;
	}
	
#right_column .dashboard_nav_item {
	border-bottom:0px solid #eee !important;
	background-image:none  !important;
	}
	
#right_column .dashboard_nav_item .dashboard_nav_title, #right_column .dashboard_nav_item .dashboard_nav_title a, #right_column .dashboard_nav_item a, #right_column .dashboard_nav_item ul.dashboard_subpages,#right_column .dashboard_nav_item ul.dashboard_subpages li a {
	color: #ffffff !important;
	}
	
#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {
	color: #ffffff !important ;
	}
	
#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold, .radar {
background: none !important;
}

#left_column #posts .notification .avatar { 
	border-color: transparent !important; 
	background-color: #ffffff !important; 
	opacity: 0.7 !important;
	}
	
#left_column #posts .notification .avatar:hover {
	opacity: 1.00 !important;
	}
	
.dashboard_switch_blog_menu_header {
	background-color: #ffffff !important;
	color:#000 !important;
	}
	
.dashboard_switch_blog_menu_item {
	background-color: #ffffff !important;
	color:#000 !important;border:0px !important
	}
	
.dashboard_switch_blog_menu_footer {
	background-color: #ffffff !important;
	color:#000 !important;
	}
	
.dashboard_switch_blog_menu_wrapper{
	background-color: #ffffff !important;
	}
	
#new_post_notice_container {
	color: #fff!important
	}
	
#new_post_notice_container a {
color: #fff !important
} 

#dashboard_switch_blog_menu_current, #dashboard_switch_blog_arrow_and_divider, #dashboard_switch_blog_arrow_current {
	background: #ffffff !important;
	}
	
ol#posts li.post.new_post {
	background: #FFFFFF url('http://i53.tinypic.com/wkpz4h.jpg') center center no-repeat;
	}
	
img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"] {
opacity: 0 !important;
}
	
.new_post_label {
	font-size: 11px !important;
	padding-top: 20px !important;
	font-family: Georgia, Times New Roman, sans-serif !important;
	font-weight: bold !important;
	font-style: italic !important;
        text-shadow: #cecece 1px 1px 1px;
	text-transform: lowercase !important;
	color:#676767 !important;
	letter-spacing: 0px !important 
	}
	
#left_column #posts .notification {
	background-color: #ffffff !important;
	color:#aaaaaa !important;
	border-bottom: #ffffff !important
	}
	
#left_column #posts .notification .block {
	color:#aaaaaa !important;
	}
	
#left_column #posts .notification.alt {
	background-color: #ffffff !important;
	}
	
#left_column #posts .notification.last_notification, #left_column #posts .notification.single_notification {
	border-bottom: #ffffff !important;
	}
	
#left_column #posts .notification a {
	color:#555555 !important;
	border-bottom:0px !important;
	}
	
#left_column #posts .notification blockquote {
	border-left: #ffffff !important;
	}
	
#left_column #posts .notification .avatar {
	border-color: transparent !important;
	background-color: #ffffff !important;
	opacity: 1.00 !important;
	}
	
#left_column #posts .notification .avatar:hover {
	opacity: 0 !important;
	}

.notification_type_icon {
	display:none !important;
	}


.selection_nipple {
border-bottom: 11px solid rgba(0,0,0,0.2) !important;/*share the same color/opacity as the content pane*/
border-top: 11px solid transparent !important;
border-right: 11px solid transparent !important;
margin-top: -11px !important;/*adjust the new nipple position so it meshes seamlessly with the content pane*/
}

/****RADAR PHOTO****/
.post.photo_post .photo,
.post.photo_post .video_thumbnail {
border-radius: 6px !important;
-moz-box-shadow: 1px 1px 10px #444444 !important;
} /*make the radar photo have a shadow like the iconbar and round the corners*/
.content.is_media,
.mask.top,
.mask.bottom {
background:none !important;
}/*make the blue background frame around the radar photo go away*/

#posts .post .permalink {
background: none !important;
}

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/****COLORS****/
#tags .header DIV A, #add_photo_link A {
 color: rgba(120,120,120, 1.0) !important;/*DARKCOL*/
}
.explore_tumblr:hover, .add_and_remove:hover, .open_blog.with_subtitle:hover, .customize:hover, .likes:hover, .settings:hover, .mass_editor:hover, .posts:hover, .messages:hover, .drafts:hover, .queue:hover , .category.active, #categories .category,  .selected .settings, .selected .posts, .selected .followers, .selected .messages, .selected .drafts, .selected .queue, .selected .tag, .post_filter_tabs A,
#auto_pagination_loader, .selected .settings:hover, .selected .posts:hover, .selected .messages:hover, .selected .drafts:hover, .selected .followers:hover, .selected .queue:hover, .post_filter_tabs A:hover:not([class="active"]), .big_button:hover, #categories .category.active, #categories .category:hover:not([class="category active"]), #controls DIV {
 color: rgba(255, 255, 255, 1.00) !important;/*WHITE*/
}
 .category.active, #categories .category, .selected .settings, .selected .posts, .selected .followers, .selected .messages, .selected .drafts, .selected .queue, .selected .tag, .post_filter_tabs A,
#auto_pagination_loader {
  -moz-box-shadow: rgba(0,0,0,1) 0 1px 0 !important;/*BLACK*/
  box-shadow: rgba(0,0,0,1) 0 1px 0 !important;/*BLACK*/
  padding: 9px 8px !important;
  text-align: center !important;
}
#upload_image_form DIV DIV {
 height: 1px !important;
 width: 130px !important;
 font: bold small-caps 0.80em tahoma !important;
 margin-top: -1px !important;
 text-align: center !important;/*add a text post - Upload photo - ugly object to manipulate*/
}
.post_filter_tabs A, .post_filter_tabs A[class="active"], .big_button, .big_button:hover {
 border-right: solid 1px rgba(127,127,127, 0.50) !important;/*MEDMCOL*/
 border-left: solid 1px rgba(127,127,127, 0.50) !important;/*MEDMCOL*/
}
.category.active, #categories .category, .selected .settings, .selected .posts, .selected .messages, .selected .drafts, .selected .queue, .selected .followers, .selected .tag, .post_filter_tabs A,
#auto_pagination_loader, .big_button {
  border-top: 1px solid #B6B6B6 !important;
  background: -moz-linear-gradient(bottom, rgba(120,120,120, 1.0), rgba(200,200,200, 1.0)) !important;/*DARKCOL, LITECOL*/
}
.post_filter_tabs A[class="active"], .post_filter_tabs A:hover, .selected .settings:hover, .selected .posts:hover, .selected .messages:hover, .selected .drafts:hover, .selected .queue:hover, .selected .followers:hover, .post_filter_tabs A:hover:not([class="active"]), .big_button:hover , #categories .category:hover:not([class="category active"]) {
 background: -moz-linear-gradient(top, rgba(120,120,120, 1.0), rgba(200,200,200, 1.0)) !important;/*DARKCOL, LITECOL*/
}
#left_column H1, #page_heading, #content p {
 color: #777777 !important;/*LITECOL*/
}
h2, LABEL, .cell.lead_editor {
 color: #999999 !important;/*LITECOL*/
}
/****Selected category effects for ADD OR REMOVE****/
.category.active .icon.arrow {
 width: 0 !important;
 height: 0px !important;
 border-top: 10px solid transparent !important;
 border-right: 10px solid rgba(200,200,200, 1.0) !important;/*MEDMCOL*/
 border-bottom: 10px solid transparent !important;
 background: -moz-linear-gradient(bottom, rgba(120,120,120, 1.0), rgba(200,200,200, 1.0)) !important;/*DARKCOL, LITECOL*/
 margin-top: -9px !important;
 margin-left: -5px !important;
}
#categories .category.active {
  margin-left: -15px !important;
  margin-right: 15px !important;
  /*color: rgba(255, 255, 255, 1.0) !important;*//*personal prefrence - highlights text for category in "Add or Remove"*/
}
#categories A[class="category active"]:first-child {
 border-top-left-radius: 0px !important;
}
#categories A[class="category active"]:last-child {
 border-bottom-left-radius: 0px !important;
}
/****FONT SIZING****/
#categories .category.active, #categories .category, .category.active, #categories .category, .selected .settings, .selected .posts, .selected .messages, .selected .drafts, .selected .queue, .selected .tag, .post_filter_tabs A, #auto_pagination_loader {
  font-size: 1.00em !important;
}
.selected .following {
  font-size: 0.80em !important;
}
.post_filter_tabs A, .post_filter_tabs A[class="active"] {
 font-size: 0.70em !important;
}
#left_column H1, #page_heading {
 font: normal 1.4em verdana !important;
}
/****FONT SHADOW****/
.selected .settings:hover, .selected .posts:hover, .selected .messages:hover, .selected .drafts:hover, .selected .queue:hover, .post_filter_tabs A:hover:not([class="active"]), .big_button:hover, #categories .category:hover:not([class="category active"]), .category.active, #categories .category, .selected .settings, .selected .posts, .selected .messages, .selected .drafts, .selected .queue, .selected .tag, .post_filter_tabs A, #auto_pagination_loader, .big_button {
  text-shadow: rgba(0,0,0,.6) 0 1px 0 !important;
 }
/***SIZING or POSITION***/
#left_column H1, #page_heading {
 margin-bottom: -10px !important;
 /*margin-top: -20px !important;*/ /*personal preference when Tumblr - Generic Theme Template is applied*/
}
#categories .category {
  padding: 6px 8px !important;
}
.selected .posts {
  padding: 9.5px 8px !important;
}
.selected .tag {
  padding: 8.5px 8px !important;
}
.post_filter_tabs A, .post_filter_tabs A[class="active"] {
 padding: 5px !important;
 width: 59px !important;
}
#right_column UL:nth-child(2) DIV DIV:first-child A:first-child {
 width: 59px !important;
}
.selected .settings:hover, .selected .posts:hover, .selected .messages:hover, .selected .drafts:hover, .selected .queue:hover, .post_filter_tabs A:hover:not([class="active"]), .big_button:hover  {
 margin-left: -1px !important;
 margin-bottom: -1px !important;
}
.post_filter_tabs A:hover:not([class="active"]) {
 margin-left: 0px !important;
}
#categories .category.active, #categories .category {
  padding: 3.125px 8px !important;
  text-align: left !important;
 }
#categories .category:hover:not([class="category active"]) {
 margin-left: -1px !important;
 margin-right: 6px !important;
}
#categories .category.active, #categories .category, #categories .category:hover:not([class="category active"]) {
 margin-bottom: 1px !important;
}
.selected .settings:hover, .selected .posts:hover, .selected .messages:hover, .selected .drafts:hover, .selected .queue:hover, .post_filter_tabs A:hover:not([class="active"]), .big_button:hover, #categories, #categories A:first-child, #categories .category:hover:not([class="category active"]) {
 margin-top: 1px !important;
}
.tabs_3 A {
 padding: 5px 0px !important;
}
.audio_post #left_column H1, .video_post #left_column H1  {
 margin-bottom: 10px !important;
}
#cancel_button, #save_button, #preview_button, #embed_a_video .tabs, #upload_a_video .tabs, #audio_upload .tabs, #search_audio_tab .tabs, #external_mp3_tab .tabs {
 height: 26px !important;
}
#photo_upload {
 margin-top: 25px !important;
}
 #take_photo_button, #add_photo_button {
 margin-top: -10px !important;
}
/****ROUNDING****/
.post.photo_post .photo, .post.photo_post .video_thumbnail, #people_finder, #left_column DIV:nth-child(11)  {
 border-radius: 16px !important;
}
.text_field, #post_state, #tag_editor, .source_url_questionmark_gradient, .mceLayout, .mceToolbar.mceCenter.mceFirst.mceLast, .mceIframeContainer.mceFirst.mceLast, #left_column DIV:nth-child(11), #post_one, #video_upload, .customize_button, .selected .tag, #post_two, #categories A:last-child {
  border-bottom-left-radius: 6px !important;
  border-bottom-right-radius: 6px !important;
}
.text_field, #post_state, #tag_editor, .source_url_questionmark_gradient, .mceLayout, .mceToolbar.mceCenter.mceFirst.mceLast, .mceIframeContainer.mceFirst.mceLast, #left_column DIV:nth-child(11), #post_one, #video_upload, .customize_button, #categories A:first-child, .selected .posts, #left_column DIV:nth-child(9) {
  border-top-left-radius: 6px !important;
  border-top-right-radius: 6px !important;

}

#categories .category,  #categories .category:hover:not([class="category active"]), #categories .category.active, div.hide_overflow, LABEL {
 border-bottom: none !important;
 color: rgba(72,72,72, 1.0) !important;
 text-shadow: rgba(174,174,174, 0.50) 1px 1px 0px !important;}
a.explore_more_tags,a.likes,a.add_and_remove,
a.customize,a.mass_editor {
 background-color: rgba(174,174,174, 01.0) !important;
       border:0px !important;}
.dashboard_options_form {
background: rgba(127,127,127, 0.65) !important;
}
#dashboard_directory #content {
background: rgba(0,0,0,0.2) !important;
}
 }
