||<=============================================================>||
||/*****    AutoHide Locationbar Buttons                   *****/||
||/*****    Context Menu Without Forward/Back Options      *****/||
||/*****    Combined Stop, Refresh, And Throbber Buttons   *****/||
||/*****    Transparent Menus With Dark Background Image   *****/||
||/*****    Hide Disabled Forward/Back Buttons             *****/||
||/*****    Show Searchbar Go Button Only When Typing      *****/||
||/*****    Sliding Addonbar At Right Bottom               *****/||
||/*****    Quickfindtoolbar With Full Controls            *****/||
||/*****    Glowing Urlbar & Searchbar                     *****/||
||/*****    Sliding Iconic Menubar options                 *****/||
||/*****    Menu Button (Iconic) & Tabs In Titlebar        *****/||
||<=============================================================>||
*/

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/*****    AutoHide Location Bar Buttons    *****/

#urlbar:not(:hover) #urlbar-icons {
visibility: collapse !important;
}

/*****    Better Context Menu    *****/

menuitem#context-back, menuitem#context-forward, menuitem#context-reload, menuitem#context-stop, #context-sep-stop {display:none !important;}

/******    Combined Stop, Refresh, And Throbber Buttons    *****/

#stop-button[disabled="true"] { display:none; } 
#stop-button:not([disabled]) + #reload-button { display:none; }

#stop-button:not([disabled]) {
  list-style-image: url("data:image/gif;base64,") !important;
  -moz-image-region: rect(0, 20px, 0, 20px) !important;
}

toolbar[iconsize="small"] #stop-button:not([disabled]) {
  list-style-image: url("data:image/gif;base64,") !important;
  -moz-image-region: rect(0, 16px, 0, 16px) !important;
}

/******    Menus Background Image | Dark Firefox    *****/

popup:not(#autoscroller), menupopup 
{
  background-image: url(data:image/png;base64,) !important;
  background-color: #000000 !important;
  color: #FFFFFF !important;
  background-position: center center !important;
  background-repeat: no-repeat !important;
  background-attachment: scroll !important;
  border-color: #AAAAAA !important;
}


menubar > menu, menupopup > menu, popup > menu, menuitem, toolbarbutton,popup:not(#autoscroller)
{ 
  color: #FFFFFF !important; 
}

/* NEW CODE - Menu Seperator: */
menuseparator
{
  border-color: #AAAAAA !important;
  color: #AAAAAA !important;
}



#contentAreaContextMenu menuitem[disabled="true"],
#contentAreaContextMenu menuitem[disabled="true"] + menuseparator,
menuitem[disabled="true"]
{ 
  /*display: none !important; */
  color:#aaaaaa !important;
}



menupopup > menu[_moz-menuactive="true"],
menupopup > menuitem[_moz-menuactive="true"],
popup > menu[_moz-menuactive="true"],
popup > menuitem[_moz-menuactive="true"] 
{
  color: #FFFFFF !important;  
}

/******    Hide Disabled Forward | Back Buttons    *****/

#back-button[disabled="true"] { display: none; }

#forward-button[disabled="true"] { display: none; }

/*****    Search Bar Go Button | Show when typing only    *****/

#searchbar .searchbar-box:not(focused), #searchbar .searchbar-textbox:not(focused) .search-go-button
{
  display: none !important; 
}

#searchbar .searchbar-box[focused="true"], #searchbar .searchbar-textbox[focused="true"] .search-go-button
{
  display: block !important; 
}

/*****    Transparent Menus Popups    *****/

/* Transparent Menus (regular conditions): */
popup:not(#autoscroller), menupopup
{
  opacity: 0.5 !important;
}

/* Custom (or remove) transparency when hovering over menu */
popup:not(#autoscroller):hover, menupopup:hover
{
  opacity: .95 !important;
}

/*****     Sliding Addon Bar | Right Bottom     *****/

#browser-bottombox {
  position: fixed;
  width: auto;
  bottom: 0px;
  padding-right: 5px;
  padding-left: 5px;
  padding-top: 1px;
  background: -moz-Dialog;
  border: 5px solid Cyan;
  -moz-transition: all 0.25s ease;
}

@media all and (-moz-windows-theme:aero) {

#browser-bottombox:not(:-moz-lwtheme) {
  background: #222334 !important; opacity: 1.5;
}

}


/* Opacity */
/**/

/* Slide effect */
#browser-bottombox {
  width:7px;
  padding-right: 0px;
  padding-left: 0px;
}

#browser-bottombox:hover {
  width:auto;
  padding-right: 5px;
  padding-left: 5px;
}

#browser-bottombox > #addon-bar {
  visibility: hidden !important;
}

#browser-bottombox:hover > #addon-bar {
  visibility: visible !important;
}

/* Side */
#browser-bottombox {
  right: 10px;
  -moz-border-radius: 7px 0 0 0;
  background-position: right bottom !important;
  border-bottom: none;
  border-right: none;
}

#addon-bar > #status-bar > .statusbar-resizerpanel {
  position: relative;
  margin-right:-4px;
}

#FindToolbar {
  position: fixed !important;
  border: 1px solid transparent;
  border-top: none; 
  border-radius: 7px 7px 7px 7px;
  background: inherit;
  bottom:5px;
  right:500px;
  background-position: 78% -40px;
}

.statuspanel-label {
  border-bottom-left-radius: 3.3px;
  border-bottom: 1px solid rgb(204, 204, 204) !important;
}

statuspanel:-moz-locale-dir(ltr)[mirror],
statuspanel:-moz-locale-dir(rtl):not([mirror]) {
  bottom: 22px;
}

/* Shadoweffect */
#browser-bottombox, #FindToolbar {
  -moz-box-shadow: 0 0 10px Cyan;
}

/* Borders */
#browser-bottombox, #FindToolbar {
  border-color: -moz-ButtonDefault;
}

@media all and (-moz-windows-theme:aero) {

#browser-bottombox, #FindToolbar {
  border-color: rgba(10%,10%,10%,.4);
}

}

/* Everything else */

#addonbar-closebutton {display: none !important;}

@media all and (-moz-windows-theme:luna-blue),(-moz-windows-theme:luna-silver),(-moz-windows-theme:luna-olive),(-moz-windows-theme:zune),(-moz-windows-theme:royale),(-moz-windows-theme:aero),(-moz-windows-classic) {

#addon-bar {
  -moz-appearance: menuitem !important;
}

}

#main-window:-moz-lwtheme #FindToolbar {
  border-top: none!important;
}

#main-window #tab-view-deck #navigator-toolbox[customizing="true"] ~ #browser-bottombox {
  width: auto !important;
  bottom: 0px !important;
  padding-right: 5px !important;
  padding-left: 5px !important;
  opacity: 1 !important;
}

#main-window #tab-view-deck #navigator-toolbox[customizing="true"] ~ #browser-bottombox > #addon-bar {
  visibility: visible !important;
}

/*****    Quick FindToolBar With Full Controls    *****/

#FindToolbar > *, .findbar-container > * {display:-moz-box !important;}

#FindToolbar .findbar-textbox{
   width: 18em !important;
}

/*****    Glowing UrlBar & SearchBar    *****/

#urlbar, #searchbar .searchbar-textbox {
       background-color: transparent !important;
}

#identity-box {
       background: none !important;
       margin-top: 1px !important;
       margin-bottom: 1px !important;
       margin-left: 1px !important;
       padding: 0 !important
}

#identity-icon-labels {
       color: #33FF33 !important;
}

#urlbar:hover{ 
       -moz-appearance: none !important; 
       -moz-box-shadow: cyan 0px 0px 10px 4px!important;
}

#searchbar .searchbar-textbox:hover{ 
       -moz-appearance: none !important; 
       -moz-box-shadow: lightsalmon 0px 0px 10px 4px!important;
}

#urlbar[focused="true"]{ 
       -moz-appearance: none !important; 
       -moz-box-shadow: lightgreen 0px 0px 10px 4px!important;
}

#searchbar .searchbar-textbox[focused="true"]{ 
       -moz-appearance: none !important; 
       -moz-box-shadow: cyan 0px 0px 10px 4px!important;
}

/*****    Sliding Iconic Menubar     *****/

/* Sliding Iconic Menubar (CrystalSVG icons) */
/* Apply changes to FireFox main menu only */
@-moz-document url(chrome://browser/content/browser.xul){

/* Initial menu setup */
/* Remove text from Main Menu */
menubar > menu > .menu-iconic-text {
   display: none !important;
}
/* Remove free space for text and dropdown simbols */
menubar > menu > hbox[anonid="accel"],
menubar > menu > .menu-right {
   display: none !important;
}
/* Adding free space around icons */
menubar > menu > .menu-iconic-left {
   padding-left: 3px !important;
   padding-right: 3px !important;
}
/* Image for collapsed menu */
#menubar-items {
  background-image: url("data:image/png;base64,") !important;
  background-repeat: no-repeat !important;
  background-position: 5px center !important;
  padding-left: 26px !important;
}
/* Collapsing menu to one icon */
#menubar-items > #main-menubar {
  visibility: collapse !important;
}
/* Showing menu */
#menubar-items:hover > #main-menubar {
  visibility: visible !important;
}
/* Hiding useless menu image */
#menubar-items:hover {
  background-image:none !important;
  padding-left: 0px !important;
