/******     Menu Button (Iconic) & Tabs In Titlebar   *****/

#appmenu-button .button-text {
	display: none !important;}

#appmenu-button dropmarker {
	margin: 18px 0 0 4px !important;}

#appmenu-button {
	list-style-image: url("chrome://branding/content/icon16.png") !important;
	padding: 0 6px 1px 5px !important;
	min-width: 10px !important;
	width: 43px !important;}

#main-window:-moz-window-inactive #appmenu-button:not(:hover) {
	background: transparent !important;
	box-shadow: 0 0 0 1px rgba(255,255,255,.25) inset !important;}

/******     Tabs In Titlebar   *****/

#main-window[tabsontop="true"][sizemode="normal"] #TabsToolbar {    
     margin-left: 35px !important; 
     margin-right: 90px !important; 
}


#main-window[tabsontop="true"][sizemode="normal"] #toolbar-menubar { 
     margin-left: 98px !important; 
     margin-top: -20px !important; 
}


#main-window[tabsontop="true"][sizemode="normal"] #appmenu-button-container {
position: relative !important;
z-index: 2 !important;
}

/*****    Rounded Tabs    *****/

.tabbrowser-tab,
.tabs-newtab-button {
background-size: 100% -moz-calc(100% - 2px) !important;
-moz-border-image: url("data:image/png;base64,") 4 5 3 6 / 4px 5px 3px 6px stretch !important;
border-radius: 10px 8px 0 0!important;
padding: 0 !important;
}

.tabbrowser-tab[pinned] .tab-reload-icon,
.tabbrowser-tab[pinned] .tab-protect-icon,
.tabbrowser-tab[pinned] .tab-lock-icon,
.tabbrowser-tab[pinned] .tab-icon-image,
.tabbrowser-tab[pinned] .tab-throbber {
margin-right: 2px !important;
margin-left: 2px !important;
}

.tabbrowser-tab[pinned],
.tabs-newtab-button {
max-width: 31px !important;
min-width: 31px !important;
}
