css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chat.deviantart.com") 


{
.damncr .msg {
font-family:lucida console!important;
}
.damncrc-topic {
background:-moz-linear-gradient(top, #FF0000, #770000) !important;
color: #202020!important; 
border-top:2px solid #202020!important;
border-right:0.5px solid #202020!important;
border-bottom:0.5px solid #202020!important;
border-left:2px solid #202020!important;
-moz-border-radius:10px!important; 
}
.odd-beacon, .alt1 {
background-color: #000000;
}
.damncrc-topic a {
color:#101010!important;
}
.damncr-title {
background:-moz-linear-gradient(bottom, #770000, #FF0000)!important;
border-bottom:1px solid #101010!important;
color: #101010!important; 
}
.damncr-title a:hover {
color:#fff!important;
}
.damncr-title a{
color:#660000!important;
}
.damncrc-chat-window {
border:2px solid #202020!important;
}
.damncrc-chat {
color: #990101!important;
background: #181818
}
.damncri-member .b {
background:-moz-linear-gradient(top, #FF0000 60%, #770000)!important;
-moz-border-radius:5px
}
.alt0 {
background:-moz-linear-gradient(bottom, #101010, #181818)!important;
}
.damnc-tabbar a { 
background:-moz-linear-gradient(top, #771010 60%, #770000)!important;
-moz-border-radius: 3px 0px 0px 3px!important;
font-family:lucida console!important;
font-weight:bold!important;
}
.damnc-tabbar a i { 
background:-moz-linear-gradient(top, #771010 60%, #770000)!important;
-moz-border-radius:0px 2px 2px 0px!important;
}
.damnc-tabbar strong.withX { 
background:-moz-linear-gradient(bottom, #FF0000, #FF0000)!important;
-moz-border-radius:3px 3px 0px 0px!important;
padding-right:23px!important;
margin-right:2px!important;
color:#440000!important;
font-family:lucida console!important;
font-weight:bold!important;
border:5px!important;
}
.damnc-tabbar strong.withX i {
display:none!important;
}
.damnc-tabbar strong {
background:-moz-linear-gradient(bottom, #000000, #000000)!important;
-moz-border-radius:7px 7px 0px 0px!important;
border-right:1px solid #9EB1A2!important;
padding-right:23px!important;
margin-right:2px!important;
}
.damnc-tabbar strong i {
display:none!important;
}
.damnc-serverlog-outer {
background:-moz-linear-gradient(bottom, #202020 99%, #404040)!important;
border-top:1px solid #202020!important;
border-right:3px solid #202020!important;
border-bottom:1px solid #202020!important;
border-left:2px solid #202020!important;
color:#000000!important;
}
.damncr .self-hl .from span {
background:#222222!important;
padding:0px 3px 0px 3px!important;
-moz-border-radius:5px!important;
}
.damncr .other-hl .from span {
background:#330000!important;
padding:0px 3px 0px 3px!important;
-moz-border-radius:5px!important;
}
.damncr .other-hl .text span {
background:#330000!important;
padding:0px 3px 0px 5px!important;
-moz-border-radius:5px!important;
}
.superdamn-buttonbar {
position:relative!important;
top:0px!important;
}
input {
-moz-border-radius:3px!important;
background:-moz-linear-gradient(bottom, #770000, #FF0000 99%)!important;
width:99.5%!important;
font-family:lucida console!important;
font-weight:bold!important;
color:#440000!important;
border:#770000;
}
textarea {
-moz-border-radius:3px!important;
background:-moz-linear-gradient(bottom, #770000, #FF0000 99%)!important;
width:99.5%!important;
font-family:lucida console!important;
font-weight:bold!important;
color:#440000!important;
border:#770000;
}
input.button {
position:relative!important;
top:2px!important;
left:0px!important;
width:50px!important;
float:right!important;
}
.damncrc-input {
border-right:3px solid #101010!important;
}
.damncr-members dt {
background:-moz-linear-gradient(top, #202020, #FF0000 5%, #440000 95%, #303030)!important;
border:1px solid #101010!important;
margin-left:-14px!important;
margin-right:-23px!important;
text-align:center!important;
white-space:nowrap!important;
-moz-border-radius:1px!important;
color:#330000!important;
font-family:courier!important;
font-weight:bold!important;
}
.damncr-members dl dd {
position: static;
background:#181818!important;
-moz-border-radius:7px!important;
color:#FF0000!important;
font-family:lucida console!important;
padding-bottom:0px!important;
padding-right:0px!important;
}
.damn-lo-col-right {
background:#101010!important;
border-left:1px solid #202020!important;
border-top:2px solid #101010!important;
overflow-x:hidden!important;
}
.damncrc-iconbar {
border-top:1px solid #101010!important;
border-right:3px solid #101010!important;
height:20px!important;
}
/*div.superdamn-buttonbar {
position:relative!important;
float:right!important;
top:25px!important;
right:40px!important;
}*/
.alt1-left-border {
background:#202020!important;
border-top:1px solid #101010!important;
border-right:1px solid #101010!important;
border-bottom:3px solid #101010!important;
border-left:2px solid #101010!important;
}

.aside-left {
background:#101010!important;
border-left:1px solid #101010!important;

}

.connarea {
border-top:1px dotted #202020!important;

}
.damncr-infoctr {
background:#202020!important;
border-top:1px solid #202020!important;
border-right:3px solid #202020!important;
border-bottom:1px solid #202020!important;
border-left:2px solid #202020!important;
}

div.bodyarea alt1-border {
background:#202020!important;

}

span.away {
color:#000000!important;

}

body a.gmbutton2, .gmbutton2, .gminlet 

{
display:none!important;
}
.damncr .msg .ts 
{
color:#505050!important;
font-family:courier new!important;
}
.damncrc-iconbar
{
padding-bottom:2px!important;
padding-top:2px!important;
}
.damncrc-iconbar .away 
{
background:-moz-linear-gradient(top, #FF0000 50%, #770000)!important;
color:#440000!important;
-moz-border-radius:2px!important;
border:#440000!important;
}
.damncrc-iconbar .away .t
{
color: #660000!important;
}
.oh-mg 
{
display:none!important;
}
.superdamnbutton
{
-moz-border-radius:5px!important;
height:20px!important;
}
}
