css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("slashdot.org") {

body
{ 
color: #fff !important; 
background: url("http://img706.imageshack.us/img706/1476/stripen.png") repeat fixed, -moz-radial-gradient(100% 50%, circle cover, #66ffff 3%, #44cccc 10%, transparent 45%) fixed, -moz-radial-gradient(0% 50%, circle cover, #66ffff 3%, #44cccc 10%, transparent 45%) fixed, -moz-radial-gradient(50% 0%, circle cover, #66ffff 3%, #44cccc 10%, transparent 45%) fixed, -moz-radial-gradient(50% 100%, circle cover, #66ffff 3%, #44cccc 10%, transparent 45%) fixed, #052222 !important; 
}

section {background: transparent !important;}  
  
header.container, .col_1 { background: transparent !important;}

#content, #slashboxes ul li:nth-child(2n-1), #slashboxes ol li:nth-child(2n-1), .listed li:nth-child(2n-1), #comments, .slashdot-it #commentlisting li.comment.full, #comments .full, .comment, #users-blocks
 {background: rgba(20,20,20,.2) !important;  }

#links, .article, article, #ccw-body, footer, #modal_box_content table, form fieldset  {background: rgba(20,20,20,.4) !important; }

.active, .active span, .slant, .daybreak .after a, .daybreak .before a, .menu .selected, .menu .selected span { background: #464646 !important;}

form input[type="text"], form input[type="tel"], form input[type="email"], form input[type="password"], form textarea {background: rgba(0,0,0,.5) !important;}

.details, .comment:hover 
{
background: -moz-repeating-linear-gradient(-15deg,rgba(50,50,50,.50) 0px, rgba(90,90,90,.50) 75px, rgba(155,155,155,.50) 200px, rgba(70,70,70,.50) 325px, rgba(50,50,50,.50) 400px), fixed, fixed !important; 
}

#modal_box
{
background: -moz-repeating-linear-gradient(-15deg,rgba(50,50,50,.50) 0px, rgba(90,90,90,.50) 75px, rgba(155,155,155,.50) 200px, rgba(70,70,70,.50) 325px, rgba(50,50,50,.50) 400px), fixed, fixed, rgba(10,10,10,.6) !important; 
}

body.slashdot-it li.comment.full .title h4, header.h hgroup, #firehose article header h2, #modal_box #editor h2.firehose, .pollBooth_view .title h3, .bar .barColor, #slashboxes article header, .btn, button, input[type="submit"], input[type="reset"], input[type="button"], .msg p
{
background: -moz-repeating-linear-gradient(-15deg,rgba(50,50,50,.50) 0px, rgba(90,90,90,.50) 75px, rgba(155,155,155,.50) 200px, rgba(70,70,70,.50) 325px, rgba(50,50,50,.50) 400px), fixed, fixed, rgba(30,30,30,1) !important; 
}

a, .btn, button, input[type="submit"], input[type="reset"], input[type="button"], nav.left_menu li.active a {color: #99ffff !important;}

.article, .b, input[type="text"], input[type="password"], .commentBody, .commentBody p, textarea, footer, p:not(.success):not(.notice):not(.error) {color: #bbb !important;}

.details, h3 {color: #66cccc !important;}

h4, #ccw *, .ccw-count-text, .quote {color: #fff !important;}


#content, article, #slashboxes ul li, #slashboxes ol li, .listed li, .slashdot-it #commentlisting li.comment.full, #comments .full, .btn, form input[type="text"]:focus, form input[type="tel"]:focus, form input[type="email"]:focus, form input[type="password"]:focus, form textarea:focus, input[type="submit"], input[type="button"], #modal_box_content table td, form fieldset, dt
 {border: none !important;}

form input[type="text"], form input[type="tel"], form input[type="email"], form input[type="password"], form textarea, hr { border: 1px solid #000 !important;}


#links {padding: 0 0 1em 1em !important;}
article footer {padding: 0 10px !important;}
#commentlisting .btn {padding: 4px !important;}
form input[type="text"]:focus, form input[type="tel"]:focus, form input[type="email"]:focus, form input[type="password"]:focus, form textarea:focus {padding: 6px !important;}
body {padding: 40px 5% !important;}
#users-blocks {padding: 20px !important;}
fieldset {padding: 10px 20px 20px 20px !important;}
legend {padding-top: 25px !important;}


.commentSub {margin-top: 5px !important;}
.col_1 {margin: 0 0 0 .5em !important;}
.comment > ul {margin: 0px 10px 10px 10px !important;}
.slashdot-it #commentlisting li.comment.full, #comments .full {margin-bottom: 30px !important;}
article {margin: 0 0 25px 0 !important;}

a:hover {text-shadow:  #fff 0px 0px 2px !important;}

.article, .btn, button, input[type="submit"], input[type="reset"], input[type="button"], a, h3, p, .quote, footer span {text-shadow: #000 0px 0px 2px !important;}



a, a:hover, #comments .oneline > div > div > div.title > h4 a {text-decoration: none !important;}


.briefarticle, #ccw-body, article footer, #users-blocks {border-radius: 10px !important;}
#links {border-radius: 0 0 10px 10px !important;}


.btn, button, input[type="submit"], input[type="reset"], input[type="button"]  {box-shadow: 0 0 .5em rgba(0,0,0,0.9), inset 0 0 2em #66cccc !important;}

form input[type="text"]:focus, form input[type="tel"]:focus, form input[type="email"]:focus, form input[type="password"]:focus, form textarea:focus
{box-shadow: 0 0 .5em #99ffff, inset 0 0 .25em #66cccc !important;}

.btn:hover, button:hover, input[type="submit"]:hover, input[type="reset"]:hover, input[type="button"]:hover {box-shadow: 0 0 .5em rgba(0,0,0,0.9), 0 0 1em 3px #99ffff, inset 0 0 2em #66cccc !important;}

.article, .briefarticle, article, .msg p {box-shadow: 0 1px 5px #000 !important;}

#content:not(.a2) #firehose article.currfh, #modal_box
{
box-shadow: 0 1px 15px #99ffff !important;
}

#modal_box #logo a
{
background: url("http://a.fsdn.com/sd/logo_w_l.png") no-repeat scroll 30px center, rgba(10,10,10,.4) !important; padding: 5px 100% 5px 5px !important; margin: -10px 0 0 -20px !important;

}

h1 {font: 24px/1.5 'Verdana',sans-serif !important;}

.msg span.slant, .advertisement {display: none !important;}

}
