css:
@namespace html url(http://www.w3.org/1999/xhtml);
/* BananaCleaner */

@-moz-document domain('banana.by') {
body { background:url(http://s003.radikal.ru/i203/1106/21/0c7d008b7de6.jpg) fixed !important; text-align: center !important;}
.header, .rm { display:none; }
.lm_g, .lm_f, .lm_b, .lm_l { display:none; }
.lm_block_h, .lm_block_f { display:none; }
.lm_block_c { 
    background: #D2A701 !important;
opacity: 0.5;
    -webkit-border-radius: 10px;
    -moz-border-radius: 10px;
    padding:10px  !important;
}
.lm_block_c .profile { 
    background: none !important;
    padding:0px !important;
}
.lm_block_c .profile  p{
    background: none !important;
    margin: 0 0 15px 0 !important;
}
.lm_block_c .pass {
    background: none !important;
    margin:0 16px 0 0 !important;
}
.lm_block_c .forgot {
    background: black !important;
    width:110px !important;
    padding:0 0 0px 95px !important;
    text-align:right;
}
.lm_block_c .reg {
    background: none !important;
    width:110px !important;
    padding:0 0 0px 95px !important;
    text-align:right;
}
.lm {     background: none !important; width:235px !important; padding-top:48px !important;}
.top_menu {
    background:none  !important;;
    margin:0 !important;;
    padding:0 !important;;
}
.top_menu li {
    background: none !important;
    margin:0 !important;;
    padding:10px 5px !important;;
}
.title_top, .title_tr, .title_cr, .title_foot, .title_fr { display:none; }
.content {     background: none !important; margin: 0 auto !important;; max-width: 1240px !important;; text-align:left !important;; }
.top_menu  li:nth-child(2) { display:none; }
.top_menu  li:nth-child(3) { display:none; }
.top_menu  li:nth-child(4) { display:none; }
.top_menu  li:nth-child(6) { display:none; }
.top_menu  li:nth-child(7) { display:none; }
.top_menu  li:nth-child(8) { display:none; }
.top_menu  li:nth-child(9) { display:none; }
.top_menu  li:nth-child(10) { display:none; }
.top_menu  li:nth-child(11) { display:none; }

.content ul:first-child >  li:nth-child(1){ display:none; }
.top_menu li:nth-child(1) {     background: none !important; display:inline !important; }
#dle-content  .c > table:nth-child(5) {  display:none; }
#dle-content  .c > table:nth-child(6) {  display:none; }
#dle-content  .c > table:nth-child(7) {  display:none; }
.story_info div:nth-child(1) {  display:none; }
.story_info div:nth-child(2) {  display:none; }
.story_info div:nth-child(3) > p:nth-child(2) {  display:none; }
.story_info {     background: none !important; height: 1px; }


#dle-content >  li:nth-child(2){     background: none !important; }

ul > li:nth-child(2) {  display:none; }
#MarketGidComposite4631 {display:none;}
#MarketGidComposite4633 {display:none;}
#MarketGidComposite4900 {display:none;}
.content  td:nth-child(1) ul:nth-child(1) li:nth-child(3) { display:none; }
.content  td:nth-child(1) .lm center {display:none;}
#adv_migom {display:none;}

.komkopos {    background: none !important;}
.comment_autor_info {    background: none !important;}
.lenta_item {    background: none !important;}
.Content-Type {    background: none !important;}
#top {    background: none !important;}
}
