css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("yugioh.wikia.com"){

body {
    background-color: #111111 !important;
    background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAACgAAAAoCAIAAAADnC86AAAAuUlEQVRYhcXWsQ3EMAwDQL6%2F9Qjef0IN8E2AIIETWxLFZ08c2PEzxkA0Zhbutr%2BovfcgnFQRW5xXIzBFdcMs1QcTVQfMVXdhuroFV6hruEhdwHXqG1yqPsLV6hwWqBNYo95hmXqBleoJi9UD1qsAvq3F309YReZzZVQzC8JJFbHFeTUCU1Q3zFJ9MFF1wFx1F6arW3CFuoaL1AVcp77BpeojXK3OYYE6gTXqHZapF1ipnrBYPWC9CuAHc7Z1q4XledcAAAAASUVORK5CYII%3D) repeat scroll 0 0 transparent !important;
         /*data uri is this image http://img811.imageshack.us/img811/2827/18555723.png*/
    color: #66CC00 !important;
}

a {
    color: #0155FF !important;
}

.plainlinks a {
    color: #0588FF !important;
}

.navbox a {
    color: #0588FF !important;
}

h2, th[style*="rgb(128, 0, 0)"] {
    color: #888888 !important;
    background: black !important;
}

table.swmtable tbody {
    border: 2px solid #888888 !important;
}

table.smwtable th {
    background: #777777 !important;
}

table.smwtable td {
    background: #898989 !important;
}

th span{
    color: #333333 !important;
}

#WikiaArticle table.type-animanga th, .box.colored.type-character > tbody > tr > th  {
    background: #747474 !important;
}

#WikiaArticle table.type-animanga td, .box.colored.type-character td {
    background: #7D7D7D !important;
}

#WikiaArticle table.type-animanga th.mainheader{
    background: #666666 !important;
}

.WikiaArticle h2 {
    border-bottom: 0px !important;
    border-left: 1px dotted yellowgreen !important;
    border-right: 1px dotted yellowgreen !important;
}
.WikiaHeader {
    background-color: #342F2F !important;
    background-image: -moz-linear-gradient(left center , #1A1B3D 0pt, #565660 50%, #2C2E68 100%) !important;
}
.WikiaHeader nav {
    border-bottom: 1px solid #342F2F;
    border-top: 1px solid #706B6B;
}
.WikiHeader li a {
    border-left: 1px solid #111111 !important;
    color: #FF11BB !important;
}
.WikiHeader li > ul a {
    border-left: 0px none !important;
    color: #E448F0 !important;
}
.WikiHeader li:hover {
    background-image: -moz-linear-gradient(center top , #666666 0%, #444444 100%) !important;
}
.WikiHeader .chevron {
    border-top-color: #666666 !important;
}
.WikiHeader .subnav {
    background: none repeat scroll 0 0 #555555 !important;
    border-color: #999999 !important;
}
.WikiaPage {
    background: none repeat scroll 0 0 #342F2F !important;
    border: 1px solid #706B6B !important;
}
.WikiHeader .shadow-mask {
    background: none repeat scroll 0 0 #706B6B !important;
}
a.wikia-button, .wikia-single-button a, .wikia-menu-button, input[type="submit"], input[type="reset"], input[type="button"], .button, button {
    background-color: #FEC356 !important;
    background-image: -moz-linear-gradient(center top , #3399FF 35%, #2359A1 65%) !important;
    border: 1px solid #2359A1 !important;
    color: black !important;
}
a.wikia-button:hover, .wikia-single-button a:hover, .wikia-menu-button:hover, input[type="submit"]:hover, input[type="reset"]:hover, input[type="button"]:hover, .button:hover, button:hover {
    background-color: #FEC356 !important;
    background-image: -moz-linear-gradient(center top , #346AB2 35%, #2288EE 65%) !important;
    border: 1px solid #2359A1 !important;
    color: black !important;

}
a.wikia-button.secondary, .wikia-single-button a.secondary, .wikia-menu-button.secondary, input.secondary[type="submit"], input.secondary[type="reset"], input.secondary[type="button"], .button.secondary, button.secondary {
    color: black !important;
}
.wikia-menu-button > li > a, .wikia-menu-button > li > span {
    border-right: 1px solid #555588 !important;
}
.WikiaRail .module, .WikiaRail #RIGHT_SKYSCRAPER {
    background-image: -moz-linear-gradient(center top , #444444 60%, #222222 100%)!important;
    border: 1px dotted #C8C8C8 !important;
}
.WikiaSearch input[type="text"] {
    background: none repeat scroll 0 0 #333344 !important;
    border: 1px solid #D9D9D9;
    color: #66CC00 !important;
    -moz-border-radius: 4px 4px 4px 4px;
}
table.cardtable {
    background: none repeat scroll 0 0 #333333 !important;
}
.navbox, .navbox-subgroup {
    background: none repeat scroll 0 0 #444444;
}
.cardtable .navbox-title, .cardtable table.navbox th {
    color: #11DD11 !important;
}

.navbox-list {
    background: none repeat scroll 0 0 #444444 !important;
    border-left: 2px solid #888888 !important;
}
.navbox, .navbox-subgroup {
    background: none repeat scroll 0 0 #888888 !important;
}
.navbox-title, table.navbox th {
    background: none repeat scroll 0 0 #666688 !important;
}
span > a {
    color: #2222DD !important;
}
ul.wikia-menu-button a {
    color: black !important;
}
.cardtablestatuses th {
    background: none repeat scroll 0 0 #323232 !important;
}
.cardtablestatuses td {
    background: none repeat scroll 0 0 #434343 !important;
}
.cardtablestatuses th.cardtablestatusesgroupheader {
    background: none repeat scroll 0 0 #000000 !important;
}
.WikiaArticleCategories {
    background-color: #454545 !important;
    background-image: -moz-linear-gradient(center top , #444444 50%, #232323 100%) !important;
}
.WikiaSearch .autocomplete {
    background: none repeat scroll 0 0 black !important;
}
.WikiaSearch .autocomplete .selected {
    background: none repeat scroll 0 0 #665566 !important;
}
ul.tabbernav li a {
    background: none repeat scroll 0 0 #676676 !important;
    color: lime !important;
}
ul.tabbernav li.tabberactive a{
    background: none repeat scroll 0 0 #9A71A9 !important; 
    border-bottom: 0px !important;
}
table.toccolours a {
    color: blue !important;
}
table.toccolours {
    background: none repeat scroll 0 0 #555566 !important;
}
table.toccolours tbody > tr:first-child >td {
    background: none repeat scroll 0 0 #78787F !important;
}
td.navbox-group {
    background: #333333 !important;
    border: #444444 !important;
}
}
