css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("linux.org.ru") {
	* {
		background: none !important;
	}

	body {
		color: #c8c8c8 !important;
		background-color: #000000 !important;
		font-size: 10pt !important;
		font-family: sans-serif !important;
	}

	textarea {
		font-size: 10pt !important;
	}

	a {
		color: #ffffff !important;
	}

	option {
		color: white !important;
		background-color: #404040 !important;
	}

	pre {
		font-size: 1em !important;
	}

	/* wiki */

	div#nav-menu, div#nav-search {
		background-color: #282828 !important;
	}
	div#nav-search input {
		color: white !important;
		background-color: #404040 !important;
	}
	div.tab-item {
		background-color: #282828 !important;
	}
	div#contents {
		background-color: #404040 !important;
		color: #ffffff !important;
	}
	div#contents h1, div#contents h2, div#contents h3 {
		color: #ffffff !important;
        }

	/* forum */

	table.nav td {
		border: none !important;
	}
    table.nav a {
        text-decoration: none !important;
    }
	div.nav {
		padding: 0 5px !important;
	}
	div.nav p {
		margin: 0.5em 0 !important;
	}
	table.message-table {
		background-color: #282828 !important;
	}
	table.message-table thead th, table.message-table tfoot td {
		background-color: #282828 !important;
	}
	table.message-table tbody td {
		background-color: #404040 !important;
		padding: 1px 5px !important;
	}
	table.message-table tbody tr:hover td {
		background-color: #343434 !important;
	}
	table.message-table tbody td a:visited {
		color: #c8c8c8 !important;
	}
	span.tag {
	   background-color: #333333 !important;
	}

	/* thread */

	div#dw {
		display: none !important;
	}
	strong.page-number {
	    background-color: #cccccc !important;
	    color: #000000 !important;
	}
	.msg {
		background-color: #404040 !important;
		border: 1px solid #282828 !important;
		padding: 0 !important;
		-moz-border-radius: 3px !important;
		border-radius: 3px !important;
	}
	.news .msg {
		background-color: #000000 !important;
		border: none !important;
	}
	.msg:target {
		border: 2px solid #c8c8c8 !important;
	}
	.msg div.title {
		background-color: #282828 !important;
		padding: 0 10px 1px 10px !important;
		margin: 0 !important;
	}
	.msg div.userpic {
		display: none !important;
	}
	.msg div.msg_body {
		margin-left: 10px !important;
		margin-right: 10px !important;
		padding: 0 0 2em 0 !important;
	}
	.msg .msg_body h1, .msg .msg_body h2 {
		font-size: larger !important;
		margin: 0.5em 0 !important;
	}
	.msg .msg_body p {
		margin: 1em 0 !important;
	}
	.msg .msg_body .sign {
        float: left;
		margin: 0.5em 0 0.5em 0 !important;
	}
	.msg .msg_body .reply {
		float: right;
		margin: 1em 0 0.5em 0 !important;
	}
    .msg .tags-section-info {
        border: none !important;
        padding: 0 1em !important;
    }
    .msg footer {
        float: left;
        border: none !important;
        padding: 0 !important;
        margin: 0 !important;
    }
    .msg .msg-top-header {
        margin: 0 1em !important;
    }
    .msg .tags {
        margin: 0 1em !important;
    }

	/* post */

	div#recaptcha_area {
		background-color: #ffffff !important;
	}

    #commentForm {
		font-size: 10pt !important;
    }
    
    .ui-widget-content {
        background-color: #333333 !important;
    }
    
    article h1, p.tags {
        margin: 0 10px !important;
    }

	/* main page */

	div.boxlet, marquee {
		background-color: #282828 !important;
		-moz-border-radius: 5px !important;
		border-radius: 5px !important;
	}
	div.boxlet h2 {
		background-color: #404040 !important;
	}
	div.news div {
		background-color: #000000 !important;
		border: none !important;
	}
}

@-moz-document url-prefix("http://www.linux.org.ru/dw.jsp") {
	body {
		background-color: #282828 !important;
	}
}
