@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("news.goo.ne.jp") {

/*Rebuild*/

body{margin-right:30px !important;}
.container,
#middle{min-width: 300px !important;}
div#inmain,
div#incontents,
div#middle{
	width:45em !important;
	margin:0 auto !important;
	}

div#incontents {
	letter-spacing:0.05em !important;
	line-height:1.7 !important;
	font-size:100% !important;
	}

div.tpl ul li,
div.tpr ul li{margin-bottom:0.5em;}

ul#crumb{margin-bottom:2em !important;}
h1.ch04{
	padding:0.5em !important;
	margin-bottom:2em !important;
	font-size:150%;
	}

/*Hidden*/

ul.sponsorlink,
dl.pub1,
#header center table,
div#rside,
div#lside,
.pr,
.fpr,
#flashad{display:none !important;}

}
