css:
/**********************************/
/* SVB Leverkusen      v1.0 skin  */
/* Date: 25/06/2011               */
/* Made by: Gizzly                */
/**********************************/

@-moz-document domain("hattrick.org") {

#page
{
	margin-top: 10px !important;
}
#top
{
	height: 0px !important;
	position: relative !important;
	z-index: 50 !important;
    background: #000000 !important;
}
#footer a
{
        color: white!important;
        font-weight: bold !important;
}
#ticker
{
	position: absolute !important;
        width: 350px !important;
	top: 30px !important;
	left: 360px !important;
}
#ticker div a
{
	padding-bottom: 2px !important;
	color: white !important;
	text-decoration: none !important;
	line-height: 1.1em !important;
}
#shortcuts
{
	margin-top: 20px !important;
	margin-left:0px !important;
}
#teamLinks
{
        color: white !important;
        margin-top: 15px !important;
	margin-left: 120px !important;
}
div#teamLinks a
{
        color: white !important;
}
#ft_rapidid {  margin-top:15px !important; }
#ft_rapidid a { color: white !important;}
#ft_rapidid_form {margin-top:-5px !important; }
/*--Users online - week - date - time ----------------------------*/
div[id="online"] {color: white !important; margin-top: 15px !important;}
div[id="time"] {color: white !important; margin-top: 15px !important;}

/*--Page background-----------------------------------------------*/
body {background: black !important;}

/*----centre title-------("Offizielle Hattrick-Mitteilungen")--------------*/
h1 
{
        color: #D11E1A !important;
        border-bottom: solid thin #D11E1A!important;
}
/*-----all boxHead--------------------*/
h2 
{
        color: black !important;
        background-color: #D11E1A !important;
        border-radius: 4px 4px 0 0 !important;
}
/*-----centre news--------------------*/
.boxBody h2 
{ 
        color: #D11E1A !important;
        background: none !important;
        border-bottom: solid 1px #D11E1A !important;
}
/*-----------boxHeads----------------------------------------------------------------------------------------*/

.sidebarBox .boxHead .boxLeft, .subMenuBox .boxHead .boxLeft, .main .boxHead .boxLeft
{
        background: none !important;
}
.sidebarBox .boxHead, .subMenuBox .boxHead, .main .boxHead { background: none !important;}
.sidebarBox .boxFooter .boxLeft,.subMenuBox .boxFooter .boxLeft,.main .boxFooter .boxLeft{ background: none !important; }
.sidebarBox .boxBody,.subMenuBox .boxBody,.main .boxBody{ border-left: none !important; border-right: none !important; }

/*Left menu*/
.subMenuBox .boxHead
{
        color: black !important;
	border-radius: 5px 5px 0 0 !important;
}
.subMenuBox .boxHead .boxLeft
{
        color: black !important;
        }
/*Centre box*/
.main .boxHead h2 
{
        color: black !important;
}
/*Right menu*/
.sidebarBox h2 
{
        color: black !important;
}

.boxFooter
{
        background: white !important;
        border-radius: 0 0 4px 4px !important;
}
/*----- Press, GB, Team events ------------------------------------------------------------------------------*/
.main .mainBox h2 
{
	border-bottom: solid 1px #D11E1A !important;
	color: #D11E1A !important;
	font-size: 14px !important;
	font-weight: bold !important;
	padding: 1px !important;
}
/*Central header paths*/
h2 a 
{
        color: black !important;
}
h2 a:hover 
{
        color: white !important;
}
/*News and team events date*/
.date
{
	color: #808080 !important;
	padding-right: 8px;
	font-weight: 400;
}
/*Left Menu titles*/
h3 
{
        color: #D11E1A !important;
        border-bottom: solid thin #D11E1A !important;
}
/*General Links*/
a
{
    color: #D11E1A !important;
	/*text-decoration: none !important;*/
}
/*----subMenu and sidebar links------------------------------------------------------------------------------*/
.subMenuBox a, .subMenuBox a
{
    /*text-decoration: none !important;*/
	color: black !important;
}
.sidebarBox a 
{
        text-decoration: underline !important;
	color: black !important;
}
/*Bot teams link*/
div a[class="shy"] 
{
        color: gray !important;
}
/*Hide Hattrick logo*/
#logo
{
        background: none !important;
}
/*Hide ticker's stars*/
#ticker div
{
        /*background: none !important;*/
}
/* central page background */
#content
{
        background: #ffffff!important; /* Old browsers */
background: -moz-linear-gradient(left, #ffffff 0%, #000000 33%, #000000 67%, #ff3232 100%) !important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, right top, color-stop(0%,#ffffff), color-stop(33%,#000000), color-stop(67%,#000000), color-stop(100%,#ff3232))!important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(left, #ffffff 0%,#000000 33%,#000000 67%,#ff3232 100%)!important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(left, #ffffff 0%,#000000 33%,#000000 67%,#ff3232 100%)!important; /* Opera11.10+ */
background: -ms-linear-gradient(left, #ffffff 0%,#000000 33%,#000000 67%,#ff3232 100%)!important; /* IE10+ */
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#ffffff', endColorstr='#ff3232',GradientType=1 )!important; /* IE6-9 */
background: linear-gradient(left, #ffffff 0%,#000000 33%,#000000 67%,#ff3232 100%)!important; /* W3C */
}
/*Image header (banner)*/
#header 
{
	height: 102px !important;
	padding-top: 20px !important;
        /*background-image: url(http://i586.photobucket.com/albums/ss302/SGDGizzly/HT-Skin/Marci0595/header2.jpg),-moz-linear-*/
	background-image: url(http://i586.photobucket.com/albums/ss302/SGDGizzly/HT-Skin/Marci0595/header4-2.png) !important;
	border-radius: 20px 20px 0 0 !important;
}
/*--------Top menu settings----------------------------------------------------------------------------------*/
#menu
{
	height: 20px !important;
	margin-top: 5px !important;
        background: black!important;
        
}
#menu a
{
	line-height: 20px !important;  
        color: #D11E1A!important;
}
div#menu a:hover 
{
        color: white!important; 
        background: #D11E1A!important;
}
a:hover
{
	color: #FF4500 !important;
}
/*bottom white line*/
div[id="bottom"]
{
        background: #ffffff!important; /* Old browsers */
background: -moz-linear-gradient(left, #ffffff 0%, #000000 33%, #000000 67%, #ff3232 100%) !important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, right top, color-stop(0%,#ffffff), color-stop(33%,#000000), color-stop(67%,#000000), color-stop(100%,#ff3232))!important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(left, #ffffff 0%,#000000 33%,#000000 67%,#ff3232 100%)!important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(left, #ffffff 0%,#000000 33%,#000000 67%,#ff3232 100%)!important; /* Opera11.10+ */
background: -ms-linear-gradient(left, #ffffff 0%,#000000 33%,#000000 67%,#ff3232 100%)!important; /* IE10+ */
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#ffffff', endColorstr='#ff3232',GradientType=1 )!important; /* IE6-9 */
background: linear-gradient(left, #ffffff 0%,#000000 33%,#000000 67%,#ff3232 100%)!important; /* W3C */
border-radius: 0 0 20px 20px !important;
}

/*---Aufstellung---------------------------------------------------------*/
div[class="name"] a       {color: #000000 !important;}
div[class="name"] a:hover {color: #D11E1A !important;}

/*---Forum---------------------------------------------------------------*/
div[class="boxBody"]   {background: white !important;}
div[class="message"] a {color: #D11E1A !important;}
div[class="signature"] a {color: #D11E1A !important;}

.cfHeader
{
	background: none repeat scroll 0 0 #D11E1A !important;
}
#cfPollHeader
{
	background: black !important;
	padding: 5px 10px;
	font-weight: bold;
        color: #ffffff;
}
.cfWrapper
{
	border: solid 1px #c70000 !important;
	margin: 0 0 12px 0;
}
.cfWrapper a
{
        color: black !important;
}
.cfUserInfo a
{
        color: #D11E1A !important;
}
.folderHeader a:link 
{
	color: #D11E1A !important;
	font-weight: bold !important;
}
.cfFooter a
{ 
        color: black !important;
}
.folderItem div.ignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
}
.folderItem div.unignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
}
.folderItem div.unignore:hover
{

}
/*Ads section*/
div[class="insertHeader"]
{

}
div[class="insertFooter"]
{
        background: #ffffff !important;
}
/*-------------Formatting buttons----------------------------------------------------------------------------*/

/*------Foxtrick popup-------*/
ul.ft-popup-list {background-color: black !important; border: solid 1px white !important; }
ul.ft-popup-list > li > a,  ul.ft-popup-list > li > span {	color: white !important; }
ul.ft-popup-list > li > a:hover {background-color: #D11E1A !important; }
ul.ft-popup-list > li:hover {background-color: #D11E1A !important;}

/*------lineup-order-----*/
#order_tabs a {color:black !important;}
#order_tabs a:hover {color:#D11E1A !important;}

/*------MyOffice---------*/
.eventCalendarDate.selectedEventDate {background-color:#D11E1A !important; border-color:#D11E1A !important; color:white !important;}
.eventItem { background-color:#D11E1A !important; border:1px solid black !important;}
/*shortcuts*/
img.scNewMail 
{

}
}
