@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("muropaketti.com"), domain(taskumuro.com) {

/************************************************/
/*  Muropaketti.com - Layout Fix & Ads Removal  */
/* -------------------------------------------  */
/*   created by Lifeless - lifeless@3rdw.net    */
/* -------------------------------------------  */
/*        version: 1.01 - 2011-06-23            */
/* -------------------------------------------  */
/*            No Lilies Were Harmed             */
/*       During the Making of This Style        */
/* -------------------------------------------  */
/*                nos univers                   */
/*              A Contre-Courant                */
/*              tout Ã  l'envers                 */
/*             pas pour longtemps               */
/************************************************/

/************************************************/
/* Version 1.01 : 
       * Bug fixes
       * Login box visible
       * One style for muropaketti & taskumuro
*/
/************************************************/

/* REMOVE ADS */
#sidebar-ads,#subnavimainos,#topadvertisements,#uranus-feed,.mainosbanneri,#edome,.keskimainos { display: none; }

/* PLAZA STUFF */
#megaplazacategoryfooterwrapper, #megaplazafooterwrapper { display: none; }

/* LOGIN STUFF */
#megaplazatopwrapper { position: absolute !important; visibility: collapse !important; } 
#megaplazatopwrapper #logoutForm, #megaplazatopwrapper #loginForm { visibility: visible !important; position: absolute !important; top: 30px !important; margin-right: 50px !important; }

/* UUSIMMAT ARTIKKELIT */
#carousel { width: 1170px !important; }
#carousel { -moz-border-radius: 0px !important; -webkit-border-radius: 0px !important; border-radius: 0px !important; border-right: none !important; }
#carousel h2 { width: auto !important; }
#carousel_holder { height: 60px !important; width: 100% !important; }
#carousel > h2 { font-size: 11pt !important; }
#carousel a { top: 20px; }
#carousel_holder img { height: 40px !important; width: 59px !important; float: right; margin: 0 2px 2px 0 !important; }
#carousel_holder li { margin: 0 68px 0 0px !important; width: 180px !important; }
#carousel_holder h2 { font-size: 9pt !important; width: 200px !important; margin: 0 !important; }

/* UUTISET */
#contentarea { width: 100% !important; }
#contentarea-inside { width: 100% !important; }
#content { width: 930px !important; }
.articleheading { width: 100% !important; }
.alue-etusivu #content img { float: right !important; margin: 10px !important; }
#content p { text-align: left !important; }

/* ARTIKKELIT */
#kommentit {width: auto !important; }

/* SIDE BAR */
#sidebar-a { width: 162px !important; float: right !important; padding-right: 10px !important; }
#sidebar-a .nostoboksi:not(#taskumuro) { display: none; }
#sidebar-a .taskumuro *, #sidebar-a li { width: 95% !important; margin: 0px !important; }
#sidebar-a h2 { font-size: 2pt !important; background-position: 6px 5px !important; }
#sidebar-a h3 { font-size: 9pt !important; }
#sidebar-a .nostoboksi { width: 100% !important; }
.sidebar-b DIV[style="margin:0 0 12px 12px;"] { display: none !important; }
.sidebar-b { width: 14% !important; float: right !important; padding-right: 10px !important; }
.sidebar-b .nostoboksi { width: 162px !important; margin-left: 2px !important; }
.sidebar-b iframe  { width: 150px !important; overflow-x: scroll !important; }


}
