@namespace url(http://www.w3.org/1999/xhtml);@-moz-document url-prefix('http://www.tumblr.com'), url-prefix('https://www.tumblr.com')   {body {background:-moz-linear-gradient(left,#B00000,#802A00,#808000,#008000,#000080,#00002A,#2A002A)!important;}img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{opacity:0.00 !important;}#new_post{background-color:rgba(255,255,255,0.25)!important;}.new_post_label,#tags .header DIV A{color:rgba(0,0,0,1.0)!important;}#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: -moz-linear-gradient(left,#B00000,#802A00,#808000,#008000,#000080,#00002A,#2A002A)!important;background:rgba(0,0,0,0.25)!important;-moz-box-shadow: 1px 2px 3px #000000 !important;border-radius:12px!important;}.selection_nipple.white,.selection_nipple:not([class="selection_nipple white"]){background:none!important;width:0px!important;height:0px!important;margin-top:0px!important;border-bottom:solid 11px rgba(0,0,0,0.25)!important;border-right:11px solid transparent!important;border-left: 11px solid transparent!important;}.selection_nipple:not([class="selection_nipple white"]){border-bottom:solid 11px rgba(0,0,0,0.25)!important;}.mceContentBody{color:rgba(255,255,255,1.0)!important;}
#content_top{width:0px!important;height:0px!important;}#content_bottom{width:0px!important;height:0px!important;}#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {padding-top:25px!important;padding-bottom:25px!important; -moz-border-radius: 20px !important;}LABEL {color:rgba(0,0,0,1.0)!important;}#footer{display: none !important;}.post.link.not_mine:hover,.post.quote.not_mine:hover,.post.audio.not_mine:hover,.post.video.not_mine:hover,.post.regular.not_mine:hover,.post.note.not_mine:hover,.post.photo.not_mine:hover{border-top-right-radius: 24px !important;opacity:1!important;}.permalink{background:none!important;width:16px!important;height:16px!important;background:rgba(170,179,188, .85)!important;border-bottom-left-radius:10px !important;border-top-right-radius:24px !important;margin-top:6px !important;margin-right:6px!important;}.permalink:hover{background:rgba(127,139,152,1) !important;}.selected .following,.selected .followers,.selected .settings,.selected .posts,.selected .messages,.selected .drafts,.selected .queue,.selected .likes{color:rgba(255,255,255,0.50)!important;background:rgba(44,71,98, 1.0)!important;}#new_post .arrow{opacity:0.25!important;margin-left:-1px !important;}#categories .category,#categories .category.active{color:rgba(255,255,255,.85)!important;}#dashboard_directory #tabs, #main_explore #wire{margin-top:-25px !important;}#new_post_label_text,#new_post_label_photo,#new_post_label_quote,#new_post_label_link,#new_post_label_chat,#new_post_label_audio,#new_post_label_video{font: bold .85em verdana !important;text-transform:lowercase!important;}#new_post_label_text{color: rgba(255,0,0,1.0)!important;}#new_post_label_photo{color: rgba(255,84,0,1.0)!important;}#new_post_label_quote{color: rgba(255,255,0,1.0)!important;}#new_post_label_link{color:rgba(0,255,0,1.0)!important;}#new_post_label_chat{color:rgba(0,0,255,1.0)!important;}#new_post_label_audio{color:rgba(0,0,84,1.0)!important;}#new_post_label_video{color:rgba(84,0,84,1.0)!important;}li.selected, li.recessed, a.active, div.post_filter_tabs a {
