css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fanfiction.net") {

a.underline:visited {
color: #ccc !important;
}

#zmenu input.searchfield {
margin-top: 1px;
margin-right: 0 !important;
width: 100px !important;
-moz-transition: all 0.5s ease 0s
}

#zmenu input.searchfield:focus, #zmenu input.searchfield:active {
width: 200px !important;
}

#storytext {
word-wrap: break-word;
}

#review:before {
content: "Write a Review";
display: inline-block;
position: absolute;
left: 14px;
top: -10px;
margin-bottom: -12px;
background: white;
padding: 0 6px;
color: #333;
}

#review {
border-radius: 6px;
border: 2px solid #ccc;
width: 640px;
margin: 15px auto 0;
padding: 20px 0 10px;
position: relative;
}

#review table {
width: 600px !important;
}

.xinput_style, textarea {
background: none !important;
box-shadow: inset 0 0 3px rgba(0, 0, 0, 0.1) !important;
border-radius: 4px;
cursor: default !important;
display: inline-block;
}

div[style="text-align:right;text-vertical-align:middle"] {
text-align: center !important;
}

.xinput_style span[title] {
display: inline-block;
padding: 0 4px;
border-radius: 4px;
background: #eee;
border: 1px solid #ccc;
}

.submit input {
border-radius: 4px;
}

#review_postbutton {
padding: 7px 5px !important;
}

div[style="height:4px"], div[style="height:5px"], div[style="height:15px"] {
height: 10px !important;
}

a#q_submit {
background: none repeat scroll 0 0 #617798;
border: 0 none;
color: #FFF !important;
cursor: pointer;
margin: 0;
padding: 4px 8px;
display: inline-block;
border-radius: 4px
}

}
