css:
@font-face {
  font-family: 'Zeyada';
  font-style: normal;
  font-weight: normal;
  src: local('Zeyada'), url('http://themes.googleusercontent.com/font?kit=ptqipUhdR_LjE2DvMNyREQ') format('woff');
}
.logo {
	font-face: Zeyada !important;
	color: rgba(205,0,205,0.6) !important;
	font: 9em "Zeyada" !important ;
	font-weight: 700;
	text-shadow: 0px 0px 60px rgba(97,3,152,0.9) !important;
	background-attachment: fixed;
	background-clip: border-box;
/*	background-color: transparent; */
	background-color: rgba(0,0,0,0) !important;
	background-image: none !important;
	background-origin: padding-box;
	background-position: 100% 0%;
	background-repeat: no-repeat;
	background-size: auto;
}
.logo img, .logo br, .radio {
	display:none;
}
html, body {
	background: url('http://s3.postimage.org/g9azqalny/twilight1.png') no-repeat fixed #f0e0f0 !important; 
	background-position: bottom right !important;
	color: #2A024A !important;
	background-color: #f0e0f0 !important;
	font-family:Verdana, Helvetica, Arial, sans-serif !important;
}
#delform {
    background: none !important;
    border: none;
    margin-top: -2px  ;
    padding-top: 2px  ;
}
a , a:hover, .reflink a:hover {
	text-decoration: none !important; 
	color:rgb(239,39,150) !important;
}
.adminbar {
	text-align:right;
	clear:both;
	float:right;
	font-size: 13px;
}
#verytopbar, .adminbar, #themedropdown {
    background-color: rgba(0,0,0,0) !important;
}
.admin {
	color: rgb(255,0,0) !important;
}
.replymode {
	background-color:#c77ac7 !important;
	color:#4D008A !important;
	text-align:center;
	padding:2px;
	width:100%;
	border-radius: 5px !important;
}
.catalogmode {
	background-color:#c77ac7 !important;
	color:#4D008A !important;
	text-align:center;
	padding:2px;
	width:100%;
	border-radius: 5px !important;
}
.postarea {
	background-image: none !important;
	background-color: rgba(0,0,0,0) !important;
}
.rules {
	/*font-size:0.7em;*/
	width: 468px;
	font-size: 10px;
	font-family: sans-serif;
}
.rules li {
	margin-left: 1em;
	/*text-indent: 0em;*/
}
.postblock {
	background-color:#c77ac7 !important;
	color:#4D008A !important;
	font-weight:800;
	border-radius: 5px !important;
	text-align:center !important;
}
.userdelete {
	background-color:#c77ac7 !important;
	color:#4D008A !important;
	font-weight:800;
	border-radius: 5px !important;
	text-align:left !important;
}
.footer {
	text-align:center;
	font-size:12px;
	font-family:serif;
}
.passvalid {
	background:#EEAA88;
	text-align:center;
	width:100%;
	color:#ffffff;
}
.dellist {
	font-weight: bold;
	text-align:center;
}
.delbuttons {
	text-align:center;
	padding-bottom:4px;
 
}
.managehead {
	background:#AAAA66;
	color:#400000;
	padding:0px;
}
.postlists {
	background:#FFFFFF;
	width:100%;
	padding:0px;
	color:#800000;
}
.row1 {
	background:#EEEECC;
	color:#800000;
}
.row2 {
	background:#DDDDAA;
	color:#800000;
}
.unkfunc {
	background:rgba(0,0,0,0);
	color:rgba(155,0,70,1) !important;
}
.filesize {
	text-decoration: none !important; 
	background:rgba(0,0,0,0) !important;
	font-size:0.5em !important;
	font-family:serif !important;
	color:#4D008A !important;
	font-weight:800 !important;
}
.filetitle {
	background:rgba(0,0,0,0) !important;
	font-size:0.6em;
	color:#CC1105;
	font-weight:800;
}
.postername {
	color:#4D008A !important;
}
.postertrip {
	color:#228854;
}
.oldpost {
	color:#CC1105;
	font-weight:800;
}
.omittedposts {
	color:#A37B9E !important;
}
.reply, .blockquote {
	padding: 10px !important;
	border: 0px !important;
	border-radius: 10px !important;
	background-color: rgba(230,196,230,0.8) !important;
	color: #000000 !important;
}
.replyhl {
}
.replytitle {
	font-size: 1.2em;
	color:#CC1105;
	font-weight:800;
	background-color: rgba(230,196,230,0.8) !important;
}
.commentpostername {
	color:#117743;
	font-weight:800;
}
.thumbnailmsg {
	font-size: small;
	color:#800000;
}
.abbrev {
	color:#707070;
}
.highlight {
	background: rgba(230,196,230,1) !important;
	border: 2px solid #9900CC !important;
	border-radius: 10px !important;
	color: #4D008A !important;
}
#watchedthreads {
	background-color: rgba(230,196,230,1) !important;
	border: 0px !important;
	border-radius: 10px !important;
}
#watchedthreadlist {
    padding: 0.2em;
	background-image: none;
}
.reflinkpreview { 
	border: 2px solid rgba(153,0,204,.5) !important;
	background-color: #f0e0f0 !important; 
	border-radius: 10px !important;
} 
img.thumb {
	box-shadow: 8px 6px 25px rgba(83,98,145,1) !important;
	background-color: rgba(255,255,255,1) !important;
}
.doubledash {
	vertical-align:top;
	clear:both;
	float:left;
}
#postform { 
	background-attachment: fixed;
	background-clip: border-box;
/*	background-color: transparent; */
	background-color: rgba(0,0,0,0) !important;
	background-image: none !important;
	background-origin: padding-box;
	background-position: 100% 0%;
	background-repeat: no-repeat;
	background-size: auto;
}
#bodywrap1 { 
	background-attachment: fixed;
	background-clip: border-box;
/*	background-color: transparent; */
	background-color: rgba(0,0,0,0) !important;
	background-image: none !important;
	background-origin: padding-box;
	background-position: 100% 0%;
	background-repeat: no-repeat;
	background-size: auto;
}
.topbar { 
	background-attachment: scroll;
	background-clip: border-box;
	background-color: rgb(255, 255, 255);
	background-image: none;
	background-origin: padding-box;
	background-position: 0% 0%;
	background-repeat: repeat;
	background-size: auto;
	border-bottom-color: rgb(92, 91, 127);
	border-bottom-style: solid;
	border-bottom-width: 1px;
	color: rgb(170, 170, 170);
	left: 0px;
	opacity: 0.6;
	padding-bottom: 0px;
	padding-left: 0px;
	padding-right: 2px;
	padding-top: 2px;
	position: fixed;
	top: 0px;
	width: 100%;
 }
.tab {
	background-color:#c77ac7 !important;
	color: rgb(0,0,0) !important;
	text-align:center;
	padding:2px;
	border-top-left-radius: 5px;
	border-top-right-radius: 5px;
}
.current {
	background-color:#c77ac7 !important;
	color: rgb(0,0,0) !important;
	text-align:center;
	padding:2px;
	border-top-left-radius: 5px;
	border-top-right-radius: 5px;
}
.permalink {
	background-color:#c77ac7 !important;
	color: rgb(0,0,0) !important;
	text-align:center;
	padding:2px;
	border-top-left-radius: 5px;
	border-top-right-radius: 5px;
	border-bottom-left-radius: 5px;
	border-bottom-right-radius: 5px;

}
