css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {

#right_column > UL > LI:nth-child(n):not([class^="item"]),
#right_column > UL:nth-child(n) > DIV > DIV:nth-child(n) > A:nth-child(n) {
background-image: none !important;
background:rgba(0,0,0,0.0) !important; border:0px !important;}

.post_filter_tabs .active {
  font: italic 1.2em tahoma !important;
outline: dotted rgba(128,128,128, 0.75) thin !important;
}

#right_column > UL > LI:nth-child(n):not([class^="item"]):hover,
#right_column > UL:nth-child(n) > DIV > DIV:nth-child(n) > A:nth-child(n):hover,
.right_column_section li a:hover,
#right_column > UL:first-child > LI:first-child > A:hover,
#right_column > UL:nth-child(3) > LI:first-child > A:hover,
#right_column  UL:first-child  LI:last-child  A:hover,
#right_column UL:nth-child(2) LI:last-child A:hover {background:rgba(255,255,255, .25) !important;
color: white !important;
}

/****COLORS****/
#tags .header DIV A, #add_photo_link A {
  color: rgba(120,120,120, 1.0) !important;/*DARKCOL*/
}
.explore_tumblr:hover, .add_and_remove:hover, .open_blog.with_subtitle:hover, .customize:hover, .settings:hover, .mass_editor:hover, .posts:hover, .messages:hover, .drafts:hover, .queue:hover , .category.active, #categories .category, .selected .settings, .selected .posts, .selected .messages, .selected .drafts, .selected .queue, .selected .following, .following, .selected .tag, .post_filter_tabs A, 
#auto_pagination_loader, .selected .settings:hover, .selected .posts:hover, .selected .messages:hover, .selected .drafts:hover, .selected .queue:hover, .following:hover, .post_filter_tabs A:hover:not([class="active"]), .big_button:hover, #categories .category.active, #categories .category:hover:not([class="category active"]), #controls DIV {
  color: rgba(255, 255, 255, 1.00) !important;/*WHITE*/
}
 .category.active, #categories .category, .selected .settings, .selected .posts, .selected .messages, .selected .drafts, .selected .queue, .selected .following, .following, .selected .tag, .post_filter_tabs A, 
#auto_pagination_loader {
   -moz-box-shadow: rgba(0,0,0,1) 0 1px 0 !important;/*BLACK*/
   box-shadow: rgba(0,0,0,1) 0 1px 0 !important;/*BLACK*/
   padding: 9px 8px !important;
   text-align: center !important;
}
#upload_image_form DIV DIV {
  height: 1px !important;
  width: 130px !important;
  font: bold small-caps 0.80em tahoma !important;
  margin-top: -1px !important;
  text-align: center !important;/*add a text post - Upload photo - ugly object to manipulate*/
}
.post_filter_tabs A, .post_filter_tabs A[class="active"], .big_button, .big_button:hover {  
  border-right: solid 1px rgba(127,127,127, 0.50) !important;/*MEDMCOL*/
  border-left: solid 1px rgba(127,127,127, 0.50) !important;/*MEDMCOL*/
}
#login_form BUTTON, .category.active, #categories .category, .selected .settings, .selected .posts, .selected .messages, .selected .drafts, .selected .queue, .selected .following, .following, .selected .tag, .post_filter_tabs A, 
#auto_pagination_loader, .big_button, .right_column_section li {
   border-top: 1px solid rgba(192,192,192, 0.50) !important;/*BORDCOL - top border for all button.hover button effects*/
   background: -moz-linear-gradient(bottom, rgba(0,0,0, 0.25), rgba(255,255,255, 0.25)) !important;/*DARKCOL, LITECOL*/
   background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(0,0,0,0.25)), color-stop(50%,rgba(64,64,64,0.25)), color-stop(51%,rgba(128,128,128,0.25)), color-stop(100%,rgba(255,255,255,0.25))) !important;/*DARKCOL, LITECOL*/
   background: -webkit-linear-gradient(top, rgba(0,0,0,0.25) 0%,rgba(64,64,64,0.25) 50%,rgba(128,128,128,0.25) 51%,rgba(255,255,255,0.25) 100%) !important;/*DARKCOL, LITECOL*/
}

#login_form BUTTON:hover, .post_filter_tabs A[class="active"], .post_filter_tabs A:hover, .selected .settings:hover, .selected .posts:hover, .selected .messages:hover, .selected .drafts:hover, .selected .queue:hover, .following:hover, .post_filter_tabs A:hover:not([class="active"]), .big_button:hover , #categories .category:hover:not([class="category active"]){
  background: -moz-linear-gradient(top, rgba(0,0,0, 0.25), rgba(255,255,255, 0.25)) !important;/*DARKCOL, LITECOL*/
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(0,0,0,0.25)), color-stop(50%,rgba(128,128,128,0.25)), color-stop(51%,rgba(32,124,202,0.25)), color-stop(100%,rgba(255,255,255,0.25))) !important;/*DARKCOL, LITECOL*/
  background: -webkit-linear-gradient(top, rgba(0,0,0,0.25) 0%,rgba(64,64,64,0.25) 50%,rgba(128,128,128,0.25) 51%,rgba(255,255,255,0.25) 100%) !important;/*DARKCOL, LITECOL*/
}
#login_form BUTTON:before {
content:" Login" !important;
padding-left: 6px !important;
}
#login_form BUTTON:hover:before {
content:" Login" !important;
padding-left: 5px !important;
margin-top: -20px !important;
color: rgba(22,22,22, 1.0) !important;
}
#login_form BUTTON{
max-width: 15px !important;
}
#login_form BUTTON SPAN {
  display: none !important;
}
#left_column H1, #page_heading, div.l-content p {
  color: rgba(200,200,200, .65) !important;/*LITECOL*/
}
h2, LABEL, .cell.lead_editor {
  color: rgba(200,200,200, 1.0) !important;/*LITECOL*/
}
/****Selected category effects for ADD OR REMOVE****/
.category.active .icon.arrow {
  width: 0 !important; 
  height: 0px !important; 
  border-top: 10px solid transparent !important; 
  border-right: 10px solid rgba(128,128,128, 0.75) !important;/*MEDMCOL*/ 
  border-bottom: 10px solid transparent !important;
  background: -moz-linear-gradient(bottom, rgba(0,0,0, 0.50), rgba(255,255,255, 0.50)) !important;/*DARKCOL, LITECOL*/
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(0,0,0,0.25)), color-stop(50%,rgba(64,64,64,0.25)), color-stop(51%,rgba(128,128,128,0.25)), color-stop(100%,rgba(255,255,255,0.25))) !important;/*DARKCOL, LITECOL*/
  background: -webkit-linear-gradient(top, rgba(0,0,0,0.25) 0%,rgba(64,64,64,0.25) 50%,rgba(128,128,128,0.25) 51%,rgba(255,255,255,0.25) 100%) !important;/*DARKCOL, LITECOL*/
  margin-top: -9px !important;
  margin-left: -5px !important;
}
#categories .category.active {
   margin-left: -15px !important;
   margin-right: 15px !important;
   color: rgba(255, 255, 255, 1.0) !important;/*personal preference - highlights text for category in "Add or Remove"*/
}
#categories A[class="category active"]:first-child {
  border-top-left-radius: 0px !important;
}
#categories A[class="category active"]:last-child {
  border-bottom-left-radius: 0px !important;
}
/****FONT SIZING****/
#categories .category.active, #categories .category, .category.active, #categories .category, .selected .settings, .selected .posts, .selected .messages, .selected .drafts, .selected .queue, .selected .tag, .post_filter_tabs A, #auto_pagination_loader {
   font-size: 1.00em !important;
}
.selected .following, .following {
   font-size: 0.80em !important;
}
.post_filter_tabs A, .post_filter_tabs A[class="active"] {
  font-size: 0.70em !important;
}
#left_column H1, #page_heading {
  font: normal 1.4em verdana !important;
}
/****FONT SHADOW****/
.selected .settings:hover, .selected .posts:hover, .selected .messages:hover, .selected .drafts:hover, .selected .queue:hover, .following:hover, .post_filter_tabs A:hover:not([class="active"]), .big_button:hover, #categories .category:hover:not([class="category active"]), .category.active, #categories .category, .selected .settings, .selected .posts, .selected .messages, .selected .drafts, .selected .queue, .selected .following, .following, .selected .tag, .post_filter_tabs A, #auto_pagination_loader, .big_button {
   text-shadow: rgba(0,0,0,.6) 0 1px 0 !important;
 }
/***SIZING or POSITION***/
#left_column H1, #page_heading {
  margin-bottom: -10px !important;
  /*margin-top: -20px !important;*//*personal preference when Tumblr - Generic Theme Template is applied*/
}
.selected .following, .following, #categories .category {  
   padding: 6px 8px !important;
}
.selected .posts , .selected .tag {
   padding: 8.5px 8px !important;
}
.post_filter_tabs A, .post_filter_tabs A[class="active"] {
  padding: 5px !important;
  width: 59px !important;
}
#right_column UL:nth-child(2) DIV DIV:first-child A:first-child {
  width: 59px !important;
}
.selected .settings:hover, .selected .posts:hover, .selected .messages:hover, .selected .drafts:hover, .selected .queue:hover, .following:hover, .post_filter_tabs A:hover:not([class="active"]), .big_button:hover  {
  margin-left: -1px !important;
  margin-bottom: -1px !important;
}
.post_filter_tabs A:hover:not([class="active"]) {
  margin-left: 0px !important;
}
#categories .category.active, #categories .category {
   padding: 3.125px 8px !important;
   text-align: left !important;
 }
#categories .category:hover:not([class="category active"]) {
  margin-left: -1px !important;
  margin-right: 6px !important;
}
#categories .category.active, #categories .category, #categories .category:hover:not([class="category active"]) {
  margin-bottom: 1px !important;
}
.selected .settings:hover, .selected .posts:hover, .selected .messages:hover, .selected .drafts:hover, .selected .queue:hover, .following:hover, .post_filter_tabs A:hover:not([class="active"]), .big_button:hover, #categories, #categories A:first-child, #categories .category:hover:not([class="category active"]) {
  margin-top: 1px !important;
}
.tabs_3 A {
  padding: 5px 0px !important;
}
.audio_post #left_column H1, .video_post #left_column H1  {
  margin-bottom: 10px !important;
}
#cancel_button, #save_button, #preview_button, #embed_a_video .tabs, #upload_a_video .tabs, #audio_upload .tabs, #search_audio_tab .tabs, #external_mp3_tab .tabs {
  height: 26px !important;
}
#photo_upload {
  margin-top: 25px !important; 
}
 #take_photo_button, #add_photo_button {
  margin-top: -10px !important;
}
/****ROUNDING****/
.post.photo_post .photo, .post.photo_post .video_thumbnail, #people_finder, #left_column DIV:nth-child(11)  {
  border-radius: 16px !important;
}
.text_field, #post_state, #tag_editor, .source_url_questionmark_gradient, .mceLayout, .mceToolbar.mceCenter.mceFirst.mceLast, .mceIframeContainer.mceFirst.mceLast, #left_column DIV:nth-child(11), #post_one, #video_upload, .customize_button, .selected .tag, #post_two, #categories A:last-child {
   border-bottom-left-radius: 6px !important;
   border-bottom-right-radius: 6px !important;
}
.text_field, #post_state, #tag_editor, .source_url_questionmark_gradient, .mceLayout, .mceToolbar.mceCenter.mceFirst.mceLast, .mceIframeContainer.mceFirst.mceLast, #left_column DIV:nth-child(11), #post_one, #video_upload, .customize_button, #categories A:first-child, .selected .following, .following, .selected .posts, #left_column DIV:nth-child(9) {
   border-top-left-radius: 6px !important; 
   border-top-right-radius: 6px !important;
}
/****RADAR PHOTO****/
.post.photo_post .photo, .post.photo_post .video_thumbnail {
  -moz-box-shadow: 1px 1px 1px rgba(0,0,0, .50) !important;
}
.content.is_media, .mask.top, .mask.bottom {
  background: none !important;
}
/*green background for following - add and remove buttons*/
#right_column UL:first-child.right_column_section, .big_button {
  background-image: none !important;
}

#categories .category,  #categories .category:hover:not([class="category active"]), #categories .category.active, div.hide_overflow, LABEL {
  border-bottom: none !important;
  color: rgba(32,32,32, 1.0) !important;/*FONTCOL*/
  text-shadow: rgba(174,174,174, 0.50) 1px 1px 0px !important;}
/*a.explore_more_tags,
a.add_and_remove,*/
a.customize,
a.mass_editor {
  background-color: rgba(0,0,0, 0.0) !important;	
	border:0px !important;
}
.dashboard_options_form {
  background: rgba(127,127,127, .65) !important;
}
/*Adds icons back to following,and selected - posts, messages, drafts, queue*/
 .following .hide_overflow:before {
  margin-left: -20px !important;
  opacity: .50 !important;
  content: url(data:image/png;base64,) !important;
}
.selected .posts .hide_overflow:before {
  margin-left: -44px !important;
  opacity: .50 !important;
  content: url(data:image/png;base64,) !important;
}
.selected .messages .hide_overflow:before {
  margin-left: -15px !important;
  opacity: .50 !important;
  content: url(data:image/png;base64,) !important;
}
.selected .drafts .hide_overflow:before {
  margin-left: -41px !important;
  opacity: .50 !important;
  content: url(data:image/png;base64,) !important;
}
.selected .queue .hide_overflow:before {
  margin-left: -38px !important;
  opacity: .50 !important;
  content: url(data:image/png;base64,) !important;
}
/**SAVE**/}
