@-moz-document url-prefix(chrome://stylish/content/stylish.xul), url-prefix("chrome://stylish/content/manage.xul") {

#styles-children {
    background-color: #01CEF0 !important;
    }

#styles-children::-moz-tree-cell(current) {
    background-color: #FFFFFF !important;
    }

#styles-children::-moz-tree-cell-text(current) {
    color: #000000 !important;
    font-weight: bold !important;
    }

#styles-children::-moz-tree-checkbox { list-style-image: url("data:image/gif;base64,R0lGODlhEAAOALMAAAAAAISChMTCxPz%2B%2FAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAEAAAAALAAAAAAQAA4AAwQXEMhJq704681zCF0wfFwgkCXYrWzrbhEAOw%3D%3D") !important;
   }

#styles-children::-moz-tree-checkbox(checked) {
      list-style-image: url("data:image/x-png;base64,") !important;
   }

}
