css:
/*
 * Description:  調教賈斯汀娘
 *
 */

/** 頻道 **/
@-moz-document domain("justin.tv") {
/* 不想要的通通隱藏 */
.fp_opa_pushdown + * + #right_col.right_col.ad, STRONG#channel_views_count + STRONG, .header_notification.new_alert,#channel_hlctv, #DirectoryLeaderBottomv2, #PopUnderChan .managed_ad,
#ChanMedRectv2, #ChanLeaderv2, #ChanLeaderBottomv2, #adaptv_ad_player_div > *, .hdr_lg.broadcast_btn, .hdr_grp_left, .footer_ad, A[href="http://itunes.apple.com/us/app/justin-tv/id358612216?mt=8"], #socialcam_banner_promo,
H1, #banner_default, #next_live_channel, #channel_header, #channel_tabs_container, #channel_notifications, #survey, #subplayer_buttons, #channel_lists, #footer, NOSCRIPT, HTML > BODY > NOSCRIPT > SPAN, #chat_selector, #ChanUnderChat_holder, #channel_gifts_container, #channel_schedule_container, #top_fans_container, #dvr, #banner_custom, #related, #admin_nxtchan, #action_contents,#clips, #meebo, #ad, #iphone_banner, .panel, .section, #share_box, #ad_holder {
    display: none !important;
}
FORM[action="/search"]  { visibility : hidden !important; }

/* 灰色的極細框線 */
#site_header, #live_site_player_flash, #chat_lines, #stats, #about, #clips, #related {
	border: 1px solid #333 !important;
}
.left_col_bg.col_bg { border-style: solid !important; border-width: 1px 0px 1px 1px !important; border-color: #333 !important; }
#chat_container     { border-style: solid !important; border-width: 1px 1px 1px 0px !important; border-color: #333 !important; }

#chat_lines, .cntr,.simple_chan_sect, #about, #stats, #data_tabs ul.tabs, #related_list, #related .chan_sect_title {
	background: #111 !important;
}
/* 實況主標誌 */
.tag.broadcaster {
    border: 1px solid #FFFFFF !important; 
}

/* 黑底白字 */
TD, .left_col_bg.col_bg, #chat_container, #page_wrapper, #body_wrapper, #jtv_chat {
    background: #000000 !important;
    color: #FFFFFF !important;
}

.line1, .line2, .form_section    { color: #000 !important; }

/* 追隨標誌 */
#actions, #action_links {position:absolute ; top:196px ; left:255px ; width:150px ; border-color:transparent !important;}
#share_tab, #report_tab { display: none !important;}

#favorite_tab { text-shadow: 1px 1px 0.5px #FFF !important;}

/* 透明背景 */
#chat_settings_dropmenu_button, #chat_viewers_dropmenu_button, #chat_menu_buttons, #right_col, .wrapper {background: transparent !important;}

/* 圓角 */
.left_col_bg.col_bg, #left_col { -webkit-border-top-left-radius: 8px !important;
                                 -webkit-border-bottom-left-radius: 8px !important;
                                 -moz-border-radius-topleft: 8px !important;
                                 -moz-border-radius-bottomleft: 8px !important;
                                 border-top-left-radius: 8px !important;
                                 border-bottom-left-radius: 8px !important;
}
.list_item.channel.vert-2_col { -webkit-border-radius: 8px !important;
                                 -webkit-border-radius: 8px !important;
                                 -moz-border-radius: 8px !important;
                                 -moz-border-radius: 8px !important;
                                 border-radius: 8px !important;
                                 border-radius: 8px !important;
}

/*討論區右方下拉清單*/
#chat_viewers_dropmenu_button_toggle, #chat_settings_dropmenu_button_toggle { 
        border-style: solid !important;
        border-width: 1px 1px 1px 0px !important;
        border-color: #000 !important; 
}

#chat_settings_dropmenu {
        position : absolute !important; top:6px !important;
}


/* 半透明效果 */

input[type=text],textarea,#share_api_buttons_dtabs {
	opacity: 0.8 !important;
}

/*主題*/
#info { position : absolute ; top: -60px ; left: -80px ; width:67% ;z-index:2 !important; }
#status { color: transparent ;border-color: transparent !important; }
#channel_status_message { color: #FFF ; font-size:20px !important; }
#channel_status_form + .nick, .info_pic, .link_context.col_bg, .tabs.bottom, #gameselector_subcategory_link, #share_api_buttons_dtabs {display: none !important;}
#data_tabs { width: 100% !important;}

/*討論區高度*/
#chat_lines { height: 325px !important; }
.left_col_bg.col_bg, #chat_container { height: 436.5px !important; }
#speak { height: 100px !important; }
.chat_thumb.chat_color { height: 68px ;width:68px !important; }
#chat_text_input { height: 60px !important; overflow:auto !important; }
#chat_line_list P {font-size:medium !important; }

.left_col_bottom { position:relative ; top: 3em!important;}

#header_inside { width:60% ;z-index:1 !important; }

}

@-moz-document regexp("http://?([a-z_-]{2,5})(?=.justin.tv/directory).*") {
/** 頻道列表展開 **/
.directory,.left_col {width:100% !important; }
UL.channel_list.li_grid.clear.clearfix > LI { margin:3px !important;}
HTML[xmlns\:og="http://opengraphprotocol.org/schema/"] {background:black !important;}
.list_item.channel.vert-2_col {height:120% !important;width:11em !important; border: 1px solid #333 !important;}
#language_filter_dropmenu_toggle {position: absolute; left:43em ; top:0px !important;}
.small { color:#FFF !important; }
A[href="/mai_sdcgf/videos"] { color:#99eeff !important; }

}
