css:
@-moz-document url-prefix("http://taskumuro.com/"), regexp("http://muropaketti\\.com/(?!ext).*") {
/* Muropaketti.com & Taskumuro.com CSS v1.5.0 (8.10.2015) */

/* Common / Content */
#wrapper, #contentarea, #contentarea-inside, #subnavigation, #subnavigation ul { width: 1170px !important; }
#subnavigation ul li ul { width: 200px !important; }
input.hidden { display: inline-block !important; }
li.muro_social { display: inline !important; }
#wrapper { margin-top: 15px !important; }
#content { width: 900px !important; }
.blogpost img, .article img, .blogpost embed, .article embed, .article iframe, .blogpost iframe { margin-left: auto !important; margin-right: auto !important; display: block !important; clear: both !important; }
.blogpost img.oikea, .article img.oikea { float: right !important; margin-left: 10px !important; }
.blogpost img.vasen, .article img.vasen { float: left !important; margin: 8px !important; }
.archive { width: 700px !important; }
.articleheading, #searchresults h1 { width: 897px !important; }
.articleheading h2 { width: 95% !important; }
img { border-style: none !important; }
.kainalo { width: 95% !important; margin: 20px 0 !important; }

/* Rounded image borders */
.blogpost img, .article img, #carousel img, .nostoboksi img { border-radius: 10px; -moz-border-radius: 10px; -webkit-border-radius: 10px; }
.blogpost img, .article img, #carousel img { border: 1px solid #BBB !important; } /* White theme border */
.alue-muropaketti.dark .blogpost img, .alue-muropaketti.dark .article img, .alue-muropaketti.dark #carousel img { border: 1px solid #777 !important; } /* Dark theme border */

/* Table img fix */
.blogpost table img, .article table img { margin: 0px 10px !important; display: inline-block !important; }

/* Carousel */
#carousel { width: 100% !important; border-width: 0 0 1px 0 !important; border-bottom-right-radius: 0px !important; }
#carousel_holder { left: 46px !important; }
#carousel ul li { width: 200px !important; }
#carousel > h2 { width: 1130px !important; }
#carousel ul li h2 { font-size: 14px !important; line-height: 1.3em !important; }

/* Sidebar */

.etusivu-muropaketti div#sidebar-a, .etusivu-muropaketti div.sidebar-b { width: 210px !important; clear: right !important; margin: 5px 10px !important; }
.etusivu-muropaketti div#sidebar-a, .alue-muropaketti div.sidebar-b { float: right !important; }
.alue-muropaketti .nostoboksi, #sidenavi { width: 205px !important; }
.alue-muropaketti .nostoboksi h3 { font-size: 13px !important; word-wrap: break-word; }
.alue-muropaketti .nostoboksi ul li { padding: 6px 4px !important; width: 196px !important; float: left !important; }
.alue-muropaketti .nostoboksi img { width: 178px !important; margin: 0 0 7px 10px !important; float: left !important; }

/* Comments */
#kommentit { width: 810px !important; }
#kommentit #palsta1 { margin-left: 70px !important; }
#kommentit #palsta2 { width: 340px !important; margin: 25px 0 0 15px !important; }
#kommentit #palsta2 input { float: none !important; margin-top: 15px !important; }
#mbbs_container { width: 700px !important; margin: 0px auto !important; }
#kommentit textarea { width: 700px !important; }

/* Ads */
.alue-muropaketti { background: #CAD4DA !important; } /* Force white background */
.alue-muropaketti.dark { background: #161616 !important; } /* Force dark background */
#sidebar-a div:not([ID]),
.sidebar-b .nostoboksi:last-child,
.sidebar-b div[style="margin:0 0 12px 12px;"],
.mainosbanneri,
.keskimainos,
#topadvertisements,
#sidebar-ads,
#subnavimainos,
.footer-om,
div[style="margin-left:12px;margin-bottom:12px"],
iframe[style="border:none; float:left; overflow:hidden; width:406px; height:90px;margin-right:10px"],
#___plusone_0,
.tt-wrapper { display: none !important; }
}
