css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document
domain(facebook.com) {


/*Background*/

body:not(.transparent_widget):not(.connect_widget), .showOnceInteracted {background: #222 !important;
background-attachment:fixed !important;
}


/* StartPage */

.loggedout_menubar_container { background-color: #222}

.fbIndexFeaturedRegistration, #pageFooter, div.mtm.locales,  .menu_login_container td.html7magic, .menu_login_container .login_form_label_field  {display: none !important;} 

.loggedout_menubar .fb_logo { padding-left: 410px}
.menu_login_container { padding: 30px 260px}


/* Site */
/* Bar */

#pageHead {width: 981px !important; top: 0px !important;left: 0 !important; right: 0 !important;margin: -10px auto !important;}
body:not(.UIPage_LoggedOut):not(.fbIndex) #blueBar {width: 100% !important; margin: -10px auto !important; top: 10px !important; height: 30px !important;}

#pageHead {z-index: 99999 !important; }
#pageHead, body:not(.UIPage_LoggedOut):not(.fbIndex) #blueBar {position: fixed !important; }
body:not(.fixedBody) #content {margin-top: 30px !important;}

#pageLogo a { background-color: transparent}

#blueBar, #headNav  {
background-color: #222;
border-color: transparent}

/* Backgrounds(transparent) */

.hasLeftCol #contentCol, .home #leftCol, .hasLeftCol #mainContainer, #contentCol {background: transparent; border-color: transparent}


.uiHeaderNav, .uiSideNav .item, .uiHeaderTopBorder, .uiListLight, .egoUnitBorder, .uiComposerTopBorder { border-color: transparent}

a, h1, h2, h3, h4, h5, h6, .fbx_stream_header div.filterListSelector .uiSelectorButton .uiButtonText, .MessagingMessage, #fbMessagesFlyout .preview, #fbMessagesFlyout .time {color: #fff!important;text-shadow: #000 1px 1px 5px !important}

div#pagelet_composer, .uiHeaderSection, .uiSideHeader, .uiUfi .ufiItem, .fbJewelFlyout, .fbJewelFlyout .jewelFooter a, .og .fbNubButton, .og .fbDock .rNubContainer { background-color: rgba(100,100,100,.7); border-color: transparent}

.og .fbNubFlyout, #navAccount.openToggler ul { background-color: rgba(100,100,100,.7)}
}
