css:
/*
 * Google dark menu in Ubuntu style
 */

@-moz-document  domain(google.com),
                domain(google.co.uk),
                domain(google.de)
{

 /*
 * Google dark menu in Ubuntu style
 */

.qp {
  border-bottom: 0 !important;
}

#mngb {
  position: relative !important;
  /*top: -3px !important;*/
  margin-bottom: 8px !important;
  width: 100% !important;
}

/* panel wrapper, shadow behind panel */
#gb {
  height: 20px !important;
  margin-bottom: 5px !important;
  font-family: Ubuntu, sans-serif !important;
  color: #dfdbd2 !important;
  font-size:13px !important;
  background: rgba(0, 0, 0, 0.5) !important;
  box-shadow: 0 -10px 20px 10px black !important;
  position: relative !important;
  z-index: 100 !important;
}

/* panel, panel gradient */
#gbx3, #gbx4 {
  height: 24px !important;
  background: linear-gradient(top, #5c5b56 0%, #3c3b37 100%) !important;
  background: -moz-linear-gradient(top, #5c5b56 0%, #3c3b37 100%) !important;
  background: -webkit-linear-gradient(top, #5c5b56 0%, #3c3b37 100%) !important;
  background: -o-linear-gradient(top, #5c5b56 0%, #3c3b37 100%) !important;
  border-bottom: 1px solid #32322d !important;
  opacity: 1 !important;
}

#gbz .gbzt, #gbz .gbgt, #gbg .gbgt {
  color: #dfdbd2 !important;
}

/* G icon (left) */
#gbz {
  padding-left: 57px !important;
  background: url("data:image/png;base64,") no-repeat scroll 18px 2px transparent !important;
}

/* vertical separator between G icon and first menu item */
#gbz .gbt:first-child {
  box-shadow: -1px 0 0 0px #3e3e3a !important;
  border-left: 1px solid #5f5e5a !important;
}

/* menu items */
.gbt {
  line-height: 20px !important;
}

/* menu item text */
.gbts {
  font-family: Ubuntu !important;
}

/* don't colorize background of menu items */
.gbzt-hvr, .gbzt:focus, .gbgt-hvr, .gbgt:focus {
  background: transparent !important;
}

/* active Google service (bold) */
.gbz0l .gbts, .gbm0l, .gbm0l:visited {
  color: #dfdbd2 !important;
}

/* menu item text with submenu opened */
.gbto .gbtsa, .gbto .gbts {
  top: 1px !important;
  padding-top: 0px !important;
  color: #dfdbd2 !important;
  background: linear-gradient(top, #3c3c39 0%,#43423f 100%) !important;
  background: -moz-linear-gradient(top, #3c3c39 0%, #43423f 100%) !important;
  background: -webkit-linear-gradient(top, #3c3c39 0%, #43423f 100%) !important;
  background: -o-linear-gradient(top, #3c3c39 0%, #43423f 100%) !important;
  border: 1px #4b4a46 solid !important;
  border-bottom: 0 !important;
  box-shadow: 0 0 0 1px #343331 !important;
  border-top-left-radius: 5px !important;
  border-top-right-radius: 5px !important;
}

/* menu right wrapper: .gbg */
/* menu right: .gbtc */
/* menu item with submenu opened */ 
.gbto {
  box-shadow: 0 0 0 #000 !important;
}

/* submenu wrapper */
.gbm {
  top: 24px  !important;
  box-shadow: 0 2px 10px #000000 !important;
  border: 1px solid #3D3D39 !important;
}

.gbmc {
  background: none repeat scroll 0 0 #42423E !important;
}

.gbmtc {
  line-height: 24px !important;
  padding-top: 2px !important;
  padding-bottom: 2px !important;
}

/* submenu iframe */
.gbs {
  top: 25px !important;
}

/* submenu horizontal seperator */
.gbmh {
  border-bottom: 1px solid #494945 !important;
  border-top: 1px solid #3D3D3A !important;
}

.gbmt {
  color: #dfdbd2  !important;
  line-height: 22px !important;
  padding-left: 20px !important;
}

/* submenu item text link */
.gbmt, .gbml1, .gbmt:visited, .gbml1:visited {
  color: #dfdbd2 !important;
}

/* preferences menu item */
#gbgs5 {
  padding-top: 2px !important;
}

/* submenu items hover (orange) */
.gbmt-hvr {
  background: linear-gradient(top , #FD9B72 0%, #FD9B72 4%, #F98658 5%, #F47E4E 30%, #EA703E 75%, #F36C37 95%, #F27949 96%, #F27949 100%) repeat scroll 0 0 transparent !important;
  background: -moz-linear-gradient(top , #FD9B72 0%, #FD9B72 4%, #F98658 5%, #F47E4E 30%, #EA703E 75%, #F36C37 95%, #F27949 96%, #F27949 100%) repeat scroll 0 0 transparent !important;
  background: -webkit-linear-gradient(top , #FD9B72 0%, #FD9B72 4%, #F98658 5%, #F47E4E 30%, #EA703E 75%, #F36C37 95%, #F27949 96%, #F27949 100%) repeat scroll 0 0 transparent !important;
  background: -o-linear-gradient(top , #FD9B72 0%, #FD9B72 4%, #F98658 5%, #F47E4E 30%, #EA703E 75%, #F36C37 95%, #F27949 96%, #F27949 100%) repeat scroll 0 0 transparent !important;
  box-shadow: 0 0 0 1px #d25c2e !important;
  color: #FFFFFF !important;
}

/* preferences icon: always use the monochrome icon */
#gbi5 {
  background: url("http://ssl.gstatic.com/gb/images/b_8d5afc09.png") repeat scroll 0 0 transparent !important;
}

/* username submenu bold */
.gbpc .gbps {
  color: #dfdbd2 !important;
}

/* fix preferences icon tab height*/
.gbto #gbgs5 {
  padding: 2px 5px 2px !important
}

/* fix profile image position */
#gbi4i, #gbi4id {
  height: 24px !important;
  top: -2px !important;
  width: 24px !important;
}

/* fix message counter position */
#gbi1a {
  top: 0px !important;
}

/* fix share text box position */
.gbgsc {
  top: 0px !important;
  padding-bottom: 1px !important;
}

/* fix message submenu position */
#gbwc {
  margin-top: -7px !important;
}

/* Google+ messages submenu */
body.hCJnsb {
  color: #dfdbd2 !important;
}

.CXXbBe {
  color: #dfdbd2 !important;
}

/* message background */
.K7gtsc {
  background: linear-gradient(top, #FD9B72 0%, #FD9B72 0.5%, #F98658 0.5%, #F47E4E 30%, #EA703E 75%, #F36C37 99.5%, #F27949 99.5%, #F27949 100%) repeat scroll 0 0 transparent !important;
  background: -moz-linear-gradient(top, #FD9B72 0%, #FD9B72 0.5%, #F98658 0.5%, #F47E4E 30%, #EA703E 75%, #F36C37 99.5%, #F27949 99.5%, #F27949 100%) repeat scroll 0 0 transparent !important;
  background: -webkit-linear-gradient(top, #FD9B72 0%, #FD9B72 0.5%, #F98658 0.5%, #F47E4E 30%, #EA703E 75%, #F36C37 99.5%, #F27949 99.5%, #F27949 100%) repeat scroll 0 0 transparent !important;
  background: -o-linear-gradient(top, #FD9B72 0%, #FD9B72 0.5%, #F98658 0.5%, #F47E4E 30%, #EA703E 75%, #F36C37 99.5%, #F27949 99.5%, #F27949 100%) repeat scroll 0 0 transparent !important;
  box-shadow: 0 0 0 1px #d25c2e !important;
  color: #FFFFFF !important;
}

.sZ1YBc {
  border-top: 0 !important;
}

}
