css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("friendfeed.com"){

*,img {
font-family:"Helvetica", "Trebuchet MS","Segoe UI","Arial","Serif" !important;
color:#333 !important;
outline:none !important;
}

body {
background:none !important;
background-color:#fff !important;
}

.bar {
border:none !important;
background:none !important;
background-color:#C8DCF5 !important;
}

a,a:link,a:visited {
text-decoration:none !important;
color:#1e529d !important;
}

a:hover {
color:#1e529d !important;
border-bottom:1px dotted #333 !important;
}

a:active {
text-decoration:none !important;
color:#f4a460 !important;
}

.comment {
background-color:#f5f5f5 !important;
color:#efefef !important;
}

.friend {
background-color:#ffffff !important;
color:#000 !important;
}

.owner {
background-color:#f2f9fd !important;
color:#000 !important;
}

.bottomcomment {
background-color:#ffffff !important;
}

img {
outline:none !important;
border:none !important;
}

#footer a,#footer a:visited,.feed .entry .info a,.feed .entry .info a:visited,.feed .entry .likes a,.feed .entry .likes a:visited,.feed .entry .comment a,.feed .entry .comment a:visited,.feed .entry .expandcomment a,.feed .entry .expandcomment a:visited,.feed .entry.deleted a,.feed .entry.deleted a:visited,a.fl,a.fl:visited,.usersubgrid .lists a,.usersubgrid .lists a:visited {
color:#333 !important;
}

.logo a {
text-decoration: none !important;
border: none !important;
}

.likes {
font-size:12px !important;
background-color:#FFEFE8 !important;
border-bottom:1px solid #eeded7 !important;
}

.cluster {
border-bottom:1px dotted #708090 !important;
padding-bottom:1em !important;
}

.title {
font-size:14px !important;
color:#000 !important;
}

.title a {
color:#1e529d !important;
}

.main ul li a,.section ul li a {
font-size:11px !important;
}

.sharebox,.entry,.pager.top {
border-bottom:1px solid #bbb !important;
margin-bottom:10px !important;
padding-bottom:10px !important;
}

.searchbold {
background-color:#CCFFE6;
}

.box-bottom {
display:none !important;
}

.subscribebar,.subscribebar a:link,.subscribebar a:visited,.control,.control a:link,.control a:visited {
color:#fff !important;
}

.info,#footer,.pager,.bottom,.hidetoggle,.box-body ul li a,.buttons input {
font-size:14px !important;
line-height: 15px !important;
}

.title .name a,.title .name .text {
color:#3B7AC7 !important;
}

.images .container a:hover {
text-decoration: none !important;
border: none !important;
}

.images .container img {
margin: 2px !important;
}

.images .container img:hover {
margin: 1px !important;
border: 1px solid #bbb !important;
}

.more a:hover {
text-decoration: none !important;
border: none !important;
}

.box-bar-text a {
color: #fff !important;
}

.stats {
font-size: 12px !important;
}

}
