css:
/*
Google nav-bar fix
By Thomas Hunter
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("google.com") {

#gbx4,#gbx3 {
    background-color: transparent !important;
    border-bottom-color:#ddd !important;
}
.gbzt-hvr .gbtb2, .gbgt-hvr .gbtb2 {
    border-top-color:#3333EE !important;
}
.gbzt-hvr, .gbzt:focus, .gbgt-hvr, .gbgt:focus {
    background-color: transparent !important;
}
.gbz0l .gbts,.gbts {
    color:#1111CC !important;
    font-weight: normal !important;
}
#gbi5 {
    background-position:-6px -22px !important;
}
#gbg5:hover #gbi5 {
    background-position:0 0 !important;
}
#gbg5:hover {
    background-color:#5474EC !important
}
.gbmt-hvr {
    background-color:#DEE2EF !important;
}
.gbgt-hvr .gbtsa {
    background-color:#EFF3FB !important;
}

}
