css:
@-moz-document url-prefix('http://www.karachan.org/pony/') {

html, body {
  font-family: 'Times New Roman' !important;
  background: url('http://i56.tinypic.com/2wd0nlh.png') no-repeat fixed !important;
  background-position: right center !important;

  color: #654fa2 !important;
  background-color: #fff !important;

  margin-top: 0 !important;
  padding: 0 !important;
}

a {
  text-decoration: none !important;
  color:#4eb6e9 !important;
}

.spoiler a {color: #000 !important;}
.spoiler:hover a  {color: #fff !important;}

.topbar {
  top: 0px !important;
  border-top: 30px solid #000;
  border-bottom: none !important;
  opacity: 1 !important;
}

.radio { display: none; }
img[src="/radio/banners/radio.png"] { display: none; }
.logo { color: #000 !important; margin-top: -40px !important;}
.logo img { visibility: hidden;}


hr {
  border: 1px !important;
  height: 2px !important;
  background-color:#307ec8 !important;
  background: none;
}

#watchedthreads {
  top: 160px !important;
  left: 100px !important;
  background-color: #f0f4fd !important;
  border: 0px !important;
  border-radius: 10px !important;
}
#watchedthreadsdraghandle {
  background-color: #645dbc !important;
  color: #f0f4fd !important;
}

.postarea { margin: 20px 350px 0px 0px !important;}
.postblock { background: #6baff6 !important; color: #000 !important;) }
.postarea input, .postarea textarea { background: rgb(240,243,254) !important; color: #000 !important; }


#delform { margin: 25px 0px 0px 10px !important; }

.reply, blockquote {
  padding: 10px !important;
  border: 2px !important;
  border-radius: 10px !important;
  background-color: rgba(78,182,233,0.6) !important;
  color: #000000 !important;
}
.reply {
  background-color: rgba(185,192,210,0.5) !important;
  border: 2px solid rgb(240,243,254)!important;
}

.postername {
  color:#3B4990 !important;
}

blockquote > a {
  color: #fff !important;;
}

.reflinkpreview {
   border: 0 !important;
   background-color: #fff !important;
   border-radius: 10px !important;
}

.filesize {
  text-decoration: none !important;
  background:inherit !important;
}

img.thumb {
  -webkit-border-radius: 5px;
  -moz-border-radius: 5px;
  border: 4px solid #6baff6;
}


.replymode {
  background-color:  rgba(100,93,188,0.3)!important;
  margin-bottom: 15px;
}

.highlight {
  background: rgba(230,196,230,1) !important;
  border: 2px solid #9900CC !important;
  border-radius: 10px !important;
  color: #3B4990 !important;
}

div.navbar {
  font-size: 13px !important;
}

.unkfunc {
  text-decoration: none !important;
  color: #05417f !important;
}

}
