css:
@-moz-document url-prefix("http://jennie.kottnet.net:") {


html {
	height: 100%;
}

body {
	height: 100%;
	background-color: black !important;
	color: orange !important;
	font-family: Tahoma, sans-serif;
}

a, a:link, a:active, a:visited, a:focus {
	color: orange !important;
	text-decoration: none;
	outline: none;
}

a:hover {
	text-decoration: underline;
}

#main a:visited {
	color: orange !important;
}

#wrapper {
	background-color: black !important;
	min-height: 80%;
	overflow: hidden;
	margin: 0 auto;
	width: 900px;

	border: 1px solid orange !important;
	border-top: none;
	-moz-box-shadow: 0 0 1em #666;
	-webkit-box-shadow: 0 0 1em orange !important;
	box-shadow: 0 0 1em orange !important;
}

#banner_top {
	background-color: black !important;
}

h1 {
	line-height: 37px;
	padding: 0 0.9em 0 0.6em;
	width: 50px;
	text-align: center;
	background-color: black !important;
	font-weight: normal;
	font-style: italic;
	font-size: 100%;
}

#tag {
	height: 4em;
	line-height: 4em;
	text-align: center;
	font-size: 70%;
	color: orange !important;
}

#main {
	padding: 0 20px 20px 20px;
	font-size: 90%;
	width: 680px;
}

#menu {
	border-left: 2px solid orange !important;
	padding-left: 10px;
	position: absolute;
	margin-left: 700px;
	width: 170px;
	font-size: 14px;
}

ul.nav, ul.nav li, ul.nav ul {
	list-style: none;
}

ul.nav li.topitem {
	margin-right: 55px;
}

ul.nav li, ul.nav li.topitem .title {
	display: block;
	margin-bottom: 5px;
}

ul.nav li.subitem {
	font-size: 90%;
}

ul.nav li.topitem:before,
ul.nav li.modtitle:before {
	content: "Â» ";
}

ul.nav li.topitem, ul.nav li.modtitle {
	padding-left: 3px;
}

ul.nav li.parent:before {
	content: "";
}

ul.nav li.parent {
	padding-left: 0;
}

ul.nav li.subitem {
	margin-left: 20px;
}

#infobar {
	text-align: right;
	clear: both;
	position: relative;
	top: -30px;
	margin-left: 485px;
	background-color: black !important;
	color: orange !important;
	padding: 0 6px 3px 0;
}

.errorbar + #infobar {
	top: -59px;
}

#infobar input {
	border: none;
	font-family: Tahoma, sans-serif;
	padding: 1px;
	font-size: 14px;
	margin: 0 4px;
	width: 120px;
}

#infobar input.submit {
	border: 1px solid orange !important;
	font-size: 12px;
	width: 60px;
}

#infobar_ident, .logoutbox, .loginbox {
	padding: 4px;
	font-size: 70%;
}

#infobar_ident {
	margin-right: 4px;
}

.logoutbox, .loginbox {
	padding-top: 7px;
}

.successbar,
.errorbar {
	width: 100%;
	background-color: black !important;
	padding: 5px 10px;
	font-weight: bold;
	color: orange !important;
}

.successbar {
	background-color: black !important;
}

h2 {
	margin-bottom: 0.4em;
}

h3 {
	margin-top: 1.2em;
	margin-bottom: 0.4em;
}

.textsection, .toptable {
	width: 670px;
	overflow: hidden;
}

.section {
	clear: both;
}

.sectionbody {
	margin-left: 1em;
}

.sectionbody input, textarea, select {
	border: 2px solid orange !important;
	font-family: Tahoma, sans-serif;
	padding: 3px;
	font-size: 14px;
}

input:focus, textarea:focus, select:focus {
	-moz-box-shadow: 0 0 0.4em orange !important;
	-webkit-box-shadow: 0 0 0.4em orange !important;
	box-shadow: 0 0 0.4em orange !important;
	outline: none; /* for webkit */
}

textarea {
	height: 90px;
}

div.submitline {
	margin: 1em;
	padding-top: 1em;
	border-top: 1px solid orange !important;
}

.submitline input {
	width: 200px;
	height: 2.2em;
}

input.full, textarea.full,
.full input, .full textarea {
	width: 500px;
}

input.twothird, textarea.twothird,
.twothird input, .twothird textarea {
	width: 450px;
}

input.half, textarea.half,
.half input, .half textarea {
	width: 300px;
}

input.third, textarea.third,
.third input, .third textarea {
	width: 150px;
}

table {
	border: 1px solid orange !important;
	border-spacing: 1px;
}

td, th {
	padding: 5px 10px;
	min-width: 50px;
}

thead td, th {
	background-color: black !important;
}

th {
	text-align: left;
}

tbody td {
	background-color: black !important;
}

tr.evenrow td {
	background-color: black !important;
}

.info {
	font-style: italic;
	font-size: 80%;
}

.subsection {
	clear: both;
	margin: 0;
}

.subsection div {
	float: left;
}

.subsection .inputlabel {
	width: 120px;
	text-align: right;
	padding: 10px 5px 0 0;
}

.subsection input, .subsection select, .subsection textarea {
	margin: 5px 0 5px 0;
	min-width: 100px;
	vertical-align: middle;
}

.subsection div.checkbox {
	padding: 9px 0 0 3px;
}

.subsection div.checkbox input {
	min-width: 0;
}

.section .info {
	margin-bottom: 5px;
	display: block;
}

.subsection .info {
	text-align: right;
}

.half .info {
	width: 435px;
}

.third .info {
	width: 285px;
}

.twothird .info {
	width: 585px;
}

td.mod_descr,
td.mod_name,
td.mod_args input {
	font-size: 80%;
}

td.mod_name,
.checkboxandlabel {
	white-space: nowrap;
}

.lotsofcheckboxes .checkboxandlabel {
	display: block;
	float: left;
	width: 200px;
	margin-top: 0.5em;
}

#breadcrumb {
	width: 670px;
	padding: 0 0 3px 1px;
	margin-bottom: 10px;
	border-bottom: 1px solid orange !important;
}

td {
	word-wrap: break-word;
}

.textsection p {
	margin-bottom: 0.7em;
}

}
