css:
@-moz-document url-prefix('http://www.bing.com/') {
	body, #sw_im, .sw_tc, .sw_aa1, .sw_aa2, .sw_aa3, .sw_aa4, .sw_card, div.ec {
		background: none !important;
	}
	
	.sw_aal, .sw_aar, .sw_aat, .sb_pt {
		border-color: #fff !important;
	}
	
	.sw_logoT {
		display: none;
	}
	
	.sw_t {
		visibility: hidden;
	}
	
	.sw_b input {
		width: 37em !important;
	}
	
	.sb_pt h1 {
		padding: 0 10px 0 20px !important;
	}
	
	.sb_ph {
		margin-left: -.8em !important;
		width: 96%;
	}
	
	#sw_page {
		margin-left: 50px;
	}
	
	li.sa_wr {
		margin-left: -1em !important;
	}
	
	.sw_t, .sw_t *, .sw_t a:visited {
		color: #000;
	}
	
	.sw_logo {
		background: url(data:image/png;base64,) 0px -3px no-repeat !important;
	}
}
