css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://encrypted.google.com/"), url-prefix("https://www.google.com/"), url-prefix("http://www.google.com/"), url-prefix("http://video.google.com/"), url-prefix("http://maps.google.com/"), url-prefix("http://news.google.com/"), url-prefix("https://picasaweb.google.com/") {

#gbx4 {
  background: white !important;
}
#gbx3, #gbx4 {
  border-bottom: 1px solid #DDD !important;
}

#gbz a,
#gbz span,
#gbg span {
  color: blue !important;
}

a.gbzt:hover,
a.gbgt:hover {
  background: rgb(240,240,240) !important;
}

a.gbzt:active,
a.gbgt:active {
  background: rgb(235,235,235) !important;
}

}
