css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("torrentz.eu") {

body {
   background-color: #444444 !important;
}

.top {
   background-color: #000000 !important;
}

div.top {
   background-image: none !important;
}

div.top > h1 > a {
   color: #33b0ff !important;
}

div.top > ul {
   background-image: none !important;
}

div.top > ul > li > a {
   background-color: #000000 !important;
   color: #33b0ff !important;
}

div.top > ul > li > a:hover {
   border-bottom: 10px solid #33b0ff !important;
   
}

form.search {
   background-image: none !important;
   background-color: #000000 !important;
}

/* SEARCH BOX */
form.search #thesearchbox {
   background: #444444 !important;
   color: #33b0ff !important;
}

/* SEARCH BUTTON */
form.search #thesearchbutton {
   color: #33b0ff !important;
   background: #444444 !important;
}

form.search > fieldset {
   background: none #000000 !important;
}

form.search > fieldset > div {
   border-left: 10px solid #000000 !important;
}

ul.autocomplete {
   border: none !important;
}

body > div > h2 {
   background: #000000 !important;
   color: #33b0ff !important;
}

body > div > dl {
   background: #444444 !important;
   border-bottom: 1px solid #000000 !important;
   color: #000000 !important;
}

body > div > dl > dt > a {
   color: #ffffff !important;
}

div.results > h2 {
   background: #000000 !important;
   border-bottom: 5px solid #000000 !important;
   color: #33b0ff !important;
}

div.results > h3 {
   background: #888888 !important;
   border-bottom: 10px solid #888888 !important;
   color: #000000 !important;
}

div.results > h3 > a {
   color: #206a98 !important;
}

div.results > h3 > small {
   color: #5aacff !important
}

div.results > p {
   background: #000000 !important;
   border-top: 1px solid #000000 !important;
}

div.results > p > span > a {
   border: 1px solid #33b0ff !important;
   background: #000000 !important;
   color: #33b0ff !important;
}

div.results > p > span > a:hover {
   background: #444444 !important;
}

div.results > p > span > span {
   border: 1px solid #33b0ff !important;
   background: #444444 !important;
   color: #33b0ff !important;
}

div.results > p > span > strong {
   color: #33b0ff !important;
}

div.results > dl {
   background: #444444 !important;
   border-bottom: 1px solid #000000 !important;
   color: #FFFFFF !important;
}

div.results > dl > dt > a {
   color: #FFFFFF !important;
}

div.results > dl > dt > a:hover {
   color: #33b0ff !important;
}

div.results > dl > dd > span.u {
   color: #90ff59 !important;
}

div.results > dl > dd > span.d {
   color: #33b0ff !important;
}

/* ERROR */
div.error {
   border: 1px solid #33b0ff !important;
   background: #000000 !important;
   color: #33b0ff !important;
}

div > iframe {
   visibility: hidden !important;
}

div.results > div {
   color: #FFFFFF !important;
}

div.results > div > a {
   color: #33b0ff !important;
}

div.results > div > a:hover {
   color: #206a98 !important;
}
div.results > div > b {
   color: #75808A !important
}

div.cloud > a:hover {
   color: #33b0ff !important;
}

div.recent > a:hover {
   color: #33b0ff !important;
}

div.help > a {
   color: #000000 !important;
}

div.help > a > b {
   color: #206a98 !important;
}

div.help > a > b:hover {
   color: #33b0ff !important;
}

form.profile > fieldset > legend {
   background: #000000 !important;
   color: #33b0ff !important;
   border: 1px solid #33b0ff !important;
}

form.profile > fieldset {
   color: #33b0ff !important;
   background: #000000 !important;
   border: 1px solid #33b0ff !important;
}

form.profile > fieldset > label {
   color: #33b0ff !important;
}

form.profile > fieldset > div.s > input {
   background: #444444 !important;
   color: #33b0ff !important;
}

form.profile > fieldset > input.i {
   background: #444444 !important;
   color: #33b0ff !important;
}

div.help li {
   color: #FFFFFF !important;
}

div.help li > a:hover {
   color: #33b0ff !important;
}

div.index-stats > h2 {
   border-bottom: 5px solid #000000 !important;
}

div.index-stats > p {
   background: #000000 !important;
   border-top: 1px solid #000000 !important;
   color: #33b0ff !important;
}

div.index-stats {
   background: none !important;
}

div.index-stats > div {
   color: #33b0ff !important;
}

div.index-stats > dl > dt {
   color: #FFFFFF !important;
}

div.footer > .generic > a:hover {
   color: #33b0ff !important;
}

p.generic a:hover {
   color: #33b0ff !important;
}

}
