css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("community.ratsun.net") {

#ipboard_body { background-color: #000000 !important }

#content { background-color: #202020 !important }

#search_wrap { background-color: #303030 !important }

#search input#main_search { background-color: #303030 !important }

#secondary_nav > div { background-color: #202020 !important }

#secondary_nav a { color: #00CCFF !important }

#breadcrumb { background-color: #202020 !important }

#section_links { background-color: #202020 !important }

.header { background: #303030 !important; color: #0099FF !important}

.row1, .row1 td, .post_block.row1 { background-color: #454545 !important }

.row1 .altrow, .row1.altrow, .personal_info, .general_box { background-color: #454555 !important}

.row2, .row2 td, .post_block.row2, .message { background-color: #505050 !important }

.row2 .altrow, .row2.altrow, ul.topic_moderation { background-color: #505060 !important }

body {color: #000000 !important }

#ipbwrapper { background: #202020 !important }

table.ipb_table tr td { border-bottom: #454545 !important }

.desc, p.posted_info, .warn_panel strong { color: #FFFFFF !important }

a { color: #00CCFF !important }

table.ipb_table { border-bottom: 2px solid #202020 !important }

.general_box {border-color: #202020 !important }

.general_box h3 { background: none repeat scroll 0 0 #303030 !important; color: #00CCFF !important }

.sidebar_toggle { background-color: #202020 !important; padding: 0 0px !important }

#statusHook .status_main_wrap { background-color: #252525 !important; border-bottom: 2px solid #202020 !important }

body#ipboard_body fieldset.submit, body#ipboard_body p.submit { background-color: #505050 !important }

#statusHook .status_mini_wrap h5 { color: #CCCCCC !important }

.photo { border: 1px solid #454545 !important }

.input_submit.alt { background-color: #303030 !important; border-color: #303030 !important; color: #CCCCCC !important }

body .maintitle {
    background: url("https://lh5.googleusercontent.com/-LSpUzIhWnxU/TgvLYDeVhaI/AAAAAAAABo4/bTU8bsMLZBc/s800/Title%252520BG.jpg") repeat-x scroll left 50% #1D3652 !important }

ul.pagination li.active { color: #00CCFF !important }

ul.pagination li.total, ul.pagination li.active { background: none repeat scroll 0 0 transparent !important; border: 0 none !important; color: #00CCFF !important }

ul.pagination li { background: none repeat scroll 0 0 #454545 !important; border: 1px solid #303030 !important; color: #00CCFF !important }

.topic_buttons li a { background: url("https://lh3.googleusercontent.com/-oK9b1gFqsak/Tg1pCCJBN0I/AAAAAAAABqg/P-4puJBKKmI/s800/Button%252520BG%252520right.jpg") no-repeat scroll right top transparent !important }

.topic_buttons li {
    background: url("https://lh6.googleusercontent.com/-jPct2KkZHTo/Tg10l-wZKlI/AAAAAAAABqk/ASprzGc47RA/s800/Button%252520BG%252520left.jpg") no-repeat scroll left top transparent !important}

ul.mini_pagination li a { border: 1px solid #303030 !important }

.topic_prefix { background-color: #252525 !important; color: #FFFFFF !important }
}
