css:
/*corrections made to style http://userstyles.org/styles/43704*/
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix('https://www.tumblr.com/login'),
  url('https://www.tumblr.com/'),
  url('http://www.tumblr.com/dashboard'),
  url('http://www.tumblr.com/new/blog'),
  url('http://www.tumblr.com/inbox'),
  url('http://www.tumblr.com/help'),
  url('https://www.tumblr.com/preferences'),
  url('http://www.tumblr.com/lookup'),
  url('http://www.tumblr.com/explore'),
  url('http://www.tumblr.com/following'),
  url('http://www.tumblr.com/likes'),
  url-prefix('http://www.tumblr.com/blog/'),
  url-prefix('http://www.tumblr.com/dashboard/'),
  url-prefix('http://www.tumblr.com/new/'),
  url-prefix('http://www.tumblr.com/tumblelog/'),
  url-prefix('http://www.tumblr.com/tagged/'),
  url-prefix('http://www.tumblr.com/spotlight/'),
  url-prefix('http://www.tumblr.com/edit/') , url("https://www.tumblr.com/dashboard"), url("https://www.tumblr.com/new/blog"), url("https://www.tumblr.com/inbox"), url("https://www.tumblr.com/help"), url("https://www.tumblr.com/lookup"), url("https://www.tumblr.com/explore"), url("https://www.tumblr.com/following"), url("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/dashboard/"), url-prefix("https://www.tumblr.com/new/"), url-prefix("https://www.tumblr.com/tumblelog/"), url-prefix("https://www.tumblr.com/tagged/"), url-prefix("https://www.tumblr.com/spotlight/"), url-prefix("https://www.tumblr.com/edit/") {

*{
  text-shadow:none !important;
  color:#565656 !important;
}

Body {
  background-color:#f5f5f5 !important;
  color:#565656 !important;
  font-size:12px !important;
  background-image: url('http://static.tumblr.com/wq1d2dz/WMQlg3v5u/bg.png') !important;
  background-repeat:repeat !important;
  background-attachment: fixed !important;
  background-position:top !important
}
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  background-color: #eedeea !important
}
#default_tabs > LI > A > SPAN:first-child,
#user_channels > LI > A > SPAN:first-child {
  color: rgba(213,164,201, .40) !important;
}

#left_column #posts .post .post_avatar,
#left_column #posts .post .sub_avatar .sub_avatar_inner {
  background-color: #eedeea !important;
  border-bottom-color: #eee !important;
}
#left_column #posts .post{
  border-bottom:1px #eee solid !important
}
#nav .nav_item.active,
#nav .nav_item .nav_menu,
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
  background-color:#eedeea !important;
  background-image:none !important;
  border:0px !important
}
#nav .nav_item .nav_menu {
  border-bottom:0px !important
}
#nav .nav_item .nav_menu a {
  color:#888 !important;
  border-top-color:#eee !important;
}
#nav .nav_item .nav_menu a:hover {
  color:#666 !important;
}
#login_form > P > A,
#footer, #footer a, #footer > DIV > STRONG {
  color:rgba(213,164,201, 1.0) !important;
  background-color: rgba(251,251,251, 0.00) !important;
  background-image:none !important;
}
.no_posts_found, h1.dashboard_header, h1.dashboard_header a, h1.dashboard_header span {
  color:#ccc !important
}
#right_column .dashboard_nav_item {
  border-bottom:1px solid #eee !important;
  background-image:none  !important
}
#right_column .dashboard_nav_item .dashboard_nav_title,
#right_column .dashboard_nav_item .dashboard_nav_title a,
#right_column .dashboard_nav_item a,
#right_column .dashboard_nav_item ul.dashboard_subpages,
#right_column .dashboard_nav_item ul.dashboard_subpages li a {
  color:#666 !important;
}
#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {
  color:#888 !important;
}
#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {
  background: none !important;
}
#show_radar a, #hide_radar a,
#hide_recommended_blogs a, #show_recommended_blogs a {
  color:#666 !important;
}
#left_column #posts .notification {
  background-color:#fbfbfb !important;
  color:#666 !important;
  border-bottom-color: #eee !important
}
#left_column #posts .notification .block {
  color:#eedeea !important;
}
#left_column #posts .notification.alt {
  background-color: #f7f7f7 !important;
}
#left_column #posts .notification.last_notification,
#left_column #posts .notification.single_notification {
  border-bottom-color:#ececec !important;
}
#left_column #posts .notification a {
  color:#565656 !important;
  border-bottom:0px !important;
}
#left_column #posts .notification blockquote {
  border-left-color:#eee !important;
}
#left_column #posts .notification .avatar {
  border-color:transparent !important;
  background-color:#fbfbfb !important;
  opacity:0.5 !important;
}
#left_column #posts .notification .avatar:hover {
  opacity:1 !important;
}
.dashboard_switch_blog_menu_header {
  background-color:#fbfbfb !important;
  color:#666 !important;
}
.dashboard_switch_blog_menu_item {
  background-color:#fbfbfb !important;
  color:#666 !important;
  border:0px !important
}
.dashboard_switch_blog_menu_footer {
  background-color:#fbfbfb !important;
  color:#666 !important;
}
.dashboard_switch_blog_menu_wrapper {
  background-color:#fbfbfb !important;
}
#new_post_notice_container {
  color:#fff !important
}
#new_post_notice_container a {
  color:#fff !important
}
#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current {
  background-image:none !important
}
.notification_type_icon,
#right_column > UL:first-child > LI:last-child > A > DIV:last-child {
  display:none !important
}
h1,h2,h3,h4,h5,h6{ color:#555555 !important
}
a, a:visited{ color:#666666 !important
}
a:hover{ color:#888888 !important
}

/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://static.tumblr.com/wq1d2dz/wcqlg3w7a/icones.png') center center no-repeat !important;
}
.new_post_label_icon {
	background-image: none !important;
}
img[src*="https://secure.assets.tumblr.com/images/post_icons_sprite.png"], .new_post_label_icon {
    opacity: 0 !important;
}
.new_post_label {
  font-size:2px !important;
  padding-top:10px !important;
  font-family:century gothic!important; 
  color:transparent!important;
  letter-spacing:1px !important
}

#logo {
  height: 0 !important;
  width: 0 !important;
	/* these numbers match the new image's dimensions */
	padding-left: 210px !important;
	padding-top: 49px !important;
	background: url(http://static.tumblr.com/wq1d2dz/98Xlg3w4f/logo.png) no-repeat !important;
}
.sub_avatar{width:32px !important; height:32px !important; background-color:transparent !important}
.post.photo_post .photo, .post.photo_post .video_thumbnail {
  -webkit-box-shadow: 2px 2px 2px rgba(0,0,0,.5) !important;
  border-radius: 12px !important;
}
.content.is_media, .mask.top, .mask.bottom {
  background: none !important;
}
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  border-bottom: solid 11px rgba(255,255,255, .65) !important;
  border-right: 11px solid transparent !important;
  border-left: 11px solid transparent !important;
}
.selection_nipple:not([class="selection_nipple white"]) {
  border-bottom: solid 11px rgba(238,222,234, 1.0)  !important;
}
.post.note.same_user_as_last.is_mine:hover,.post.regular.is_mine:hover,.post.regular.same_user_as_last.is_mine:hover,.post.link.not_mine:hover,.post.quote.not_mine:hover,.post.audio.not_mine:hover,.post.video.not_mine:hover,.post.regular.not_mine:hover,.post.note.not_mine:hover,.post.photo.not_mine:hover {
  border-top-right-radius: 24px !important; 
  opacity: 1.00 !important;
}
.permalink {
  background: none !important;
  width: 16px !important;
  height: 16px !important;
  background: rgba(170,179,188, .85) !important;
  border-bottom-left-radius: 10px !important;
  border-top-right-radius: 24px !important;
  margin-top: 6px !important;
  margin-right: 6px !important;
}
.permalink:hover {
background: rgba(127,139,152, 1.0 ) !important;
}
#right_column > UL > LI:nth-child(n):not([class^="item"]),
#right_column > UL:nth-child(n) > DIV > DIV:nth-child(n) > A:nth-child(n){
  background-image: none !important;
  background:rgba(0,0,0,0.0) !important; border:0px !important;}
.text_field, #post_state, #tag_editor, .source_url_questionmark_gradient, .mceLayout, .mceToolbar.mceCenter.mceFirst.mceLast, .mceIframeContainer.mceFirst.mceLast, #left_column DIV:nth-child(11), #post_one, #video_upload, .customize_button, .selected .tag, #post_two, #categories A:last-child {
   border-bottom-left-radius: 6px !important;
   border-bottom-right-radius: 6px !important;
}
.text_field, #post_state, #tag_editor, .source_url_questionmark_gradient, .mceLayout, .mceToolbar.mceCenter.mceFirst.mceLast, .mceIframeContainer.mceFirst.mceLast, #left_column DIV:nth-child(11), #post_one, #video_upload, .customize_button, #categories A:first-child, .selected .following, .following, .selected .posts, #left_column DIV:nth-child(9) {
   border-top-left-radius: 6px !important; 
   border-top-right-radius: 6px !important;
}
#login_form BUTTON {
   border-top: 1px solid rgba(192,192,192, 0.50) !important;/*BORDCOL - top border for all button.hover button effects*/
   background: -moz-linear-gradient(bottom, rgba(0,0,0, 0.25), rgba(255,255,255, 0.25)) !important;/*DARKCOL, LITECOL*/
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(0,0,0,0.25)), color-stop(50%,rgba(41,137,216,0.25)), color-stop(51%,rgba(32,124,202,0.25)), color-stop(100%,rgba(255,255,255,0.25))) !important;
background: -webkit-linear-gradient(top, rgba(0,0,0,0.25) 0%,rgba(64,64,64,0.25) 50%,rgba(128,128,128,0.25) 51%,rgba(255,255,255,0.25) 100%) !important;
}
#login_form BUTTON:hover {
  background: -moz-linear-gradient(top, rgba(0,0,0, 0.25), rgba(255,255,255, 0.25)) !important;/*DARKCOL, LITECOL*/
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(0,0,0,0.25)), color-stop(50%,rgba(41,137,216,0.25)), color-stop(51%,rgba(32,124,202,0.25)), color-stop(100%,rgba(255,255,255,0.25))) !important;
background: -webkit-linear-gradient(top, rgba(0,0,0,0.25) 0%,rgba(64,64,64,0.25) 50%,rgba(128,128,128,0.25) 51%,rgba(255,255,255,0.25) 100%) !important;
}
#login_form BUTTON:before {
  content:" Login" !important;
  padding-left: 6px !important;
}
#login_form BUTTON:hover:before {
  content:" Login" !important;
  padding-left: 5px !important;
  margin-top: -20px !important;
  color: rgba(22,22,22, 1.0) !important;
}
#login_form BUTTON {
  max-width: 15px !important;
  border-radius: 10px !important;
}
#login_form BUTTON SPAN {
  display: none !important;
}
.login.sign_up {display: none !important;
}
.nipple {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  border-right: solid 6px rgba(255,255,255, 1.00) !important;
  border-bottom: 6px solid transparent !important;
  border-left: 0px solid transparent !important;
  border-top: 6px solid transparent !important;
  margin-top: 0px !important;
}
.nipple.border {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  border-right: solid 6px rgba(205,123,184, .50) !important;
  border-bottom: 6px solid transparent !important;
  border-left: 1px solid transparent !important;
  border-top: 6px solid transparent !important;
  margin-top: 1px !important;
}
#posts .avatar_frame, .notification.single_notification {
  border: 1px rgba(205,123,184, 1.0) solid !important;
}
li.selected, li.recessed, a.active, div.post_filter_tabs a {
background: none !important}
div#right_column,
div#right_column:after {
  background: none !important;
}
/**SAVE**/}
@-moz-document url-prefix('http://www.tumblr.com/dashboard/iframe'), url-prefix('https://www.tumblr.com/dashboard/iframe')  {body {background:none!important;}}
