css:
@namespace url(http://www.w3.org/1999/xhtml);







@-moz-document domain("facebook.com") { 



 



/*lolo start*/ 







div.home_right_column,div#leftCol,div#contentArea {



        border-radius: 10px 10px 10px 10px;



	background: repeat scroll 0 0 rgba(255, 248, 221, 0.9); 


        zoom:1;



	margin:2px;

color: #875014;


}







div.contentArea>div {
background-color:rgba(255, 248, 221, 0.9);


	padding: 5px;
color: #875014;


}







span.fsm.fwn.fcg {

background-color:rgba(255, 248, 221, 0.9);

	padding: 1px;

color: #875014;

}







.uiUfi .ufiItem {



border-radius: 4px 4px 4px 4px;


background-color:rgba(255, 248, 221, 0.9);
	
color: #875014;


}







div.home_right_column,div#leftCol,div#contentArea {



	margin: 0px;
background-color:rgba(255, 248, 221, 0.9);
color: #875014;

}







.hasLeftCol .hasRightCol #contentArea {
background-color:rgba(255, 248, 221, 0.9);


	padding-right: 5px;

color: #875014;

}







.hasLeftCol .hasRightCol #contentArea {
background-color:rgba(255, 248, 221, 0.9);


	width: 473px;
color: #875014;


}







.hasLeftCol #contentArea,.hasRightCol #contentArea {
background-color:rgba(255, 248, 221, 0.9);


	float: left;



	margin-left: 13px;
color: #875014;


}







.uiStreamSource a {
background-color:rgba(255, 248, 221, 0.9);


	color: #875014;



}







.home #rightCol .home_right_column {
background-color:rgba(255, 248, 221, 0.9);


	margin: -5px;
color: #875014;


}







.uiUfi .ufiItem {



background-color:rgba(255, 248, 221, 0.9);
color: #875014;

zoom: 1;



}







#pagelet_ego_pane {



background:repeat scroll 0 0 rgba(255, 248, 221, 0.9);


color: #875014;

zoom: 1;



}







.fbProfileByline {



	background: repeat scroll 0 0 rgba(255, 248, 221, 0.9);



	border-radius: 10px 10px 10px 10px;



	zoom: 1;



	margin-left: -10px;



	padding: 5px;

color: #875014;


}







.UIActionLinks .comment_link,.UIActionLinks .comment_link input {



	color: #FFEFEB;

background-color:rgba(255, 248, 221, 0.9);

}







.mbm.fbProfilePhotoStrip {

background-color:rgba(255, 248, 221, 0.9);

	margin-left: -15px;

color: #875014;

}







.uiStreamFooter a {



	color: #875014;

background-color:rgba(255, 248, 221, 0.9);

}




color: #875014;


div.pagelet_stream_header {



border-radius: 10px 10px 10px 10px;	
background-color:rgba(255, 248, 221, 0.9);
color: #875014;

}







.uiStream .uiAttachmentDesc,.uiStream .uiStreamPassive {



	color: #875014;

background-color:rgba(255, 248, 221, 0.9);

}







.hasLeftCol #mainContainer,.hasLeftCol #contentCol,#headNav {


background-color:rgba(255, 248, 221, 0.9);

	border: 0 none !important;



	border-right: 0 none;



	border-left: 0 none;

color: #875014;

}







.uiStream .uiStreamMessage {



	color: #B9663F;

background-color:rgba(255, 248, 221, 0.9);

}







a {



	color: #8A3348;

background-color:rgba(255, 248, 221, 0.9);

}







.uiUfiComment .commentActions {



	background: none repeat scroll 0 0 transparent;
color: #875014;


}







.fcg {



	background-color: rgba(255, 248, 221, 0.9);



    color: #875014;



	padding: 5px;



	border-radius: 5px 5px 5px 5px;


	zoom: 1;



}







#ci_module_list {



	background-color: transparent;

background-color:rgba(255, 248, 221, 0.9);
color: #875014;
}







#ci_module_list li.ci_module.expanded {



	background-color: transparent;

background-color:rgba(255, 248, 221, 0.9);
color: #875014;
}







#headNav {



	border-style: solid solid none;
background-color:rgba(255, 248, 221, 0.9);
color: #875014;

}







#pageHead {



	border-bottom: 5px solid transparent;



	border-left: 5px solid transparent;
background-color:rgba(255, 248, 221, 0.9);
color: #875014;

}







.uiUfiAddComment .mentionsAddComment .commentBox {



	background-color: rgba(255, 248, 221, 0.9);

color: #875014;

	zoom: 1;



}







/*lolo end*/



html,body {

background-color: rgba(255, 248, 221, 0.9);


	margin: 0;



	padding: 0;



	min-height: 1000px !important;



	overflow: hidden;

color: #875014;

}







div.standalone_minifeed.column,.holder,#profileActions,.fmp,.fmptd,.clearfix,#content>div:last-child,#search_results,.srch_landing



	{



	background-color: transparent !important;

background-color: rgba(255, 248, 221, 0.9);
color: #875014;

}







html {



	background-color: #FCE0D7 !important;




	background-image:



		url("data:image/png;base64,")



		!important;



	background-repeat: repeat !important;



	z-index: 2 !important;



	overflow: auto;

color: #875014;

}







body {



	background-color: transparent !important;



	background-image:url("http://i51.tinypic.com/20s9004.jpg");



	background-attachment: fixed !important;



	background-repeat: repeat !important;



	background-position: top left !important;



color: #875014;

}






}
