css:
/*
	Hecq Chan
 - css for www.4chan.org, hugely inspired by RiceChan and 4Chan Dark Compact++.	*/
/*
	V1.1	*/

@namespace url(http://www.w3.org/1999/xhtml);

.rules,
.logo+hr,
.logo+hr+div,
.logo+hr+div+hr,
hr+script+center>a,
hr+script+center+hr,
#recaptcha_logo,
#recaptcha_tagline,
table+br+hr+center>a,
table+br+hr+center+hr,
.postarea>form>div[style]>a[target],
.doubledash {display:none !important}

* {
    border: none !important;
    background: none !important;
    color: inherit !important;
}
html {
    color: #555 !important;
    background: #222 !important;
}

form {
    max-width: 1000px !important;
    margin: 0 auto !important;
    width: 85% !important;
}
#navtop {
    position: fixed;
    top:0;left:0;right:0;
    color: #666 !important;
    background: #111 !important;
    border-bottom: 1px solid #282828 !important;
    text-align: center;
    margin: -4px;
    padding: 3px;
}
#navtop a {
    color: #6BDBF7 !important;
}
div.logo {
    left: -735px;
    text-shadow: 0 0px 10px #FAFAD2;
    position: fixed;
    visibility: hidden;
}
div.logo font span {
    color: white;
    font: 35px Lucida Sans !important;
    visibility: visible;
}
hr {
    border-bottom: 1px solid rgba(0,0,0,0.5) !important;
    border-top: 1px solid rgba(255,255,255,0.1) !important;
}
a>img {
    box-shadow: 0 0 6px #000;
    -moz-box-shadow: 0 0 6px #000;
    -webkit-box-shadow: 0 0 6px #000;
}
.logo b {
    color: #fff !important;
}
.postarea {
    padding: 0 !important;
}
.postarea form div {
    border: none !important;
}
.postarea form {
    border-bottom: 1px solid #1C1C1C;
    padding-bottom: 18px !important;
}
.postarea table {
    margin-bottom: -10px;
    margin-top: -3px;
}
.postblock{
    background: #1c1c1c !important;
    border: 0 !important;
    color: #aaa !important;
}
input, input[type=\"submit\"], input[type=\"button\"], select, button, textarea {
    background: #181919 !important;
    border: 1px #333 solid !important;
    color: #aaa !important;
    moz-appearance: none;
}
input[type=\"checkbox\"]:checked {
    background: rgba(255,255,255,.4) !important;
}
input[type=\"checkbox\"] {
    background: rgba(0,0,0,.4) !important;
    border: 1px solid rgba(255,255,255,.3) !important;
    border-radius: 2px !important;
    height: 10px !important;
    moz-appearance: none !important;
    moz-border-radius: 2px !important;
    webkit-appearance: none !important;
    webkit-border-radius: 2px !important;
    width: 10px !important;
}
img#recaptcha_reload {
    background-image: url(data:image/png;base64,) !important;
    border: none !important;
    height: 0px !important;
    margin: 0 !important;
    padding-top: 18px !important;
    width: 25px !important;
}
img#recaptcha_switch_audio {
    background-image: url(data:image/png;base64,) !important;
    border: none !important;
    height: 0px !important;
    margin: 0 !important;
    padding-top: 15px !important;
    width: 25px !important;
}
img#recaptcha_whatsthis {
    background-image: url(data:image/png;base64,) !important;
    border: none !important;
    height: 0px !important;
    margin: 0 !important;
    padding-top: 16px !important;
    width: 25px !important;
}
#recaptcha_logo, #recaptcha_tagline {
    display: none !important;
}
form>table {
    padding: 4px !important;
}
.postername,.commentpostername {
    color: #D6D3D1 !important;
}
.postertrip {
    color: #FFB882 !important;
}
blockquote {
    color: #fff !important;
}
.quotejs {
    color: #999 !important;
}
.quotelink {
    color: #FE0D5D !important;
}
.unkfunc {
    color: #5AC4E4 !important;
}
.reply, .replyhl {
    background: rgba(0,0,0,0.2) !important;
    border: 1px solid #161616 !important;
    margin: 8px !important;
    width: 1000px !important;
    border-radius: 6px;
    -moz-border-radius: 6px;
    -webkit-border-radius: 6px;
    box-shadow: 0 0 6px rgba(0,0,0,0.5);
    -moz-box-shadow: 0 0 6px rgba(0,0,0,0.5);
    -webkit-box-shadow: 0 0 6px rgba(0,0,0,0.5);
}
.replyhl {
    width: 1000px !important;
    border: 1px solid #FE0D5D !important;
    border-width: 4px 0 !important;
}
.commentpostername>span[style] {
    color: #FBC000 !important;
    font-size: 1.1em;
}
a[href="mailto:sage"] {
    text-decoration: none !important;
}
a[href="mailto:sage"]:before {
    color: #f90;
    content: 'Sage ';
    text-shadow: 0 -1px 1px #f00;
}
.filesize span {
    color: #FB989E !important;
    letter-spacing: -5em;
}
span.spoiler {
    color: #363535 !important;
    background: #363535 !important;
}
span.spoiler:hover {
    color: #fff !important;
}
br+br+b[style] {
    color: #f00 !important;
}
#navtopr, #hd, #ft, td small, .doubledash, #footer, .rules, center font h3, center font small, center small font {
    display: none !important;
}
