@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.google"), url-prefix("https://www.google"), url-prefix("https://encrypted.google"), url-prefix("https://www.ggssl"), url-prefix("http://images.google"), url-prefix("http://video.google"), url-prefix("http://news.google"), url-prefix("http://translate.google."), url-prefix("http://blogsearch.google"), url-prefix("http://books.google"), url-prefix("http://scholar.google"), url-prefix("http://groups.google"), url-prefix("https://groups.google"), url-prefix("http://code.google"), url-prefix("http://trends.google"), url-prefix("http://earth.google"), url-prefix("http://chrome.google."), url-prefix("https://chrome.google."), url-prefix("https://chrome.google."), url-prefix("http://docs.google.com/picker"), url-prefix("http://picasa.google"), domain("picasaweb.google.com"), domain(googlelabs.com) {

body {
max-width: 100% !important;
background-color: #2D2D2D !important;
color: #A9A9A9 !important;
}

#hplogo {
height: 0 !important;
width: 0 !important;
/* these numbers match the new image's dimensions */
padding-left: 113px !important;
padding-top: 39px !important;
background: url("http://www.google.com/images/experiments/nav_logo78.png") no-repeat 0 -41px !important;
}

#hplogo > div {
top: -49px !important;
left: -13px !important;
}

.gbm {
background: none repeat scroll 0 0 #2D2D2D !important;
border: 1px solid #4C4C4C !important;
}

.gbto .gbts {
background: none repeat scroll 0 0 #2D2D2D !important;
border-color: #4C4C4C !important;
}

.gbmt, #rhs {
background-color: #2D2D2D !important;
border-color: #4C4C4C !important;
}

.gbmt-hvr, .gbmt:focus {
background-color: #4C4C4C !important;
}

#mngb #gbx4 {
border-bottom: 1px solid #4C4C4C !important;
}

.sfbg {
background-color: #0E0E0E !important;
border-bottom: 1px solid #4C4C4C !important;
}

#logo img {
background: none repeat scroll 0 0 #0E0E0E !important;
}

.lst {
background: none repeat scroll 0 0 #2D2D2D !important;
border-left: 1px solid #4C4C4C !important;
color: #C8C8C8 !important;
}

.lst-td {
background: none repeat scroll 0 0 #2D2D2D !important;
border-top: 1px solid #4C4C4C !important;
border-bottom: 1px solid #4C4C4C !important;
border-left: none !important;
border-right: 1px solid #4C4C4C !important;
}

.lst-b {
background: none repeat scroll 0 0 #2D2D2D !important;
border-right: 1px solid #4C4C4C !important;
}

#rg_h {
background: none repeat scroll 0 0 #2D2D2D !important;
}

.gac_od, .gac_id, .gssb_e {
border: 1px solid #4C4C4C !important;
}

.gac_m, .gssb_a, .gsq_a {
background: none repeat scroll 0 0 #2D2D2D !important;
color: #8A8A8A !important;
}

#gac_scont .gac_b, #gac_scont .gac_b td.gac_c, #gac_scont .gac_b td.gac_d, tr.gssb_i, .gssb_a:hover, .gsq_a:hover {
background: none repeat scroll 0 0 #4C4C4C !important;
}

a {
color: #6699CC !important;
}

a:visited {
color: #336699 !important;
}

a:hover {
color: #99CCFF !important;
}

#leftnav, #tbdi, #hidden_modes, #hmp {
background-color: #2D2D2D !important;
}

.msel, .tbos, .tbots, .tbotu {
color: #BBBB00 !important;
}

.mitem:hover, #showmodes:hover {
background-color: #4C4C4C !important;
}

.lnsep {
border-bottom: 1px solid #4C4C4C !important;
}

.navend .csb {
background-position: 0px -41px !important;
width: 29px !important;
}

#nav .cur {
color: #BBBB00 !important;
}

#nav .cur .csb {
background-position: -28px -41px !important;
}

.fl .csb {
background-position: -49px -41px !important;
}

.navend #pnnext .csb {
background-position: -69px -41px !important;
width: 69px !important;
}

cite {
color: #77BB88 !important;
}

.vspib:hover, .vspib:active {
background-position: -21px -133px !important;
}

.vspib {
background-position: -1px -133px !important;
width: 16px !important;
height: 16px !important;
}

#cnt.vsh #ires div.vsc:hover > .vspi, #ires div.vso > .vspi, #cnt.vsh #rhs div.vsc:hover > .vspi, #rhs div.vso > .vspi {
background: none repeat scroll 0 0 #4C4C4C !important;
border: 1px solid #4C4C4C !important;
}

#vspb {
background-color: #4C4C4C !important;
border: 1px solid #4C4C4C !important;
}

#vspci {
border-bottom: none !important;
border-left: none !important;
}

.rgsh, #rgsh_s {
background: none repeat scroll 0 0 #2D2D2D !important;
}

#tbbc, #tbt5 {
background: #0E0E0E !important;
}

.read .result {
color: #77BB88 !important;
}

.read .chart {
border-left: 1px solid #4C4C4C !important;
border-right: 1px solid #4C4C4C !important;
}

.read .top {
border-top: 1px solid #4C4C4C !important;
}

.read .bottom {
border-bottom: 1px solid #4C4C4C !important;
}

#clirlanglist {
background: none repeat scroll 0 0 #2D2D2D !important;
border-color: #4C4C4C !important;
}

#taw > div[style="border:1px solid #ebeff9;padding:2px 2px 0 8px;margin:0 0 8px"] {
border: 1px solid #4C4C4C !important;
}

#tads {
background: none repeat scroll 0 0 #2D2D2D !important;
border: 1px solid #4C4C4C !important;
}

#rso > li:last-child > div:last-child {
border: none !important;
}

#rso hr {
color: #4C4C4C !important;
}

/* for even more page */
h1#logo {
background: url("http://www.google.co.uk/images/experiments/nav_logo78.png") no-repeat scroll 0 -41px transparent !important;
width: 113px !important;
height: 39px !important;
margin: 10px 20px 10px 10px !important;
}

h1#logo img {
display:none !important;
}

body > h2, #about {
background: none repeat scroll 0 0 #0E0E0E !important;
border: 1px solid #4C4C4C !important;
}

}
