css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("php.net") {

 body
 {
    font-size: 100% !important;
 }
 #content, #headmenu, #footnav, #copyright, #thismirror, #headsearch span, #headsearch input, #headsearch select, #footnav a, #headmenu a
 {
    font-size: 75% !important;
 }
 #confTeaser ul li
  {
    background: none !important;
  }
 #conferencesSidebar,
 #releaseBox,
 #candidateBox
  {
    border: none !important;
  }

 img[src="/gifs/php-logo.gif"]
  {
    display: none !important;
  }

 img[src="/gifs/logo-bug.gif"],
 #headnav > a:first-child > img,
 .vc_navheader td > a > img
  {
    height: 0 !important;
    width: 0 !important;

    padding-left: 125px !important;
    padding-top: 25px !important;

    background: 
      url(data:image/png;base64,)
      no-repeat
      !important;
  }

 #changeLangImage
  {
    height: 0 !important;
    width: 0 !important;

    padding-left: 11px !important;
    padding-top: 11px !important;

    background:
      url("http://us.php.net/images/small_submit_white.gif")
      no-repeat
      !important;
  }

 a:not(:link)
  {
    color: #ccc !important;
  }

 a:link
  {
    color: #E8E1BD !important;
  }
 a:visited
  {
    color: #C8C19D !important;
  }
 a:link:hover,
 a:visited:hover
  {
    background-color: transparent !important;
    color: #fff !important;
  }

 body,
 p,
 .dokuwiki .li
  {
    color: #ccc !important;
    border-color: #000 !important;
  }
 .dokuwiki .edit
  {
    border-color: #000 !important;
  }

 .vc_navheader
  {
    border: 1px solid #000 !important;
  }
 .vc_navheader .quicksearch
  {
    border-top: 1px solid #000 !important;
  }
 .vc_navheader td:not([rowspan])
  {
    padding: 3px !important;
  }
 .vc_navheader td[rowspan]
  {
    vertical-align: middle !important;
  }

 h1, h2, h3, h4,
 .foot-copy,
 .foot-source,
 .form-holder,
 .head-logo,
 .head-menu,
 .headrow
  {
    background: none !important;
    color: #fff !important;
    border-color: #000 !important;
  }

 html, body,
 #content,
 pre
  {
    background: #222 !important;
  }

 #content,
 .textcell,
 .ulcell
  {
    border-bottom: 1px solid #000 !important;
    border-left: 1px solid #000 !important;
    border-right: 1px solid #000 !important;
    color: #bbb !important;
  }

 .menu
  {
    border-right: 1px solid #000 !important;
  }

 #headmenu,
 #footnav
  {
    color: #000 !important;
    padding-left: 5px !important;

    position: relative !important;
    bottom: 0 !important;
    left: 0 !important;
    right: 0 !important;
    top: 0 !important;
    text-align: left !important;
  }
 tr[bgcolor="#cccccc"],
 .headertop,
 #header,
 #headnav,
 #pagefooter,
 .foot,
 .head,
 .vc_navheader td
  {
    background: #444455 !important;
    border-color: #000 !important;
    color: #bbb !important;
    height: auto !important;
    min-height: 1em !important;
  }
 #headsearch,
 #langs,
 #footnav,
 .foot-bar,
 .headerbottom,
 .head-search,
 .vc_navheader .quicksearch
  {
    background: #333344 !important;
    border-color: #000 !important;
    color: #bbb !important;
  }

 blockquote,
 #bugheader,
 .manualnavbar,
 .php,
 .phpcode
  {
    background: #333 !important;
    border: 1px solid #000 !important;
    color: #bbb !important;
  }

 #bugheader
  {
    margin-bottom: 20px !important;
  }

 #bugheader th,
 .sect1 > a,
 .sect2 > a
  {
    color: #aaa !important;
  }
 #bugheader td
  {
    background: #111 !important;
    color: #999 !important;
    padding: 3px !important;
  }

 div.refentry a,
 div.reference a,
 div.section a
  {
    border-bottom: 1px dotted #65CD99 !important;
    line-height: 1.75em !important;
    padding-bottom: .15em !important;
  }

 h4.eventmonth, 
 ol#releases,
 ol#candidates,
 span.control,
 ul.toc li.header,
 #bugheader th#number
  {
    border: 1px solid #000 !important;
  }
 #releaseBox h4,
 #candidateBox h4
  {
    border: 1px solid #000 !important;
    border-bottom: 0 !important;
  }

 [bgcolor="#e0e0e0"],
 [bgcolor="#eeeeee"],
 [style*="background-color: rgb(255, 255, 255);"],
 div[id^="layout_"],
 .dokuwiki .page,
 td.v,
 #conferencesSidebar,
 #leftbar,
 .dokuwiki .button,
 .even,
 .form-input,
 .man-nav,
 .menu,
 .moreInfo,
 .sidebar_left,
 .sidebar_right
  {
    background: #333 !important;
    border-color: #000 !important;
    color: #bbb !important;
  }

 [bgcolor="#e8e8e8"],
 .dokuwiki .button:hover,
 .moreInfoHeader,
 .note_text,
 .note,
 .odd
  {
    background: #555 !important;
    border-color: #000 !important;
    color: #ccc !important;
  }

 /* grey poupon */
 [style*="background-color: rgb(232, 232, 232);"],
 td[style*="background-color: rgb(204, 204, 204);"],
 th[bgcolor="#ffffff"],
 ol#releases,
 ol#candidates,
 div.example,
 div.informalexample,
 div.classsynopsis div.ooclass,
 span.control.active,
 .doctable th,
 td.search-prev_next,
 th.ch,
 th.flag,
 th.pkgListHeader,
 tr.h > th,
 ul.toc li.header,
 .headervalue,
 #nav a
  {
    background: #69675C !important;
    border-color: #000 !important;
    color: #bbb !important;
  }

 /* lighter grey poupon */
 [bgcolor="#cccccc"],
 th[style*="background-color: rgb(204, 204, 204);"],
 tr[style*="background-color: rgb(204, 204, 204);"],
 h4.eventmonth, 
 #releaseBox h4,
 #candidateBox h4,
 div.example div.example-contents p,
 div.informalexample p,
 #nav a.active,
 li.current > a,
 td.e,
 td.tableHeader,
 td.tableFooter,
 th.results,
 tr.Fbk,
 .alisthead,
 .dokuwiki .bar,
 .dokuwiki .tocheader,
 .headerlabel
  {
    background: #918E80 !important;
    border-color: #000 !important;
    color: #000 !important;
  }

 /* green bg */
 div.refsect1,
 div.refsect1 dl,
 span.control,
 table.standard th,
 table.standard td.sub,
 table.standard th.sub,
 table.standard th.subr,
 table.standard th.subl,
 tr.Asn,
 .code_border,
 .textcell,
 .varlistentry
  {
    background-color: #3D5045 !important;
    border-color: #000 !important;
  }

 /* cerulean bg */
 #menu > li > a:hover,
 #nav a:hover,
 #bugheader th#number,
 div.refsect1 div.methodsynopsis,
 div.refsect1 div.constructorsynopsis,
 div.refsect1 div.destructorsynopsis,
 div.refsect1 dd,
 table.standard td,
 .doctable td,
 tr.Ver,
 .dokuwiki .breadcrumbs,
 .dokuwiki .toc,
 .dokuwiki #toc__inside,
 .mirrors td,
 .ulcell
  {
    background-color: #334244 !important;
    border-color: #000 !important;
  }

 /* red bg */
 tr.Opn
  {
    background: #913233 !important;
    color: #ccc !important;
  }

 .dokuwiki .breadcrumbs,
 div.example div.example-contents p,
 div.informalexample p
  {
    border-bottom: 1px solid #000 !important;
    border-top: 1px solid #bbb !important;
    padding-left: 5px !important;
  }

 .example table
  {
    background: transparent !important;
    width: 100% !important;
  }

 table .phpcode,
 .code,
 .example .example-contents,
 .mediaobject,
 .classsynopsis,
 .informalexample pre,
 .informalexample .phpcode
  {
    background-color: #222 !important;
    border: 2px dashed #000 !important;
    color: #bbb !important;
    margin: 5px !important;
    padding: 5px !important;
  }

 #confTeaser,
 .caution,
 .explain,
 .tip,
 .warning,
 .warnings,
 form#asearch table#primary
  {
    background: #000 !important;
    border: 2px dotted #ccc !important;
    color: #b97 !important;
  }

 div#usernotes
  {
    background: #333 !important;
    border: 1px solid #000 !important;
    color: #bbb !important;
  }

 div#usernotes div.head,
 div#usernotes div.foot,
 #footer
  {
    background: #444 !important;
    border-bottom: 1px solid #000 !important;
    border-top: 1px solid #000 !important;
    color: #bbb !important;
  }

 div#usernotes div.text
  {
    background: #444 !important;
    border: 1px solid #000 !important;
    color: #bbb !important;
  }

/* PHP Code Syntax Highlighting */
 .phpcode span.html
  {
    color: #bbb !important;
  }
 .phpcode span.string,
 .phpcode span[style="color: rgb(221, 0, 0);"],
 .php span[style="color: rgb(0, 127, 0);"],
 .src-line .encapsed_string
  {
    color: #8F8F8F !important;
  }
 .phpcode span.keyword,
 .phpcode span[style="color: rgb(0, 119, 0);"],
 .php span[style="color: rgb(127, 0, 127);"],
 .src-line font[color="#0099ff"]
  {
    color: #65CD99 !important;
  }
 .phpcode span.default,
 .phpcode span[style="color: rgb(0, 0, 187);"],
 .php span[style="color: rgb(0, 0, 187);"],
 .src-line strong,
 .src-line font[color="blue"]
  {
    color: #D5AC8E !important;
  }
 .phpcode span.comment,
 .phpcode span[style="color: rgb(255, 128, 0);"],
 .php span[style="color: rgb(102, 102, 102);"],
 .src-line font[color="#009999"],
 .src-line font[color="green"]
  {
    color: #87A7A3 !important;
  }


/* PHP.net WIKI */
 .dokuwiki .page
  {
    margin: 0 !important;
    padding: .5em !important;
  }

}
