css:
@-moz-document domain('plus.google.com') {

@font-face {
  font-family: 'PT Sans';
  font-style: italic;
  font-weight: bold;
  src: local('PT Sans Bold Italic'), local('PTSans-BoldItalic'), url('http://themes.googleusercontent.com/font?kit=lILlYDvubYemzYzN7GbLkKRDOzjiPcYnFooOUGCOsRk') format('woff');
}
@font-face {
  font-family: 'PT Sans';
  font-style: italic;
  font-weight: normal;
  src: local('PT Sans Italic'), local('PTSans-Italic'), url('http://themes.googleusercontent.com/font?kit=PIPMHY90P7jtyjpXuZ2cLLO3LdcAZYWl9Si6vvxL-qU') format('woff');
}
@font-face {
  font-family: 'PT Sans';
  font-style: normal;
  font-weight: normal;
  src: local('PT Sans'), local('PTSans-Regular'), url('http://themes.googleusercontent.com/font?kit=7YGmE4Ls5b94ct65u07hVQLUuEpTyoUstqEm5AMlJo4') format('woff');
}
@font-face {
  font-family: 'PT Sans';
  font-style: normal;
  font-weight: bold;
  src: local('PT Sans Bold'), local('PTSans-Bold'), url('http://themes.googleusercontent.com/font?kit=0XxGQsSc1g4rdRdjJKZrNL3hpw3pgy2gAi-Ip7WPMi0') format('woff');
}

body {
    font-family: 'PT Sans', 'museo sans', calibri, 'helvetica neue', helvetica, arial, sans-serif !important;
}

#content .a-A {
width: 100% !important;
}

#content .a-p-M-T-gi-xc  {
width: 72%  !important;
}

#content .a-f-n-A  {
width: auto !important;
}

#content .a-f-p  {
width: auto !important;
}

#contentPane.a-p-M-T-gi-xc [id^=update-],
.a-f-i.a-f-oi-Ai {
float: left !important;
clear: none !important;
border-top: 1px solid #999 !important;
width: 46% !important;
max-width: 100% !important;
}
#contentPane.a-p-M-T-gi-xc [id^=update-]:nth-child(n+1) {
margin-right:2px !important;
}

#contentPane.a-p-M-T-gi-xc [id^=update-]:nth-child(2n+1) {
clear:left !important;
}

#contentPane.a-p-M-T-gi-xc [id^=update-] img,
#contentPane.a-p-M-T-gi-xc [id^=update-] iframe,
#contentPane.a-p-M-T-gi-xc [id^=update-] object,
#contentPane.a-p-M-T-gi-xc [id^=update-] embed,
#contentPane.a-p-M-T-gi-xc [id^=update-] audio,
#contentPane.a-p-M-T-gi-xc [id^=update-] video {
max-width: 100%;
}


#contentPane.a-p-M-T-gi-xc .a-f-zd-gb-A {
clear: both;
}


body {
counter-reset: update;
}


#contentPane.a-p-M-T-gi-xc [id^=update-] {
counter-increment: update;
}
#contentPane.a-p-M-T-gi-xc [id^=update-]:before {
content: counter(update);
position: absolute; top: 13px; left: 2px;
font-family: 'american typewriter',georgia !important;
font-size: 1em !important;
color: #ccc !important;
color: rgba(0,0,0,0.4) !important;
pointer-events: none;
-webkit-transition: all 1s;
-moz-transition: all 1s;
transition: all 1s;

-webkit-box-shadow: 0px 0px 10px rgba(0,0,0,0);
-moz-box-shadow: 0px 0px 10px rgba(0,0,0,0);
-o-box-shadow: 0px 0px 10px rgba(0,0,0,0);
box-shadow: 0px 0px 10px rgba(0,0,0,0);





}

#contentPane.a-p-M-T-gi-xc [id^=update-]:hover {
-webkit-box-shadow: 0px 0px 10px rgba(0,0,0,0.2);
-moz-box-shadow: 0px 0px 10px rgba(0,0,0,0.2);
-o-box-shadow: 0px 0px 10px rgba(0,0,0,0.2);
box-shadow: 0px 0px 10px rgba(0,0,0,0.2);
}
#contentPane.a-p-M-T-gi-xc [id^=update-]:hover:before {
color: #3C6DCE !important;
}


@media (min-width: 1451px) {
#contentPane.a-p-M-T-gi-xc [id^=update-],
.a-f-i.a-f-oi-Ai {
width:32% !important;
}

#contentPane.a-p-M-T-gi-xc [id^=update-]:nth-child(2n+1) { 
clear:none !important;
}

#contentPane.a-p-M-T-gi-xc [id^=update-]:nth-child(3n+1) {
clear:left !important;
}

}



@media (min-width: 1300px) and (max-width:1450px) {
#contentPane.a-p-M-T-gi-xc [id^=update-],
.a-f-i.a-f-oi-Ai {
width:47% !important;
}

#contentPane.a-p-M-T-gi-xc .a-p-M-T-gi-xc  {
width: 62% !important;
}

}

@media (min-width: 701px) and (max-width:1299px) {
#contentPane.a-p-M-T-gi-xc [id^=update-],
.a-f-i.a-f-oi-Ai {
width:47% !important;
}

#contentPane.a-p-M-T-gi-xc .a-p-M-T-gi-xc  {
width: 60% !important;
}
}


@media (max-width:990px) {
#contentPane.a-p-M-T-gi-xc [id^=update-],
.a-f-i.a-f-oi-Ai {
width:auto !important;
max-width: 100% !important;
}

#contentPane.a-p-M-T-gi-xc .a-p-M-T-gi-xc  {
width: 55% !important;
}

}


#contentPane [data-content-url] {
height: auto !important;
max-width: 100% !important;
}


.a-p-M-T-hk-xc {
margin: 0 auto;
}


#contentPane .vcard [id^=update-] {

width: auto !important;
float:none;

}

}
