css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document url-prefix('http://www.tumblr.com/dashboard'), url-prefix('http://www.tumblr.com/tumblelog'), url-prefix('http://www.tumblr.com/tagged'),url-prefix('http://www.tumblr.com/inbox'), url-prefix('http://www.tumblr.com/new'), url-prefix('http://www.tumblr.com/likes'), url-prefix('http://www.tumblr.com/preferences'), url-prefix('http://www.tumblr.com/help') , url-prefix("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/tumblelog"), url-prefix("https://www.tumblr.com/tagged"), url-prefix("https://www.tumblr.com/inbox"), url-prefix("https://www.tumblr.com/new"), url-prefix("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/preferences"), url-prefix("https://www.tumblr.com/help") {
*{text-shadow:none !important; color:#565656 !important;}


Body{
background-color:#bf80ec !important;
color:#565656 !important;
font-family:Helvetica !important;
font-size:11px !important;
background-image:url('http://img190.imageshack.us/img190/9327/sinttulo1nux.jpg') !important;
background-repeat:no-repeat !important;
background-attachment: fixed;
background-color:#bf80ec !important;
background-position:top left !important
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background-color: transparent !important;
font-family:Georgia!important;
font-size:11px !important;
}

#left_column #posts .post .permalink {
background-color:#f5f5f5 !important;
}

#left_column #posts .post .post_avatar,
#left_column #posts .post .sub_avatar .sub_avatar_inner {
background-color: #fbfbfb !important;
border-bottom-color: #eee !important;
}

#left_column #posts .post{
border-bottom: 1.5px #eee dotted !important;
}

#nav .nav_item.active,
#nav .nav_item .nav_menu,
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background-color:#fbfbfb !important;
background-image:none !important;
border:0px !important;
font-family:Georgia !important;
font-size:16px !important;
}

#nav .nav_item .nav_menu {
border-bottom:0px !important;
font-family:Courier New !important;
font-size:11px !important;
}

#nav .nav_item .nav_menu a {
color:#888 !important;
border-top-color:#eee !important;
font-family:Georgia !important;
font-size:11px !important;
}

#nav .nav_item .nav_menu a:hover {
color:#666 !important;
font-size:11px !important;
}

#footer, #footer a {
color:#666 !important;
background-color:#fbfbfb !important;
background-image:none !important;
font-family:Georgia  !important;
font-size:11px !important;
}

.no_posts_found, h1.dashboard_header, h1.dashboard_header a, h1.dashboard_header span {
color:#ccc !important;
}

#right_column .dashboard_nav_item {
border-bottom:1.5px dotted #eee !important;
background-image:none  !important;
font-size:11px !important;
}

#right_column .dashboard_nav_item .dashboard_nav_title,
                    #right_column .dashboard_nav_item .dashboard_nav_title a,
                    #right_column .dashboard_nav_item a,
                    #right_column .dashboard_nav_item ul.dashboard_subpages,
                    #right_column .dashboard_nav_item ul.dashboard_subpages li a {
                        color:#666 !important;
font-size:11px !important;
}

#right_column .controls_section li .following{background-color:#944bc8!important;}
#right_column .controls_section li .add_and_remove{
background-color:#bf80ec !important;
font-family:Courier New !important;
font-size:11px !important;
}

#right_column .controls_section li .explore_more_tags{
background-color:#944bc8 !important;
font-family:Courier New !important;
font-size:11px !important;
}

#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {
color:#888 !important ;
font-family:Georgia  !important;
font-size:11px !important;
}

#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {
background: none !important;
font-family:Georgia  !important;
font-size:11px !important;
}
#show_radar a, #hide_radar a,
   #hide_recommended_blogs a, #show_recommended_blogs a {
color:#666 !important;
font-family:Georgia  !important;
font-size:11px !important;
}

#left_column #posts .notification {
background-color:#fbfbfb !important;
color:#666 !important;
border-bottom-color: #eee !important;
font-family:Georgia  !important;
font-size:11px !important;
}

#left_column #posts .notification .block {
color:#333 !important;
font-family:Georgia  !important;
font-size:11px !important;
}

#left_column #posts .notification.alt {
background-color: #f7f7f7 !important;
font-family:Georgia  !important;
font-size:11px !important;
}

#left_column #posts .notification.last_notification,
    #left_column #posts .notification.single_notification {
border-bottom-color:#ececec !important;
}

#left_column #posts .notification a {
color:#565656 !important;
border-bottom:0px !important;
font-family:Georgia  !important;
font-size:11px !important;
}

#left_column #posts .notification blockquote {
border-left-color:#eee !important;
font-family:Georgia !important;
font-size:11px !important;
}

#left_column #posts .notification .avatar {
border-color:transparent !important;
background-color:#fbfbfb !important;
opacity:0.5 !important;
}

#left_column #posts .notification .avatar:hover{
opacity:1 !important;
font-family:Georgia  !important;
font-size:11px !important;
}

.dashboard_switch_blog_menu_header{
background-color:#fbfbfb !important;
color:#666 !important;
font-family:Georgia  !important;
font-size:11px !important;
}

.dashboard_switch_blog_menu_item{
background-color:#fbfbfb !important;
color:#666 !important;
border:0px !important;
font-family:Georgia  !important;
font-size:11px !important;
}

.dashboard_switch_blog_menu_footer{
background-color:#fbfbfb !important;
color:#666 !important;
font-family:Georgia  !important;
font-size:11px !important;
}

.dashboard_switch_blog_menu_wrapper{
background-color:#fbfbfb !important;
font-family:Courier New !important;
font-size:11px !important;
}

#dashboard_index .post_title{
color:#000000 !important;
font-family:Courier New !important;
font-size:25px !important;}

#new_post_notice_container{
color:#fff !important;
font-family:Courier New !important;
font-size:11px !important;
}

#new_post_notice_container a{
color:#fff !important;
font-family:Courier New !important;
font-size:11px !important;
}

#dashboard_switch_blog_menu_current,
  #dashboard_switch_blog_arrow_and_divider,
   #dashboard_switch_blog_arrow_current{
background-image:none !important;
font-family:Courier New !important;
font-size:11px !important;
}

.notification_type_icon{
display:none !important;
font-family:Courier New !important;
font-size:11px !important;
}

h1,h2,h3,h4,h5,h6{color:#555555 !important; font-family:Courier New !important;
font-size:11px !important;}

a, a:visited{color:#666666 !important;font-family:Courier New !important;
font-size:11px !important;}

a:hover{color:#888888 !important;font-family:Courier New !important;
font-size:11px !important;}

/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://i50.tinypic.com/65a13t.jpg') center center no-repeat !important;
}
.new_post_label_icon {
	background-image: none !important;
}



.new_post_label{
font-size:11px !important;
padding-top:10px !important;
font-family:Courier New !important;
text-transform: lowercase!important;
letter-spacing:1px !important;
}

#logo {
height: 0 !important;
	width: 0 !important;
	/* these numbers match the new image's dimensions */

	padding-left: 230px !important;
	padding-top: 64px !important;
	background: url(http://i55.tinypic.com/t5q25e.png) no-repeat !important;
font-family:Courier New !important;
font-size:11px !important;
}

.sub_avatar{width:32px !important; height:32px !important; background-color:transparent !important;
font-family:Courier New !important;
font-size:11px !important;}
}

#dashboard_index #next_page_link,#previous_page_link{
background:none #fbfbfb !important;
font-family:Courier New !important;
font-size:15px !important;
}
