css:
/*
Google+ Custom Theme by David
http://gplus.to/DavidShares
*/

@-moz-document domain('plus.google.com'), url-prefix('https://plus.google.com') {

@media screen {
@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Open Sans'), local('OpenSans'), url('http://themes.googleusercontent.com/static/fonts/opensans/v2/cJZKeOuBrn4kERxqtaUH3T8E0i7KZn-EPnyo3HZu7kw.woff') format('woff');
}
}

/* Google+ Top Nav */
div#gbx4 {border-color:#ced9dd; background-color:#488196;}
div.a-U-T {background-color:#ced9dd; margin-top:-5px; height:55px;}
span.gbts:hover {background-color:#345d6b; color:#fff;}
div.a-U-Pg-T a.a-b-h-Jb img {filter:alpha(opacity=70); opacity:0.70;}
div.a-U-Pg-T a.a-b-h-Jb img:hover {filter:alpha(opacity=100); opacity:100;}

/* Google+ Left Nav */
.d-h.a-b-h-Jb.a-la-h.a-la-aH.a-la-h-Pa, .d-h.a-b-h-Jb.a-la-h.a-la-aH {background-color:#F5F5F5; border:1px solid #F5F5F5; font-weight:bold;}
div.a-Rf-mG a {font-size:18px; font-family:arial,sans-serif; color:#333;}
a.d-h.a-b-h-Jb.a-la-h.a-la-Rb-h.a-b-la-Rb-h:hover {background-image:url(http://img4.imageshack.us/img4/3828/circle4.png); background-position:0% 50%; color:#44b34c; font-weight:bold;}
a.d-h.a-b-h-Jb.a-la-h.a-la-GF.a-la-GF-La:hover {background-image:url(http://img845.imageshack.us/img845/4176/spark1g.png); background-position:0% 100%; color:#c6b88f; font-weight:bold;}
iframe.talk_iframe  {background-color:#f5f5f5; border:1px solid #ededed; margin-left:-5px; padding-left:6px; padding-right:6px;}

/* Google+ Stream  */
div#contentPane.a-p-M-T.a-p-M-T-gi-xc {background-color:#F5F5F5; width:675px;}
div#contentPane.a-p-M-T.a-p-M-T-Gp-xc {background-color:#F5F5F5; width:675px;}
div#contentPane .a-f-i {border-top: 1px solid #ced9dd;}
div.a-b-f-i.a-f-i {background-color:#F5F5F5;}
div.a-b-f-i.a-f-i {width:675px;}
div.n-Ob {width:634px;}
div.n-Ob:hover {border:1px solid #4D90FE; width:634px;}
div.va-Q.n-Zd-va {width:636px;}
span.d-h.a-b-f-zd-gb-h.a-f-zd-gb-h {width:675px;}
.esw {position:relative; top:2px;}

/* Google+ Right Column */
div.a-b-Cs-T.a-Cs-T.d-s-r {margin-right:-100px;}

/* Google+ Profile View */
div.d-Aa-ua.d-Aa-ua-Fa {background-color:#F5F5F5;}
div.a-b-c-p-Mc.a-c-p-Mc {background-color:#F5F5F5;}
span.fn {font-family: 'Open Sans', arial, serif; color:#488195; text-shadow: 1px 1px 0px #fff;}

/* Google+ Notifications */
div.MJI2hd {background-color:#F5F5F5; width:800px; padding-top:2px;}
div.a-Hc-A.a-b-Hc-A {background-color:#f5f5f5;}
div.a-Hc-ox {background-color:#fff1a8; text-align:center; padding:4px;}

}
